import React from 'react';
import { observer } from 'mobx-react';
import { ConnectionMini } from '../../../common/icons';
import InstanceItem from '../../model/InstanceItem';
import store from '../../store/RootStore';

const RelationshipsCount = observer(({ item, camera }: { item: InstanceItem, camera: string }) => {
  const { relationships } = item.getCamera(camera);
  const { currentFrame } = store.frame;
  const currRelationships = relationships.filter((r) => !!r.frames[currentFrame]);
  const count = currRelationships.length;
  if (count <= 0) {
    return null;
  }
  return (
    <div className="sidebar-category-relationship">
      <ConnectionMini />
      {count}
    </div>
  );
});

export default RelationshipsCount;
