import { Field, Condition, Effect, Rule, FieldControlType, FieldOption } from '@appen/easy-form/es/types';
import JobProxy from '../../libs/JobProxy';
import { TOOL_MODE } from '../../utils/tool-mode';

export interface Ontology {
  key?: string;
  class_name: string;
  display_name?: string;
  display_color: string;
  description?: string;
};

export interface Payload {
  locale?: string;
  issue_types?: string;
  base_url?: string;
  custom_check?: string;
  submit_check?: string | boolean;
  jobProxy: JobProxy;
  ontology: Ontology[];
  audio: string;
  toolMode?: TOOL_MODE;
  original_urls?: string;
  show_original?: string | boolean;
  label_config_segment?: string;
  deviation?: string;
  segment_mode?: string;
  segment_overlap?: string;
  global_config?: string;
  invalid_annotatable?: string | boolean;
};

export interface Category {
  className: string;
  displayName: string;
  displayColor: string;
}

export interface LabelConfig {
  fields: Field[];
  conditions?: Condition[];
  effects?: Effect[];
  rules?: Rule[];
}

export interface FieldConfig {
  name: string;
  type: FieldControlType;
  label?: string;
  options?: FieldOption[];
}

export enum ValidationType {
  ATTR_EMPTY = 'attr_empty',
  CUSTOM = 'custom',
  QUALITY = 'quality',
  OVERLAP = 'overlap',
  GLOBAL_ERROR = 'global_error',
}

export interface ReviewData {
  result: ReviewResult;
  type?: string[];
  comment?: string;
}

export interface Review extends ReviewData {
  instanceId: string; // related instance
  validationType?: ValidationType;
  channelIndex?: number;
  isMissing?: boolean;
  start?: number;
  end?: number;
}

export enum ReviewResult {
  APPROVE = 'approve',
  REJECT = 'reject',
  SUSPEND = 'suspend',
}

export enum ReviewColor {
  APPROVE = 0x52C41A,
  APPROVE_BG = 0xCEEBCB,
  REJECT = 0xDC4624,
  REJECT_BG = 0xEED9D7,
  SUSPEND = 0xFA9114,
  SUSPEND_BG = 0xFEE9D0,
}

export interface Element {
  id: string;
  category: string;
  start: number;
  end?: number;
  text?: string;
}

export interface Statistic {
  elements: Element[];
  categories: string[];
  shapes: Set<unknown>;
  summary: { [key: string]: Summary };
  duration: number; // file duration
  approvedWordCount: number;
  rejectedWordCount: number;
  approvedCharCount: number;
  rejectedCharCount: number;
  wordCount: number;
  charCount: number;
}

export interface Summary {
  ids: string[];
  category: string;
  wordCount: number;
  charCount: number;
}

export interface Instance {
  id: string;
  start: number;
  end: number;
  channelIndex: number;
  category: string;
  label: string;
  color: string;
  text?: string;
  attributes?: {[key: string]: any};
}

export enum UpdateType {
  INIT = 'init',
  MODIFY = 'modify',
  UNDOREDO = 'undoredo',
  REMOVE_MISSED = 'remove_missed',
}

export interface Range {
  start: number;
  end: number;
}

export interface TimeRange {
  startTime: number;
  endTime: number;
}

export interface ChannelOptions {
  index: number;
  mute: boolean;
  volume: number;
}

export enum MediaModes {
  REGION_PLAY = 'region_play',
  REGION_LOOP = 'region_loop',
  OVERALL = 'overall',
}

export enum TimeAction {
  BACKWARD = 'backward',
  FORWARD = 'forward',
}

export enum OffsetAction {
  INDENT = 'indent',
  BLANK = 'blank',
}

export interface FormConfig {
  fields: Field[];
  conditions?: Condition[];
  effects?: Effect[];
  rules?: Rule[];
}

export interface Warning {
  warningType: string;
  id: string;
  message: string;
  comment?: string;
  channelIndex?: number;
  isMissing?: boolean;
  start?: number;
  end?: number;
}
