import React from 'react';
import { observer } from 'mobx-react';
import { Tooltip } from 'antd';
import cx from 'classnames';
import { Connection } from '../../../common/icons';
import Dropdown, { MenuItemType } from '../../../common/dropdown/Dropdown';
import store from '../../store/RootStore';
import i18n from '../../locales';

const Relationship = observer(() => {
  if (!store.relationship.relationshipEnabled) {
    return null;
  }

  const { options, activeOptionIndex, settingRelationship } = store.relationship;
  const activeOption = options[activeOptionIndex];
  const disabled = store.shape.cuttingPolygon || store.shape.splittingPolygn;
  return (
    <>
      <Tooltip placement="bottom" title={i18n.translate('OPERATION_RELATIONSHIP')}>
        <div
          style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}
          onClick={() => {
            if (!disabled) {
              store.relationship.activate();
            }
          }}
        >
          <div
            className={cx('operation-icon', {
              'operation-icon--active': settingRelationship,
              'operation-icon--disabled': disabled,
            })}
          >
            <Connection />
          </div>
          <div style={{ paddingRight: 6 }}>{activeOption.name}</div>
        </div>
      </Tooltip>
      <Dropdown
        arrow
        showIcon
        style={{ height: '100%' }}
        menuStyle={{ minWidth: 160, left: -32 }}
        menu={[
          {
            label: i18n.translate('OPERATION_RELATIONSHIP_MENU_TITLE'),
            type: MenuItemType.TITLE,
          },
          ...options.map((o, i) => ({
            label: o.name,
            active: i === activeOptionIndex,
          })),
        ]}
        onClick={(_, i) => store.relationship.setActiveOption(i - 1)}
      />
    </>
  );
});

export default Relationship;
