import React from 'react';
import { Application, Container, Point } from 'pixi.js';
import { Slider } from 'antd';
import GridCanvas from '../../common/shapes/canvas/GridCanvas';
import { SelectedStyle } from '../../common/shapes/canvas/GridContainer';
import { ShapeType, ShapeData } from '../../common/shapes/types';
import Shape, { BorderStyle, EventAction } from '../../common/shapes/Shape';
import { Frames, Aim } from '../../common/icons';
import i18n from '../locales';
import { AttributesMode, Instance, ReviewResult } from '../types';
import { predictShapeData } from '../utils';
import ReviewsStore from '../store/ReviewsStore';
import './GridView.scss';

interface GridViewProps {
  app: Application;
  readonly: boolean;
  labelMode: boolean;
  attributesMode: AttributesMode;
  frames: string[];
  currentFrame: number;
  instances: { [instanceId: string]: Instance };
  selectedInstanceId: string;
  selectedCategoryColor: string;
  setFrame: (frameIndex: number) => void;
  addShape: (point: Point, shapeContainer?: Container, imageBounds?: { left: number; top: number; right: number; bottom: number; }) => Shape<ShapeData> | null;
  createShape: (hexColor: string, label?: string, shapeType?: ShapeType, shapeData?: ShapeData, shapeContainer?: Container, imageBounds?: { left: number; top: number; right: number; bottom: number; }) => Shape<ShapeData>;
  updateShape: (instanceId: string, shape: Shape<ShapeData>, shapeData: ShapeData, shapeType?: ShapeType) => void;
  deleteShape: (instanceId: string, frames: number[]) => void;
  addShapeToInstance: (instanceId: string, shapeContainer?: Container, imageBounds?: { left: number; top: number; right: number; bottom: number; }) => Shape<ShapeData> | null;
  getInstanceLabel: (instance: Instance) => string;
}

interface GridViewState {
  gridSizeIndex: number;
}

const gridSizes = [200, 300, 400, 600, 800];

export default class GridView extends React.Component<GridViewProps, GridViewState> {
  state = {
    gridSizeIndex: 2,
  };

  /**
   * grid canvas ref
   */
  gridCanvasRef = React.createRef<GridCanvas>();

  /**
   * whether tool has been initialized
   */
  get initialized() {
    return this.props.frames.length > 0;
  }

  /**
   * grid items
   */
  get grids() {
    const hasReview = ReviewsStore.isEnabled;
    const { instances, selectedInstanceId } = this.props;
    const { frames = {} } = instances[selectedInstanceId] || {};
    return this.props.frames.map((frame, index) => {
      let borderColor = 0x1A1B1E;
      let selectedStyle = SelectedStyle.FILL;

      if (frames[index]) {
        borderColor = 0xA4A9BB;
        if (hasReview) {
          const reviewResult = ReviewsStore.getReviewResult(index, selectedInstanceId);
          if (reviewResult) {
            selectedStyle = SelectedStyle.LINE;
            if (reviewResult === ReviewResult.APPROVE) {
              borderColor = 0x3A5F54;
            } else if (reviewResult === ReviewResult.REJECT) {
              borderColor = 0x7C433C;
            } else if (reviewResult === ReviewResult.SUSPEND) {
              borderColor = 0x897D2E;
            }
          }
        }
      } else {
        selectedStyle = SelectedStyle.LINE;
      }

      return {
        imageUrl: frame,
        title: `${index + 1}`,
        borderColor,
        selectedStyle,
        defaultSelected: index === this.props.currentFrame,
        ...frames[index] && frames[index].isKeyFrame && { info: 'key frame ●' },
      };
    });
  }

  componentDidUpdate(prevProps: GridViewProps) {
    if (prevProps.currentFrame !== this.props.currentFrame) {
      // current frame changes
      this.gridCanvasRef.current?.setGridSelected(this.props.currentFrame);
      this.gridCanvasRef.current?.scrollTo(this.props.currentFrame);
    }
    if (
      prevProps.selectedInstanceId !== this.props.selectedInstanceId ||
      prevProps.labelMode !== this.props.labelMode ||
      prevProps.attributesMode !== this.props.attributesMode
    ) {
      this.update();
    }
  }

  /**
   * get shapes in grid
   * @param gridIndex
   * @returns
   */
  getShapesForGrid = (gridIndex: number) => this.gridCanvasRef.current?.getGridShapes(gridIndex) || [];

  /**
   * create shapes for grid
   * @param gridIndex
   * @param container
   * @param imageBounds
   * @returns
   */
  createShapesForGrid = (gridIndex: number, container: Container, imageBounds: { left: number; top: number; right: number; bottom: number; }) => {
    const shapes = [];

    const { readonly, instances, selectedInstanceId, selectedCategoryColor, createShape, updateShape, getInstanceLabel } = this.props;
    const selectedInstance = instances[selectedInstanceId];
    if (selectedInstance) {
      const label = getInstanceLabel(selectedInstance);
      const { frames } = selectedInstance;
      let shape;
      let shapeType: ShapeType | undefined;
      if (frames[gridIndex]) {
        shapeType = frames[gridIndex].shapeType;
        shape = createShape(
          selectedCategoryColor,
          label,
          frames[gridIndex].shapeType,
          frames[gridIndex].shape,
          container,
          imageBounds,
        );
      } else {
        const shapeInfo = predictShapeData(gridIndex, frames, imageBounds);
        if (shapeInfo) {
          // create predict shape
          shapeType = shapeInfo.shapeType;
          shape = createShape(
            selectedCategoryColor,
            label,
            shapeInfo.shapeType,
            shapeInfo.shape,
            container,
            imageBounds,
          );
          shape.borderStyle = BorderStyle.DASHED;
        }
      }
      if (shape && shapeType) {
        shape.on(EventAction.CHANGED, (s, data) => {
          updateShape(selectedInstanceId, s, data, shapeType!);
          this.update();
        });
        if (readonly) {
          shape.editable = false;
        }
        shape.selected = true; // always true
        shapes.push(shape);
      }
    }

    return shapes;
  };

  /**
   * add shape to grid
   * @param gridIndex
   * @param instanceId
   */
  addShapeToGrid = (gridIndex: number, instanceId: string) => {
    const grid = this.gridCanvasRef.current?.getGrid(gridIndex);
    if (grid) {
      const shape = this.props.addShapeToInstance(instanceId, grid.container, grid.imageBounds);
      if (shape) {
        const originShapes = this.gridCanvasRef.current?.getGridShapes(gridIndex) || [];
        this.gridCanvasRef.current?.setGridShapes(gridIndex, [...originShapes, shape]);
      }
      this.update();
    }
  };

  /**
   * delete shape vertex or shape itself in specific frame
   * @param frameIndex
   * @param instanceId
   */
  delete = (frameIndex: number, instanceId: string) => {
    const shapes = this.gridCanvasRef.current?.getGridShapes(frameIndex) || [];
    const pointsDeleted = shapes.some((shape) => shape.deleteSelectedPoints());
    if (!pointsDeleted) {
      this.props.deleteShape(instanceId, [frameIndex]);
    }
    this.update();
  };

  /**
   * update all grids shapes
   */
  update = () => {
    this.gridCanvasRef.current?.updateShapes(this.grids);
  };

  /**
   * update grid style
   * @param frameIndex
   */
  updateStyle = (frameIndex?: number) => {
    this.gridCanvasRef.current?.updateStyle(frameIndex);
  };

  /**
   * update grid size
   * @param sizeIndex
   */
  updateGridSize = (sizeIndex: number) => {
    this.setState({ gridSizeIndex: sizeIndex });
    const size = gridSizes[sizeIndex];
    this.gridCanvasRef.current?.updateCanvas(size);
  };

  render() {
    const { currentFrame, instances, selectedInstanceId, selectedCategoryColor, getInstanceLabel } = this.props;
    const selectedInstance = instances[selectedInstanceId];
    return this.initialized && (
      <div className="grid-view">
        <div className="grid-header">
          <div>
            {selectedInstance && (
              <div className="label">
                <div
                  className="label-dot"
                  style={{ backgroundColor: selectedCategoryColor }}
                />
                {getInstanceLabel(selectedInstance)}
              </div>
            )}
            {i18n.translate('GRID_SELECTED_LABEL', { values: { frame: currentFrame + 1 } })}
            {selectedInstance && (
              <span className="frames">
                <Frames />
                {Object.keys(selectedInstance.frames).length}
              </span>
            )}
          </div>
          <div>
            {i18n.translate('GRID_SCALE_LABEL')}
            <Slider
              tooltipVisible={false}
              step={1}
              min={0}
              max={gridSizes.length - 1}
              value={this.state.gridSizeIndex}
              onChange={this.updateGridSize}
            />
            <div
              className="icon-button"
              onClick={() => this.gridCanvasRef.current?.fitShapes()}
            >
              <Aim />
            </div>
          </div>
        </div>
        <GridCanvas
          ref={this.gridCanvasRef}
          app={this.props.app}
          grids={this.grids}
          addShape={this.props.addShape}
          createShapesForGrid={this.createShapesForGrid}
          onGridSelected={(index) => this.props.setFrame(index)}
        />
      </div>
    );
  }
}
