import React from 'react';
import { observer } from 'mobx-react';
import { ArrowRightFilled, More } from '../../../common/icons';
import InstanceItem from '../../model/InstanceItem';
import Relationship from '../../model/Relationship';
import store from '../../store/RootStore';
import i18n from '../../locales';

const RelationshipItem = observer(({ relationship, currentInstanceItem }: {
  relationship: Relationship;
  currentInstanceItem: InstanceItem;
}) => {
  const { fromInstanceItem, toInstanceItem } = relationship;
  return (
    <div className="relationship-item">
      <div style={{ flex: 1 }}>
        {
          fromInstanceItem === currentInstanceItem
            ? `[${i18n.translate('RELATIONSHIP_CURR_ITEM')}]`
            : (
              <>
                {`[${fromInstanceItem.label}]`}
                <span className="target" onClick={() => store.instance.selectInstanceItem(fromInstanceItem)}>
                  <ArrowRightFilled />
                </span>
              </>
            )
        }
        <span className="relationship-name">
          {relationship.relationship}
        </span>
        {
          toInstanceItem === currentInstanceItem
            ? `[${i18n.translate('RELATIONSHIP_CURR_ITEM')}]`
            : (
              <>
                {`[${toInstanceItem.label}]`}
                <span className="target" onClick={() => store.instance.selectInstanceItem(toInstanceItem)}>
                  <ArrowRightFilled />
                </span>
              </>
            )
        }
      </div>
      {!store.readonly && (
        <div
          className="relationship-item-more"
          onClick={(e) => store.relationship.openMenuByRelationship(relationship, e.nativeEvent)}
        >
          <More />
        </div>
      )}
    </div>
  );
});

export default RelationshipItem;
