/* eslint-disable no-undef */
// import zhCN from '@appen/spreadsheet/dist/locale/zh-cn';
import cn from './zh-CN';
import en from './en-US';

export default function (key) {
  const lang = navigator.language || navigator.browserLanguage || 'en-US';
  const message = lang.startsWith('zh') ? cn[key] : en[key];
  if (message) {
    return message;
  }
  return key;
}

