import React, { useEffect, useState } from 'react';
import screenfull from 'screenfull';
import cx from 'classnames';
import { Button, Input, Tooltip } from 'antd';
import { CloseOutlined } from '@ant-design/icons';
import Replace from './Replace';
import Guide from './Guide';
import AppenLogo from '../../common/AppenLogo';
import { Undo, Redo, CheckFlag, CloseFlag, Question, Fullscreen, Exchange } from '../../common/icons';
import { SearchResult } from '../types';
import formatMessage from '../locales';
import './Toolbar.scss';

interface ToolbarPopupProps {
  title: string;
  submitText: string;
  onSubmit: (start: number, end: number) => void;
  onClose: () => void;
}

const ToolbarPopup = ({
  title,
  submitText,
  onSubmit,
  onClose,
}: ToolbarPopupProps) => {
  const [start, setStart] = useState('');
  const [end, setEnd] = useState('');

  return (
    <div className="toolbar-popup">
      <div className="popup-title">
        <b>{title}</b>
        <CloseOutlined onClick={onClose} />
      </div>
      <div>
        {formatMessage('TOOLBAR_LABEL_FROM')}
        <Input
          value={start}
          onChange={(e) => {
            if (!e.target.value || /^(\d+)$/.test(e.target.value)) {
              setStart(e.target.value);
            }
          }}
        />
        {formatMessage('TOOLBAR_LABEL_TO')}
        <Input
          value={end}
          onChange={(e) => {
            if (!e.target.value || /^(\d+)$/.test(e.target.value)) {
              setEnd(e.target.value);
            }
          }}
        />
      </div>
      <div className="popup-tip">{formatMessage('TOOLBAR_LABEL_TIP')}</div>
      <div className="toolbar-popup-btns">
        <Button
          style={{ marginRight: 12 }}
          onClick={onClose}
        >
          {formatMessage('COMMON_CANCEL')}
        </Button>
        <Button
          type="primary"
          disabled={!start || !end}
          onClick={() => {
            onSubmit(parseInt(start, 10), parseInt(end, 10));
            onClose();
          }}
        >
          {submitText}
        </Button>
      </div>
    </div>
  );
};

interface ToolbarProps {
  current: number;
  undoDisabled: boolean;
  redoDisabled: boolean;
  undo: () => void;
  redo: () => void;
  searchResults: SearchResult[];
  currentResult: number;
  search: (key: string) => void;
  replace: (key: string) => void;
  replaceAll: (key: string) => void;
  findNext: () => void;
  focusResult: (index: number) => void;
  activateReplace: (active: boolean) => void;
  mark: (start: number, end: number) => void;
  unmark: (start: number, end: number) => void;
  save: () => void;
}

const Toolbar: React.FC<ToolbarProps> = ({
  current,
  undoDisabled,
  redoDisabled,
  undo,
  redo,
  searchResults,
  currentResult,
  search,
  replace,
  replaceAll,
  findNext,
  focusResult,
  activateReplace,
  mark,
  unmark,
  save,
}) => {
  const [markActive, setMarkActive] = useState(false);
  const [unmarkActive, setUnmarkActive] = useState(false);
  const [helpActive, setHelpActive] = useState(false);
  const [replaceActive, setReplaceActive] = useState(false);

  useEffect(() => {
    if (current >= 0 && replaceActive) {
      setReplaceActive(false);
    }
  }, [current]);

  useEffect(() => {
    if (markActive) {
      setUnmarkActive(false);
      setHelpActive(false);
      setReplaceActive(false);
    }
  }, [markActive]);

  useEffect(() => {
    if (unmarkActive) {
      setMarkActive(false);
      setHelpActive(false);
      setReplaceActive(false);
    }
  }, [unmarkActive]);

  useEffect(() => {
    if (helpActive) {
      setMarkActive(false);
      setUnmarkActive(false);
      setReplaceActive(false);
    }
  }, [helpActive]);

  useEffect(() => {
    activateReplace(replaceActive);
    if (replaceActive) {
      setMarkActive(false);
      setUnmarkActive(false);
      setHelpActive(false);
    }
  }, [replaceActive]);

  const handleFullscreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle();
    }
  };

  return (
    <div className="toolbar">
      <div>
        <div style={{ marginRight: 72 }}>
          <AppenLogo />
        </div>
        <div>
          <div style={{ position: 'relative' }}>
            <Tooltip title={formatMessage('TOOLBAR_REPLACE')}>
              <div
                className={cx('toolbar-icon', {
                  'toolbar-icon__active': replaceActive,
                })}
                onClick={() => setReplaceActive(!replaceActive)}
              >
                <Exchange />
              </div>
            </Tooltip>
            <Replace
              visible={replaceActive}
              searchResults={searchResults}
              currentResult={currentResult}
              onSearch={search}
              onReplace={replace}
              onReplaceAll={replaceAll}
              onNext={findNext}
              onFocus={focusResult}
              onClose={() => setReplaceActive(false)}
            />
          </div>
          <Tooltip title={formatMessage('TOOLBAR_UNDO')}>
            <div
              className={cx('toolbar-icon', {
                'toolbar-icon__disabled': undoDisabled,
              })}
              onClick={undo}
            >
              <Undo />
            </div>
          </Tooltip>
          <Tooltip title={formatMessage('TOOLBAR_REDO')}>
            <div
              className={cx('toolbar-icon', {
                'toolbar-icon__disabled': redoDisabled,
              })}
              onClick={redo}
            >
              <Redo />
            </div>
          </Tooltip>
        </div>
      </div>
      <div>
        <div>
          {/* <Tooltip title={formatMessage('TOOLBAR_DICTIONARY')}>
            <div className="toolbar-icon">
              <Dictionary />
            </div>
          </Tooltip> */}
          <div style={{ position: 'relative' }}>
            <Tooltip title={formatMessage('TOOLBAR_LABEL')}>
              <div
                className={cx('toolbar-icon', {
                  'toolbar-icon__active': markActive,
                })}
                onClick={() => setMarkActive(!markActive)}
              >
                <CheckFlag />
              </div>
            </Tooltip>
            {markActive && (
              <ToolbarPopup
                title={formatMessage('TOOLBAR_LABEL_TITLE')}
                submitText={formatMessage('TOOLBAR_LABEL')}
                onSubmit={mark}
                onClose={() => setMarkActive(false)}
              />
            )}
          </div>
          <div style={{ position: 'relative' }}>
            <Tooltip title={formatMessage('TOOLBAR_LABEL_DELETE')}>
              <div
                className={cx('toolbar-icon', {
                  'toolbar-icon__active': unmarkActive,
                })}
                onClick={() => setUnmarkActive(!unmarkActive)}
              >
                <CloseFlag />
              </div>
            </Tooltip>
            {unmarkActive && (
              <ToolbarPopup
                title={formatMessage('TOOLBAR_LABEL_DELETE_TITLE')}
                submitText={formatMessage('TOOLBAR_LABEL_DELETE')}
                onSubmit={unmark}
                onClose={() => setUnmarkActive(false)}
              />
            )}
          </div>
        </div>
        <div style={{ borderLeft: '1px solid #1A1920', marginLeft: 8, marginRight: -8, paddingLeft: 8 }}>
          <Tooltip title={formatMessage('TOOLBAR_HELP')}>
            <div
              className={cx('toolbar-icon', {
                'toolbar-icon__active': helpActive,
              })}
              onClick={() => setHelpActive(!helpActive)}
            >
              <Question />
            </div>
          </Tooltip>
          <Tooltip title={formatMessage('TOLLBAR_FULLSCREEN')}>
            <div className="toolbar-icon" onClick={handleFullscreen}>
              <Fullscreen />
            </div>
          </Tooltip>
          <Button type="primary" onClick={save} style={{ margin: '0 8px' }}>
            {formatMessage('TOOLBAR_SAVE', { values: { hotkey: 'Ctrl+S' } })}
          </Button>
        </div>
      </div>
      {helpActive && <Guide />}
    </div>
  );
};

export default Toolbar;
