import React from 'react';
import { v4 as uuidv4 } from 'uuid';
import { observable, computed, toJS, makeObservable } from 'mobx';
import { observer } from 'mobx-react';
import { message } from 'antd';
import Toolbar from './components/Toolbar';
import Video from './components/Video';
import Navigator from './components/Navigator';
import TranslationList from './components/TranslationList';
import { Subtitle, Status, StatusType, StatusItem, SubtitleStatus, MarkStatus, SearchResult, ReviewItem, ReplaceStatus } from './types';
import { ACTIVE_TEXTAREA_ID } from './constants';
import formatMessage from './locales';
import AutoSaver from '../common/AutoSaver';
import JobProxy from '../../libs/JobProxy';
import { isPreview, isAnnotationReadonly, isReviewEditable } from '../../utils/tool-mode';
import './index.scss';

interface TranslationProps {
  jobProxy: JobProxy;
  video_url: string;
  renderComplete: () => Promise<void>;
}

@observer
class Translation extends React.Component<TranslationProps> {
  /**
   * video component reference
   */
  video = React.createRef<Video>();

  /**
   * saved status used for undo & redo
   */
  savedStatus: Status[] = [];

  /**
   * saved search key
   */
  searchKey = '';

  /**
   * undo & redo pointer
   */
  @observable pointer = 0;

  /**
   * loading
   */
  @observable loading = true;

  /**
   * subtitle items
   */
  @observable subtitles: Subtitle[] = [];

  /**
   * selected subtitle index
   */
  @observable current = -1;

  /**
   * marked subtitle index list
   */
  @observable marks: number[] = [];

  /**
   * search results
   */
  @observable searchResults: SearchResult[] = [];

  /**
   * current focused search result
   */
  @observable currentResult = -1;

  /**
   * is replace modal active
   */
  @observable replaceActive = false;

  /**
   * review list
   */
  @observable reviews: {[id: string]: ReviewItem} = {};

  constructor(props: TranslationProps) {
    super(props);
    makeObservable(this);
  }

  /**
   * is undo disabled
   */
  @computed get undoDisabled() {
    return this.pointer <= 0;
  }

  /**
   * is redo disabled
   */
  @computed get redoDisabled() {
    return this.pointer >= this.savedStatus.length;
  }

  componentDidMount() {
    this.init();
    window.addEventListener('keydown', this.keyboardListener, false);
  }

  componentWillUnmount() {
    window.removeEventListener('keydown', this.keyboardListener, false);
  }

  saveStatus(type: StatusType, before: StatusItem[], after: StatusItem[]) {
    this.savedStatus = this.savedStatus.slice(0, this.pointer);
    this.savedStatus.push({ type, before, after });
    if (this.savedStatus.length > 200) { // max stack length is 100
      this.savedStatus.shift();
    } else {
      this.pointer += 1;
    }
  }

  updateStatus(type: StatusType, curr: StatusItem[], prev: StatusItem[]) {
    switch (type) {
      case StatusType.TRANSLATION:
        (curr as SubtitleStatus[]).forEach((item) => {
          this.subtitles[item.index].target = item.content;
        });
        if (curr.length === 1) {
          if (curr[0].index !== this.current) {
            this.current = curr[0].index;
          }
          setTimeout(() => {
            // set right anchor after the textarea changes
            const activeInput = document.getElementById(ACTIVE_TEXTAREA_ID) as HTMLTextAreaElement;
            const { anchor } = curr[0] as SubtitleStatus;
            activeInput.setSelectionRange(anchor, anchor);
          }, 0);
        }
        break;
      case StatusType.MARK:
        (prev as MarkStatus[]).forEach((item) => {
          const index = this.marks.indexOf(item.index);
          if (index >= 0) {
            this.marks.splice(index, 1);
          }
        });
        (curr as MarkStatus[]).forEach((item) => {
          const index = this.marks.indexOf(item.index);
          if (index < 0) {
            this.marks.push(item.index);
          }
        });
        break;
      case StatusType.REPLACE:
        (curr as SubtitleStatus[]).forEach((item) => {
          this.subtitles[item.index].target = item.content;
        });
        this.search(this.searchKey);
        break;
      default:
    }
  }

  async init() {
    const reviewFrom = await this.props.jobProxy.loadResult();
    if (Array.isArray(reviewFrom)) {
      this.subtitles = [...reviewFrom];
    } else {
      if (reviewFrom.auditId) {
        this.props.jobProxy.setAuditId(reviewFrom.auditId);
      }
      if (reviewFrom.subtitles) {
        this.subtitles = [...reviewFrom.subtitles];
      }
      if (reviewFrom.marks) {
        this.marks = [...reviewFrom.marks];
      }
    }

    // fix subtitles
    this.subtitles.forEach((subtitle) => {
      if (!subtitle.id) {
        // eslint-disable-next-line no-param-reassign
        subtitle.id = uuidv4();
      }
      if (subtitle.target === undefined && subtitle.pretranslation) {
        // when init, if target not provided, use pretranslation instead
        // eslint-disable-next-line no-param-reassign
        subtitle.target = subtitle.pretranslation;
      }
    });
    // fix marks
    this.marks = this.marks.filter((m) => m >= 0 && m < this.subtitles.length);

    // render complete
    this.props.renderComplete();
    this.loading = false;
  }

  keyboardListener = (event: KeyboardEvent) => {
    switch (event.key.toLowerCase()) {
      case 'tab':
        event.preventDefault();
        if (event.shiftKey && this.current - 1 >= 0) {
          // prev row
          this.current -= 1;
        } else if (!event.shiftKey && this.current + 1 < this.subtitles.length) {
          // next row
          this.current += 1;
        }
        break;
      case 'z':
        if (event.ctrlKey || event.metaKey) {
          event.preventDefault();
          if (event.shiftKey) {
            this.redo();
          } else {
            this.undo();
          }
        }
        break;
      case 'y':
        if (event.ctrlKey || event.metaKey) {
          event.preventDefault();
          this.redo();
        }
        break;
      default:
    }
  };

  onSave = async () => {
    const { toolMode } = this.props.jobProxy;
    if (isPreview(toolMode)) {
      return;
    }

    const promises = [this.saveResult('temp')]; // always save because marks should be saved all the time
    // if (isReviewEditable(toolMode)) {
    //   promises.push(this.saveReview());
    // }

    try {
      await Promise.all(promises);
      message.success(formatMessage('SAVE_SUCCESS'));
    } catch (e) {
      message.error(formatMessage('SAVE_FAIL'));
    }
  };

  saveResult = (type = 'submit') => {
    if (this.loading) {
      throw new Error(formatMessage('LOADING_ALERT'));
    }
    return this.props.jobProxy.saveResult({
      auditId: this.props.jobProxy.auditId,
      subtitles: toJS(this.subtitles),
      ...(type !== 'submit' && { marks: toJS(this.marks) }),
    });
  };

  saveReview = () => {
    if (this.loading) {
      throw new Error(formatMessage('LOADING_ALERT'));
    }
    return this.props.jobProxy.saveReviews(toJS(this.reviews));
  };

  undo = () => {
    if (this.pointer > 0) {
      this.pointer -= 1;
      const { type, before, after } = this.savedStatus[this.pointer];
      this.updateStatus(type, before, after);
    }
  };

  redo = () => {
    if (this.pointer < this.savedStatus.length) {
      const { type, before, after } = this.savedStatus[this.pointer];
      this.pointer += 1;
      this.updateStatus(type, after, before);
    }
  };

  /**
   * dummy search cancel function, will be overwritten in search
   */
  cancelSearch = () => {};

  search = (key: string) => {
    this.searchResults = [];
    this.currentResult = -1;
    this.cancelSearch(); // always cancel previous search
    if (key) {
      this.searchKey = key;
      const cancelPromise = new Promise((resolve) => {
        this.cancelSearch = resolve as () => void;
      });
      const searchPromise = new Promise((resolve) => {
        const results: SearchResult[] = [];
        this.subtitles.forEach((subtitle, rowIndex) => {
          const { target = '' } = subtitle;
          let fromIndex = 0;
          while (fromIndex >= 0 && fromIndex < target.length) {
            const index = target.indexOf(key, fromIndex);
            if (index >= 0) {
              const start = index;
              const end = index + key.length;
              results.push({ rowIndex, start, end, text: target });
              fromIndex = end;
            } else {
              fromIndex = -1;
            }
          }
        });
        resolve(results);
      });

      Promise.race([searchPromise, cancelPromise]).then((res) => {
        if (res) {
          this.searchResults = res as SearchResult[];
          if (this.searchResults.length > 0) {
            this.currentResult = 0;
          }
        }
      });
    }
  };

  replace = (key: string) => {
    if (this.searchResults.length <= 0 || this.currentResult < 0) {
      return;
    }
    const { rowIndex, start, end } = this.searchResults[this.currentResult];
    const { target = '' } = this.subtitles[rowIndex];
    const newTarget = `${target.substring(0, start)}${key}${target.substring(end)}`;
    this.subtitles[rowIndex].target = newTarget;
    this.searchResults.splice(this.currentResult, 1);

    const beforeStatus: ReplaceStatus[] = [{
      index: rowIndex,
      content: target,
    }];
    const afterStatus: ReplaceStatus[] = [{
      index: rowIndex,
      content: newTarget,
    }];
    this.saveStatus(StatusType.REPLACE, beforeStatus, afterStatus);
  };

  replaceAll = (key: string) => {
    if (this.searchResults.length <= 0 || this.currentResult < 0) {
      return;
    }
    const beforeStatus: ReplaceStatus[] = [];
    const afterStatus: ReplaceStatus[] = [];
    this.searchResults.forEach(({ rowIndex, start, end }) => {
      const { target = '' } = this.subtitles[rowIndex];
      const newTarget = `${target.substring(0, start)}${key}${target.substring(end)}`;
      this.subtitles[rowIndex].target = newTarget;

      if (beforeStatus.findIndex((bs) => bs.index === rowIndex) < 0) {
        beforeStatus.push({ index: rowIndex, content: target }); // only push once
      }
      const afterIndex = afterStatus.findIndex((as) => as.index === rowIndex);
      if (afterIndex < 0) {
        afterStatus.push({ index: rowIndex, content: newTarget });
      } else {
        afterStatus.splice(afterIndex, 1, { index: rowIndex, content: newTarget });
      }
    });
    this.searchResults = [];
    this.currentResult = -1;
    this.saveStatus(StatusType.REPLACE, beforeStatus, afterStatus);
  };

  findNext = () => {
    if (this.currentResult < this.searchResults.length - 1) {
      this.currentResult += 1;
    } else {
      this.currentResult = 0;
    }
  };

  focusResult = (index: number) => {
    this.currentResult = index;
  };

  targetSubtitleByTime = (time: number) => this.subtitles.findIndex((s) => time >= s.start && time < s.end);

  getSubtitleByIndex = (index: number) => toJS(this.subtitles[index]);

  handleCurrentChange = (current: number) => {
    if (this.subtitles[this.current] && !this.subtitles[this.current].target && this.marks.includes(this.current)) {
      // delete mark if clear the translation
      const index = this.marks.indexOf(this.current);
      this.marks.splice(index, 1);
    }
    this.current = current;
  };

  handleSubtitleChange = (index: number, target: string, lastAnchor: number, currAnchor: number, lastTarget?: string, skipSave = false) => {
    const beforeStatus: SubtitleStatus[] = [{
      index,
      anchor: lastAnchor,
      content: lastTarget || this.subtitles[index].target,
    }];
    const afterStatus: SubtitleStatus[] = [{
      index,
      anchor: currAnchor,
      content: target,
    }];
    this.subtitles[index].target = target;
    if (!skipSave) {
      this.saveStatus(StatusType.TRANSLATION, beforeStatus, afterStatus);
    }
  };

  handleSubtitlePlay = (index: number) => {
    const { start } = this.subtitles[index];
    this.video.current?.playAt(start);
  };

  handleToggleMark = (index: number) => {
    if (this.subtitles[index] && this.subtitles[index].target) {
      const beforeStatus: MarkStatus[] = [];
      const afterStatus: MarkStatus[] = [];
      const markIndex = this.marks.indexOf(index);
      if (markIndex < 0) {
        // mark
        this.marks.push(index);
        afterStatus.push({ index });
      } else {
        // delete mark
        this.marks.splice(markIndex, 1);
        beforeStatus.push({ index });
      }
      this.saveStatus(StatusType.MARK, beforeStatus, afterStatus);
    }
  };

  handleMarkDelete = (index: number) => {
    const markIndex = this.marks.indexOf(index);
    if (markIndex >= 0) {
      this.marks.splice(markIndex, 1);
    }
  };

  handleBatchMark = (start: number, end: number, mark: boolean) => {
    let s = start;
    let e = end;
    if (start > end) {
      s = end;
      e = start;
    }
    const beforeStatus: MarkStatus[] = [];
    const afterStatus: MarkStatus[] = [];
    for (let i = s - 1; i < e; i += 1) {
      const subtitle = this.subtitles[i];
      if (subtitle && subtitle.target) {
        const index = this.marks.indexOf(i);
        if (mark && index < 0) {
          this.marks.push(i);
          afterStatus.push({ index: i });
        } else if (!mark && index >= 0) {
          this.marks.splice(index, 1);
          beforeStatus.push({ index: i });
        }
      }
    }
    if (beforeStatus.length > 0 || afterStatus.length > 0) {
      this.saveStatus(StatusType.MARK, beforeStatus, afterStatus);
    }
  };

  handleActivateReplace = (active: boolean) => {
    this.replaceActive = active;
    if (active) {
      this.handleCurrentChange(-1);
    }
  };

  render() {
    return !this.loading && (
      <div className="translation-app">
        <AutoSaver
          leaveCheck
          data={{ subtitles: toJS(this.subtitles), marks: toJS(this.marks) }}
          save={() => this.saveResult('temp')}
          onSaved={() => message.success(formatMessage('AUTO_SAVE_SUCCESS'))}
        />
        <Toolbar
          current={this.current}
          undoDisabled={this.undoDisabled}
          redoDisabled={this.redoDisabled}
          undo={this.undo}
          redo={this.redo}
          searchResults={toJS(this.searchResults)}
          currentResult={this.currentResult}
          search={this.search}
          replace={this.replace}
          replaceAll={this.replaceAll}
          findNext={this.findNext}
          focusResult={this.focusResult}
          activateReplace={this.handleActivateReplace}
          mark={(start, end) => this.handleBatchMark(start, end, true)}
          unmark={(start, end) => this.handleBatchMark(start, end, false)}
          save={this.onSave}
        />
        <div className="container">
          <div className="fixed">
            <Video
              ref={this.video}
              url={this.props.video_url}
              targetCurrent={this.handleCurrentChange}
              targetSubtitle={this.targetSubtitleByTime}
              getSubtitleByIndex={this.getSubtitleByIndex}
            />
            <Navigator
              subtitles={toJS(this.subtitles)}
              current={this.current}
              marks={toJS(this.marks)}
              onCurrentChange={this.handleCurrentChange}
            />
          </div>
          <div className="flex">
            <TranslationList
              editable={!isAnnotationReadonly(this.props.jobProxy.toolMode) && !isPreview(this.props.jobProxy.toolMode)}
              reviewable={isReviewEditable(this.props.jobProxy.toolMode)}
              subtitles={toJS(this.subtitles)}
              current={this.current}
              marks={toJS(this.marks)}
              searchResults={toJS(this.searchResults)}
              currentResult={this.currentResult}
              replaceActive={this.replaceActive}
              onCurrentChange={this.handleCurrentChange}
              onSubtitleChange={this.handleSubtitleChange}
              onSubtitlePlay={this.handleSubtitlePlay}
              onMarkToggle={this.handleToggleMark}
              onMarkDelete={this.handleMarkDelete}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Translation;
