import React from 'react';
import { observer } from 'mobx-react';
import { Slider } from 'antd';
import { Frames, Aim } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';

interface HeaderProps {
  reset: () => void;
}

const Header = observer(({ reset }: HeaderProps) => {
  const { isSingleSelected, selectedInstances, selectedInstanceItems } = store.instance;
  const { currentFrame } = store.frame;
  const { gridSizes, gridSizeIndex, setGridSizeIndex } = store.grid;

  // only when not multiselected
  const selectedInstance = isSingleSelected ? selectedInstances[0] : undefined;
  const selectedInstanceItem = isSingleSelected ? selectedInstanceItems[0] : undefined;
  return (
    <div className="grid-header">
      <div>
        {selectedInstance && (
          <div className="label">
            <div
              className="label-dot"
              style={{ backgroundColor: selectedInstanceItem?.categoryItemRef.displayColor }}
            />
            {selectedInstanceItem ? selectedInstanceItem.label : selectedInstance.label}
          </div>
        )}
        {i18n.translate('GRID_SELECTED_LABEL', { values: { frame: currentFrame + 1 } })}
        {selectedInstance && (
          <span className="frames">
            <Frames />
            {selectedInstanceItem ? Object.keys(selectedInstanceItem.frameStatus).length : Object.keys(selectedInstance.frameStatus).length}
          </span>
        )}
      </div>
      <div>
        {i18n.translate('GRID_SCALE_LABEL')}
        <Slider
          tooltipVisible={false}
          step={1}
          min={0}
          max={gridSizes.length - 1}
          value={gridSizeIndex}
          onChange={setGridSizeIndex}
        />
        <div
          className="icon-button"
          onClick={reset}
        >
          <Aim />
        </div>
      </div>
    </div>
  );
});

export default Header;
