import React from 'react';
import { observer } from 'mobx-react';
import cx from 'classnames';
import Dropdown, { MenuItem, MenuItemType } from '../../../common/dropdown/Dropdown';
import { Attributes } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { AttributesMode } from '../../types';

const AttributesModeButton = observer(() => {
  const handleAttributesModeChange = (mode: string, _: number, item: MenuItem) => {
    const [type, value] = item.value!.split('-');
    store.config.setAttributesMode(value as AttributesMode, type);
  };

  const { activeAttributesMode, activePointAttributesMode, activeRelationshipsMode } = store.config;
  return (
    <Dropdown
      arrow
      showIcon
      style={{ paddingRight: 6, height: '100%' }}
      menuStyle={{ width: 120 }}
      menu={[
        { label: i18n.translate('ATTRIBUTES_MODE_POINT'), type: MenuItemType.TITLE },
        ...Object.values(AttributesMode).map((mode) => ({
          label: i18n.translate(`ATTRIBUTES_MODE_${mode.toUpperCase()}`),
          value: `point-${mode}`,
          active: mode === activePointAttributesMode,
        })),
        { label: i18n.translate('ATTRIBUTES_MODE_SHAPE'), type: MenuItemType.TITLE },
        ...Object.values(AttributesMode).map((mode) => ({
          label: i18n.translate(`ATTRIBUTES_MODE_${mode.toUpperCase()}`),
          value: `shape-${mode}`,
          active: mode === activeAttributesMode,
        })),
        ...store.relationship.relationshipEnabled ? [
          { label: i18n.translate('ATTRIBUTES_MODE_RELATIONSHIP'), type: MenuItemType.TITLE },
          ...Object.values(AttributesMode).map((mode) => ({
            label: i18n.translate(`ATTRIBUTES_MODE_${mode.toUpperCase()}`),
            value: `relationship-${mode}`,
            active: mode === activeRelationshipsMode,
          })),
        ] : [],
      ]}
      onClick={handleAttributesModeChange}
    >
      <div
        className={cx('icon-button', {
          'icon-button--active': activeAttributesMode !== AttributesMode.HIDE
            || activePointAttributesMode !== AttributesMode.HIDE
            || activeRelationshipsMode !== AttributesMode.HIDE,
        })}
        style={{ paddingRight: 0 }}
      >
        <Attributes />
      </div>
    </Dropdown>
  );
});

export default AttributesModeButton;
