import DefaultMove from './cursors/DefaultMove';
import DefaultCrosshair from './cursors/DefaultCrosshair';
import DefaultEraser from './cursors/DefaultEraser';
import PointerEraser from './cursors/PointerEraser';
import CrosshairEraser from './cursors/CrosshairEraser';
import PixelSelector from './cursors/PixelSelector';

enum Cursor {
  INHERIT = 'inherit',
  DEFAULT = 'default',
  POINTER = 'pointer',
  CROSSHAIR = 'crosshair',
  ROTATE = 'alias',
  MOVE = 'move',
  COL_RESIZE = 'col-resize',
  ROW_RESIZE = 'row-resize',
  EW_RESIZE = 'ew-resize',
  NS_RESIZE = 'ns-resize',
  NESW_RESIZE = 'nesw-resize',
  NWSE_RESIZE = 'nwse-resize',
  ZOOM_IN = 'zoom-in',
  ZOOM_OUT = 'zoom-out',
  ALL_SCROLL = 'all-scroll',
  // custom
  DEFAULT_MOVE = 'default-move',
  DEFAULT_CROSSHAIR = 'default-crosshair',
  DEFAULT_ERASER = 'default-eraser',
  POINTER_ERASER = 'pointer-eraser',
  CROSSHAIR_ERASER = 'crosshair-eraser',
  PIXEL_SELECTOR = 'pixel-selector',
}

export function getCursor(cursor: Cursor): string {
  switch (cursor) {
    case Cursor.DEFAULT_MOVE:
      return `url(${DefaultMove}) 21 19, pointer`;
    case Cursor.DEFAULT_CROSSHAIR:
      return `url(${DefaultCrosshair}) 21 19, pointer`;
    case Cursor.DEFAULT_ERASER:
      return `url(${DefaultEraser}) 21 19, pointer`;
    case Cursor.POINTER_ERASER:
      return `url(${PointerEraser}) 21 19, pointer`;
    case Cursor.CROSSHAIR_ERASER:
      return `url(${CrosshairEraser}) 19 19, pointer`;
    case Cursor.PIXEL_SELECTOR:
      return `url(${PixelSelector}) 21 19, pointer`;
    default:
      return cursor;
  }
}

export default Cursor;
