import React from 'react';
import { makeAutoObservable, runInAction } from 'mobx';
import { v4 as uuidv4 } from 'uuid';
import { message } from 'antd';
import { MessageType } from 'antd/es/message';
import { cloneDeep, debounce } from 'lodash';
import RootStore from './RootStore';
import Instance from '../model/Instance';
import InstanceItem from '../model/InstanceItem';
import Relationship from '../model/Relationship';
import ShapeWorker from '../worker/shape.worker';
import InterpolationWorker, { InterpolatedShape, InterpolationData, InterpolationResult, ShapeResolutionData, ShapeResolutionResult } from '../worker/interpolation.worker';
import i18n from '../locales';
import { formatFrames, getNextKeyFrames, getShapeTypeByTool, getToolTypeFromFrameData, includeSameTool, isCategoryChangeAllowed, isNumberChangeAllowed, isShapeDataMinified, preciseShapeByType, updateAttributesByFieldsCompare } from '../utils';
import { Instance as IInstance, InstanceItem as IInstanceItem, CategoryItem, Relationship as IRelationship, DraggableType, CameraData, NumberingMode, ViewMode, InstanceItemNumberingMode, FrameData, DynamicAttributes } from '../types';
import { ShapeData, ShapeType } from '../../common/shapes/types';
import { PixelData } from '../../common/shapes/Pixel';
import Message from '../components/message/Message';

export const interpolationSupportedShapeTypes = [
  ShapeType.PIXEL,
  ShapeType.POLYGON,
];

/**
 * store for instances
 * @class
 */
export default class InstanceStore {
  /**
   * root store
   */
  rootStore: typeof RootStore;

  /**
   * all instances
   */
  instances: { [instanceId: string]: Instance } = {};

  /**
   * selected instances
   */
  selectedInstances: Instance[] = [];

  /**
   * selected instance items
   */
  selectedInstanceItems: InstanceItem[] = [];

  /**
   * current dragging type
   */
  draggingType: DraggableType = DraggableType.INSTANCE;

  /**
   * current max instance item number (used when item numbering mode is global)
   */
  maxInstanceItemNumber = 0;

  /**
   * instance items waiting for attributes setting
   */
  attrPendingInstanceItems: InstanceItem[] = [];

  /**
   * last selected pending instance item
   */
  lastPendingInstanceItem: InstanceItem | null = null;

  /**
   * minified shapes
   */
  minifiedShapes: {
    instanceId: string
    instanceItemId: string;
    camera: string;
    frameIndex: number;
    shapeType: ShapeType;
    shape: ShapeData;
  }[] = [];

  /**
   * minified shapes count
   */
  minifiedShapesCount = 0;

  /**
   * minified shape resolver
   */
  minifiedShapeResolver?: Worker;

  /**
   * interpolation worker
   */
  interpolator?: Worker;

  /**
   * interpolated shapes (not add to store yet)
   */
  interpolatedShapes: Record<string, {
    timestamp: number;
    frameIndex: number;
    camera: string;
    shapes: Record<string, InterpolatedShape>;
  }> = {};

  /**
   * interpolated success frames
   */
  interpolatedFrames: Set<number> = new Set();

  /**
   * reorganizing instance
   */
  reorganizingInstance?: Instance;

  /**
   * instance item name being reorganizing
   */
  reorganizingInstanceItemName?: string;

  /**
   * reorganizing message
   */
  reorganizingMsg?: MessageType;

  /**
   * is any category or number change disblaed
   */
  isAnyCategoryOrNumberChangeDisabled = false;

  /**
   * category instances map
   * @getter
   */
  get categoryInstancesMap() {
    const map: { [categoryName: string]: Instance[] } = {};
    Object.values(this.instances).forEach((instance) => {
      if (!map[instance.category]) {
        map[instance.category] = [];
      }
      map[instance.category].push(instance);
    });
    this.rootStore.ontology.categories.forEach(({ className }) => {
      if (!map[className]) {
        map[className] = [];
      } else {
        map[className].sort((a, b) => a.number - b.number);
      }
    });
    return map;
  };

  /**
   * all instances sorted by timeline
   * @getter
   */
  get allInstances() {
    const { categories } = this.rootStore.ontology;
    return Object.values(this.instances)
      .sort((a, b) => {
        const instanceAFirstFrame = Number(Object.keys(a.frameStatus)[0]);
        const instanceBFirstFrame = Number(Object.keys(b.frameStatus)[0]);
        if (instanceAFirstFrame > instanceBFirstFrame) {
          return 1;
        }
        if (instanceAFirstFrame < instanceBFirstFrame) {
          return -1;
        }
        const instanceACategoryIndex = categories.findIndex((c) => c.className === a.category);
        const instanceBCategoryIndex = categories.findIndex((c) => c.className === b.category);
        if (instanceACategoryIndex > instanceBCategoryIndex) {
          return 1;
        }
        if (instanceACategoryIndex < instanceBCategoryIndex) {
          return -1;
        }
        return a.number > b.number ? 1 : -1;
      });
  }

  /**
   * multiple instance item selected
   * @getter
   */
  get isMultiSelected() {
    return this.selectedInstances.length > 1 || this.selectedInstanceItems.length > 1;
  }

  /**
   * single instance item selected
   */
  get isSingleSelected() {
    return this.selectedInstances.length === 1 && this.selectedInstanceItems.length === 1;
  }

  /**
   * does selected instance has label config to set attributes
   * @getter
   */
  get selectedInstanceAttributesEnabled() {
    if (this.isMultiSelected || this.selectedInstances.length <= 0) {
      return false;
    }

    const selectedInstance = this.selectedInstances[0];
    const selectedInstanceItem = this.selectedInstanceItems.length === 1 ? this.selectedInstanceItems[0] : undefined;
    return this.isAttributesEnabled(selectedInstance, selectedInstanceItem);
  }

  /**
   * is all shapes locked in current frame
   */
  get currentLockedStatus() {
    const { currentFrame } = this.rootStore.frame;
    const allInstances = Object.values(this.instances);

    let currentEmpty = true;
    let locked = true;
    for (let i = 0; i < allInstances.length; i += 1) {
      const instance = allInstances[i];
      const status = instance.frameLocked[currentFrame];
      if (status !== undefined) {
        currentEmpty = false;
        if (locked && !status) {
          locked = false;
        }
      }
      if (!currentEmpty && !locked) {
        break;
      }
    }

    return {
      locked,
      currentEmpty,
    };
  }

  /**
   * category locked status
   */
  get categoryLockedStatus() {
    const { currentFrame } = this.rootStore.frame;
    const { categories } = this.rootStore.ontology;
    const allInstances = Object.values(this.instances);

    const lockedStatus: {
      [category: string]: {
        locked: boolean;
        currentEmpty: boolean;
      }
    } = categories.reduce((acc, curr) => ({
      ...acc,
      [curr.className]: { locked: true, currentEmpty: true },
    }), {});

    for (let i = 0; i < allInstances.length; i += 1) {
      const instance = allInstances[i];
      const { category } = instance;
      if (lockedStatus[category].locked || lockedStatus[category].currentEmpty) {
        const status = instance.frameLocked[currentFrame];
        if (status !== undefined) {
          lockedStatus[instance.category].currentEmpty = false;
          if (lockedStatus[instance.category].locked && !status) {
            lockedStatus[instance.category].locked = false;
          }
        }
      }
    }

    return lockedStatus;
  }

  constructor(rootStore: typeof RootStore) {
    makeAutoObservable(this, {
      rootStore: false,
      maxInstanceItemNumber: false,
      attrPendingInstanceItems: false,
      lastPendingInstanceItem: false,
      minifiedShapes: false,
      minifiedShapeResolver: false,
      interpolator: false,
      interpolatedShapes: false,
      interpolatedFrames: false,
      reorganizingInstance: false,
      reorganizingInstanceItemName: false,
      reorganizingMsg: false,
      isAnyCategoryOrNumberChangeDisabled: false,
    }, {
      autoBind: true,
    });

    this.rootStore = rootStore;
  }

  clearInstances() {
    this.instances = {};
    this.selectedInstances = [];
    this.selectedInstanceItems = [];
  }

  /**
   * init instances
   * @param instances
   * @param fromPreAnnotation
   */
  async init(instances: IInstance[], fromPreAnnotation = false) {
    this.clearInstances();
    const { categories } = this.rootStore.ontology;
    const { cameraNames, updateNextShapeOrder } = this.rootStore.frame;
    const { layers, numberingMode, instanceItemNumberingMode, dataPrecision } = this.rootStore.config;
    const layersCount = layers.length || 1;
    const globalInstanceItemNumbers: number[] = [];
    const categoryNumbers: { [category: string]: number[] } = {};
    const numberDuplicatedInstances: {
      [instanceId: string]: {
        instance: IInstance;
        fixInstance: boolean;
        fixItems?: string[];
      };
    } = {};
    instances.forEach((instance) => {
      const category = categories.find((c) => c.className === instance.category);
      if (category) {
        let instanceNumberChangeDisabled = false;
        instance.children.forEach((instanceItem) => {
          const categoryItem = category.children.find((c) => c.name === instanceItem.name);
          if (categoryItem) {
            instanceItem.cameras.forEach((cameraData) => {
              if (cameraNames.includes(cameraData.camera)) {
                cameraData.frames.forEach((frameData) => {
                  // fix shape data precision
                  preciseShapeByType(frameData.shapeType, frameData.shape, dataPrecision);
                  // fix shape layer
                  if (frameData.layer === undefined || frameData.layer >= layersCount || frameData.layer < 0) {
                    frameData.layer = 0;
                  }
                  // update next shape order
                  if (typeof frameData.order === 'number' && frameData.order >= 0) {
                    updateNextShapeOrder(frameData.order, frameData.layer, frameData.frameIndex, cameraData.camera);
                  }
                  // check minified
                  if (isShapeDataMinified(frameData.shapeType, frameData.shape)) {
                    this.minifiedShapes.push({
                      instanceId: instance.id,
                      instanceItemId: instanceItem.id,
                      camera: cameraData.camera,
                      frameIndex: frameData.frameIndex,
                      shapeType: frameData.shapeType,
                      shape: frameData.shape,
                    });
                    this.minifiedShapesCount += 1;
                  }
                  // set raw status
                  frameData.isRaw = frameData.isRaw || fromPreAnnotation;
                  // set category change disabled status
                  instanceNumberChangeDisabled = instanceNumberChangeDisabled
                    || !isNumberChangeAllowed(frameData.preAnnotationData?.editMode);
                  this.isAnyCategoryOrNumberChangeDisabled = this.isAnyCategoryOrNumberChangeDisabled
                    || instanceNumberChangeDisabled
                    || !isCategoryChangeAllowed(frameData.preAnnotationData?.editMode);
                });
              } else {
                cameraData.frames = [];
              }
            });
            instanceItem.cameras = instanceItem.cameras.filter((c) => c.frames.length > 0); // remove invalid camera data
          } else {
            instanceItem.cameras = [];
          }
        });
        instance.children = instance.children.filter((i) => i.cameras.length > 0); // remove invalid instance item data
        if (instance.children.length > 0) {
          // check instance item number
          if (instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL) {
            instance.children.forEach((child) => {
              if (this.rootStore.annotatable && globalInstanceItemNumbers.indexOf(child.number) >= 0) {
                if (!numberDuplicatedInstances[instance.id]) {
                  numberDuplicatedInstances[instance.id] = {
                    instance,
                    fixInstance: false,
                    fixItems: [],
                  };
                }
                numberDuplicatedInstances[instance.id].fixItems!.push(child.id);
              } else {
                globalInstanceItemNumbers.push(child.number);
                if (child.number > this.maxInstanceItemNumber) {
                  this.maxInstanceItemNumber = child.number;
                }
              }
            });
          }

          // check instance number
          if (!categoryNumbers[category.className]) {
            categoryNumbers[category.className] = [];
          }
          const checkNumbers = numberingMode === NumberingMode.GLOBAL
            ? Object.values(categoryNumbers).flatMap((n) => n)
            : categoryNumbers[category.className];
          if (this.rootStore.annotatable && !instanceNumberChangeDisabled && checkNumbers.indexOf(instance.number) >= 0) {
            // number already exists, fix number later
            if (!numberDuplicatedInstances[instance.id]) {
              numberDuplicatedInstances[instance.id] = {
                instance,
                fixInstance: true,
              };
            } else {
              numberDuplicatedInstances[instance.id].fixInstance = true;
            }
          } else {
            categoryNumbers[category.className].push(instance.number);
          }

          // noneed to fix number
          if (!numberDuplicatedInstances[instance.id]?.fixInstance) {
            this.createInstanceFromData(instance);
          }
        }
      }
    });
    Object.values(numberDuplicatedInstances).forEach(({ instance, fixInstance, fixItems }) => {
      if (fixInstance) {
        const number = this.getNextInstanceNumber(instance.category);
        instance.number = number;
        this.createInstanceFromData(instance);
      }
      const currInstance = this.instances[instance.id];
      if (currInstance) {
        fixItems?.forEach((itemId) => {
          const item = instance.children.find((i) => i.id === itemId);
          if (item) {
            const number = this.getNextInstanceItemNumber(instance.id, item.name);
            item.number = number;
            currInstance.createItemFromData(item);
          }
        });
      }
    });
    this.resolveMinifiedShapes();
  }

  /**
   * create instance from structured instance data
   * @param instance
   */
  createInstanceFromData(instance: IInstance) {
    const category = this.rootStore.ontology.getCategory(instance.category);
    if (category) {
      const { instanceItemNumberingMode, dataPrecision } = this.rootStore.config;
      const forceMultiple = instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL;
      const newInstance = new Instance({
        id: instance.id,
        categoryRef: category,
        currentWorkerId: this.rootStore.jobProxy!.workerId,
        currentWorkerName: this.rootStore.jobProxy!.workerName,
        forceMultiple,
        dataPrecision,
        number: instance.number,
        attributes: instance.attributes,
        dynamicAttributes: instance.dynamicAttributes,
        items: instance.children,
        getNextShapeOrder: this.rootStore.frame.getNextShapeOrder,
      });
      this.instances[newInstance.id] = newInstance;
    }
  }

  /**
   * resolve minified shapes
   */
  resolveMinifiedShapes() {
    if (this.minifiedShapes.length > 0) {
      if (!this.minifiedShapeResolver) {
        this.minifiedShapeResolver = new ShapeWorker();
        this.minifiedShapeResolver!.addEventListener('message', (e) => {
          const { instanceId, instanceItemId, camera, frameIndex, shape } = e.data;
          const instance = this.instances[instanceId];
          if (instance) {
            const instanceItem = instance.items[instanceItemId];
            if (instanceItem) {
              const cameraData = instanceItem.cameras[camera];
              if (cameraData) {
                const frameData = cameraData.frames[frameIndex];
                if (frameData && !frameData.shapeUpdated) {
                  // only update when shape data haven't been updated
                  runInAction(() => {
                    frameData.shape = shape;
                  });
                }
              }
            }
          }
          // continue
          this.resolveMinifiedShapes();
        });
      }

      this.minifiedShapesCount -= 1;
      const item = this.minifiedShapes.pop();
      this.minifiedShapeResolver!.postMessage(item);
    }
  }

  /**
   * is instance attributes enable
   * @param instance
   */
  // eslint-disable-next-line class-methods-use-this
  isInstanceAttributesEnabled(instance: Instance) {
    // selected instance has label config setting
    return !!instance.categoryRef.labelConfig;
  }

  /**
   * is instance dynamic attributes enable
   * @param instance
   */
  // eslint-disable-next-line class-methods-use-this
  isInstanceDynamicAttributesEnabled(instance: Instance) {
    // selected instance has label config setting
    return !!instance.categoryRef.labelConfigDynamic;
  }

  /**
   * is instance item attributes enabled
   * @param instanceItem
   */
  isInstanceItemAttributesEnabled(instanceItem: InstanceItem) {
    const { currentCamera, currentFrame } = this.rootStore.frame;
    const cameraData = instanceItem.cameras[currentCamera];
    if (cameraData) {
      const frameData = cameraData.frames[currentFrame];
      if (frameData) {
        if (frameData.isOCR || frameData.isFormula) {
          // instance item currently is ocr shape or formula shape
          return true;
        }
        if (instanceItem.categoryItemRef.labelConfig) {
          // instance item has label config setting
          return true;
        }
      }
    }
    return false;
  };

  /**
   * is instance & instance item attributes enabled
   * @param instance
   * @param instanceItem
   */
  isAttributesEnabled(instance: Instance, instanceItem?: InstanceItem) {
    const instanceAttributesEnabled = this.isInstanceAttributesEnabled(instance);
    if (instanceAttributesEnabled) {
      return true;
    }
    const instanceDynamicAttributesEnabled = this.isInstanceDynamicAttributesEnabled(instance);
    if (instanceDynamicAttributesEnabled) {
      return true;
    }
    if (instanceItem) {
      return this.isInstanceItemAttributesEnabled(instanceItem);
    }
    return Object.values(instance.items).some((item) => this.isInstanceItemAttributesEnabled(item));
  }

  /**
   * get instance by id
   * @param instanceId
   */
  getInstanceById(instanceId: string) {
    return this.instances[instanceId];
  }

  /**
   * get next instance number
   * @param categoryName
   */
  getNextInstanceNumber(categoryName: string) {
    if (this.rootStore.config.numberingMode === NumberingMode.GLOBAL) {
      const allInstanceNumbers = Object.values(this.instances).map((i) => i.number);
      return Math.max(...allInstanceNumbers, 0) + 1;
    }
    const categoryInstanceNumbers = (this.categoryInstancesMap[categoryName] || []).map((i) => i.number);
    return Math.max(...categoryInstanceNumbers, 0) + 1;
  }

  /**
   * get next instance item number
   * @param instanceId
   * @param categoryItemName
   * @param camera
   */
  getNextInstanceItemNumber(instanceId: string, categoryItemName: string, camera = this.rootStore.frame.currentCamera) {
    if (this.rootStore.config.instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL) {
      this.maxInstanceItemNumber += 1;
      return this.maxInstanceItemNumber;
    }
    const instance = this.instances[instanceId];
    const items = Object.values(instance.items).filter((i) => i.name === categoryItemName);
    const itemsInCamera = items.filter((i) => {
      const cameraData = i.cameras[camera];
      return cameraData && !cameraData.isEmpty;
    });
    const itemNumbers = itemsInCamera.map((i) => i.number);
    return Math.max(...itemNumbers, 0) + 1;
  }

  /**
   * update selected instance
   * @param instance
   */
  updateSelectedInstance(instance: Instance | Instance[]) {
    const instances = Array.isArray(instance) ? instance : [instance];
    this.selectedInstances.forEach((i) => {
      if (instances.indexOf(i) < 0) {
        i.setSelected(false);
      }
    });
    this.selectedInstances = [...instances];
    this.selectedInstances.forEach((i) => {
      i.setSelected(true);
    });
  }

  /**
   * update selected instance item
   * @param instanceItem
   */
  updateSelectedInstanceItem(instanceItem: InstanceItem | InstanceItem[]) {
    const instanceItems = Array.isArray(instanceItem) ? instanceItem : [instanceItem];
    this.selectedInstanceItems.forEach((i) => {
      if (instanceItems.indexOf(i) < 0) {
        i.setSelected(false);
      }
    });
    this.selectedInstanceItems = [...instanceItems];
    this.selectedInstanceItems.forEach((i) => {
      i.setSelected(true);
    });
  }

  /**
   * select instance
   * @param instance
   * @param autoFocus
   * @param shouldPredict
   */
  selectInstance(instance?: Instance | Instance[] | null, autoFocus = false, shouldPredict = true) {
    const instances = Array.isArray(instance) ? instance : [...instance ? [instance] : []];
    this.updateSelectedInstance(instances);

    if (instances.length > 0) {
      // filter selected instance item
      const instanceItems: InstanceItem[] = this.selectedInstanceItems.filter((i) => instances.indexOf(i.instance) >= 0);

      // if the instance is single, default select first instance item
      if (this.rootStore.frame.isSingleCamera) {
        instances.forEach((i) => {
          if (i.isSingle && Object.keys(i.items).length > 0) {
            const item = Object.values(i.items)[0];
            if (instanceItems.indexOf(item) < 0) {
              instanceItems.push(item);
            }
          }
        });
      }
      this.updateSelectedInstanceItem(instanceItems);

      // select category & category item if needed
      if (instanceItems.length === 1) {
        // only one item is selected
        this.rootStore.ontology.selectCategory(instanceItems[0].instance.category);
        this.rootStore.ontology.selectCategoryItem(instanceItems[0].name);
      } else {
        // select first category if needed
        const allCategories = instances.map((i) => i.category);
        if (allCategories.indexOf(this.rootStore.ontology.selectedCategoryName) < 0) {
          this.rootStore.ontology.selectCategory(allCategories[0]);
        }
      }
    } else {
      this.updateSelectedInstanceItem([]);
    }

    // update shape selection
    this.rootStore.shape.selectShapeByInstanceItem(this.selectedInstanceItems, shouldPredict);
    this.rootStore.shape.updateShapesInInstance(instances);
    if (autoFocus) {
      if (this.rootStore.config.viewMode === ViewMode.DEFAULT) {
        this.rootStore.shape.fitShapes();
      } else {
        // this.rootStore.grid.gridCanvas?.fitShapes();
      }
    }
  }

  /**
   * select instance item
   * @param instanceItem
   * @param autoFocus
   * @param shouldPredict
   */
  selectInstanceItem(instanceItem?: InstanceItem | InstanceItem[] | null, autoFocus = false, shouldPredict = true) {
    const instanceItems = Array.isArray(instanceItem) ? instanceItem : [...instanceItem ? [instanceItem] : []];
    this.updateSelectedInstanceItem(instanceItems);
    const instanceSet = new Set(instanceItems.map((i) => i.instance));
    const instances = Array.from(instanceSet);
    this.selectInstance(instances, autoFocus, shouldPredict);
  }

  /**
   * update active layer index by instance items
   * @param instanceItems
   */
  updateActiveLayerIndexByInstanceItems(instanceItems: InstanceItem[]) {
    const { layers, activeLayerIndex } = this.rootStore.config;
    if (layers.length === 0) {
      // not multi layers
      return;
    }

    const { currentCamera, currentFrame } = this.rootStore.frame;
    const allLayers = new Set<number>();
    instanceItems.forEach((instanceItem) => {
      const { layer = activeLayerIndex } = instanceItem.cameras[currentCamera]?.frames[currentFrame] || {};
      allLayers.add(layer);
    });
    if (!allLayers.has(activeLayerIndex) && allLayers.size > 0) {
      const allLayersArr = Array.from(allLayers);
      this.rootStore.config.setActiveLayerIndex(allLayersArr[0]);
    }
  }

  /**
   * get current editing instance item
   */
  getCurrentInstanceItem(
    currentCategoryName = this.rootStore.ontology.selectedCategoryName,
    currentCategoryItemName = this.rootStore.ontology.selectedCategoryItemName,
    create = true
  ) {
    const selectedInstance = this.selectedInstances.find((i) => i.category === currentCategoryName) || this.selectedInstances[0];
    if (selectedInstance && selectedInstance.category === currentCategoryName) {
      const selectedInstanceItems = this.selectedInstanceItems.filter((i) => i.instance === selectedInstance);
      const selectedInstanceItem = selectedInstanceItems.find((i) => i.name === currentCategoryItemName) || selectedInstanceItems[0];
      if (selectedInstanceItem && selectedInstanceItem.name === currentCategoryItemName) {
        // has selected instance item
        const { currentCamera, currentFrame } = this.rootStore.frame;
        const { frames } = selectedInstanceItem.cameras[currentCamera];
        if (!frames[currentFrame]) {
          // not exist in current frame
          return selectedInstanceItem;
        }
      }
      if (!create) {
        return undefined;
      }

      const category = this.rootStore.ontology.getCategoryItem(currentCategoryName, currentCategoryItemName);
      if (category) {
        const { currentCamera } = this.rootStore.frame;
        const { name, count, maxCount = Infinity } = category;
        // find items exist in current camera
        const isGlobalItem = this.rootStore.config.instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL;
        const items = selectedInstance.getItemsByName(name).filter((i) => {
          const cameraData = i.cameras[currentCamera];
          return isGlobalItem || (cameraData && !cameraData.isEmpty);
        });
        if ((count !== undefined && items.length < count) || (count === undefined && items.length < maxCount)) {
          return this.createInstanceItem(selectedInstance, currentCategoryItemName);
        }
      }
    }
    if (!create) {
      return undefined;
    }
    // return a new instance
    const instance = this.createInstance(currentCategoryName);
    return this.createInstanceItem(instance!, currentCategoryItemName);
  }

  /**
   * get instance item for paste
   * @param shapeType
   */
  getInstanceItemForPaste(shapeType: ShapeType) {
    const { selectedCategory } = this.rootStore.ontology;

    const supportedCategoryItems: CategoryItem[] = [];
    selectedCategory.children.forEach((item) => {
      const shapeTypes = new Set(item.tools.map((tool) => getShapeTypeByTool(tool.type)));
      if (shapeTypes.has(shapeType)) {
        supportedCategoryItems.push(item);
      }
    });
    if (supportedCategoryItems.length <= 0) {
      // no avaliable category items
      return null;
    }

    const supportedCategoryItemNames = supportedCategoryItems.map((i) => i.name);
    const selectedInstance = this.selectedInstances.find((i) => i.category === selectedCategory.className);
    if (selectedInstance) {
      const { currentCamera, currentFrame } = this.rootStore.frame;
      const supportedSelectedInstanceItems = this.selectedInstanceItems.filter((i) => i.instance === selectedInstance && supportedCategoryItemNames.includes(i.name));

      let instanceItem;
      let i = 0;
      while (!instanceItem && i < supportedSelectedInstanceItems.length) {
        const item = supportedSelectedInstanceItems[i];
        const { frames } = item.cameras[currentCamera];
        if (!frames[currentFrame]) {
          instanceItem = item;
        }
        i += 1;
      }
      if (instanceItem) {
        return instanceItem; // find selected empty instance item
      }

      i = 0;
      while (!instanceItem && i < supportedCategoryItems.length) {
        const categoryItem = supportedCategoryItems[i];
        const { name, count, maxCount = Infinity } = categoryItem;
        const items = selectedInstance.getItemsByName(name);
        if ((count !== undefined && items.length < count) || (count === undefined && items.length < maxCount)) {
          instanceItem = this.createInstanceItem(selectedInstance, name);
        }
        i += 1;
      }
      if (instanceItem) {
        return instanceItem; // new instance item
      }
    }

    const instance = this.createInstance(selectedCategory.className);
    return this.createInstanceItem(instance!, supportedCategoryItemNames[0]);
  }

  /**
   * create instance
   * @param categoryName
   * @param defaultNumber
   */
  createInstance(
    categoryName = this.rootStore.ontology.selectedCategory.className,
    defaultNumber?: number,
  ) {
    const category = this.rootStore.ontology.getCategory(categoryName);
    if (category) {
      const { instanceItemNumberingMode, dataPrecision } = this.rootStore.config;
      const forceMultiple = instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL;
      const instance = new Instance({
        categoryRef: category,
        currentWorkerId: this.rootStore.jobProxy!.workerId,
        currentWorkerName: this.rootStore.jobProxy!.workerName,
        forceMultiple,
        dataPrecision,
        number: defaultNumber !== undefined ? defaultNumber : this.getNextInstanceNumber(categoryName),
        getNextShapeOrder: this.rootStore.frame.getNextShapeOrder,
      });
      this.instances[instance.id] = instance;
      return instance;
    }
    return undefined;
  }

  /**
   * create instance item
   * @param instance
   * @param categoryItem
   * @param defaultNumber
   */
  createInstanceItem(
    instance: Instance,
    categoryItem = this.rootStore.ontology.selectedCategoryItem.name,
    defaultNumber?: number,
  ) {
    const number = defaultNumber !== undefined ? defaultNumber : this.getNextInstanceItemNumber(instance.id, categoryItem);
    const existItem = instance.getItem(categoryItem, number);
    if (existItem) {
      return existItem;
    }
    // create
    const { children = [] } = instance.categoryRef;
    const item = children.find((c) => c.name === categoryItem);
    const instanceItem = new InstanceItem({
      instance,
      categoryItemRef: item || children[0],
      number,
    });
    instance.items[instanceItem.id] = instanceItem;
    return instanceItem;
  }

  /**
   * delete instance
   * @param instance
   */
  deleteInstance(instance: Instance | Instance[]) {
    const instances = Array.isArray(instance) ? instance : [instance];
    this.selectedInstances = this.selectedInstances.filter((i) => instances.indexOf(i) < 0);
    this.selectedInstanceItems = this.selectedInstanceItems.filter((i) => instances.indexOf(i.instance) < 0);
    instances.forEach((i) => {
      i.destroy();
      delete this.instances[i.id];
    });
  }

  /**
   * delete instance item
   * @param instanceItem
   */
  deleteInstanceItem(instanceItem: InstanceItem | InstanceItem[]) {
    const instanceItems = Array.isArray(instanceItem) ? instanceItem : [instanceItem];
    this.selectedInstanceItems = this.selectedInstanceItems.filter((i) => instanceItems.indexOf(i) < 0);
    instanceItems.forEach(({ id, instance }) => {
      instance.removeItem(id);
    });
  }

  /**
   * delete frames from instance item
   * @param instanceItem
   * @param frames
   * @param camera
   */
  deleteFramesFromInstanceItem = (
    instanceItem: InstanceItem,
    frames: number[],
    camera = this.rootStore.frame.currentCamera,
  ) => {
    const { currentCamera, currentFrame } = this.rootStore.frame;
    const unlockedFrames = frames.filter((frameIndex) => {
      const frameData = instanceItem.cameras[camera]?.frames[frameIndex];
      return !frameData?.deleteLocked;
    });

    const prevBasicInfo = instanceItem.instance.getBasicInfo();
    if (camera === currentCamera && unlockedFrames.includes(currentFrame)) {
      // remove shape
      this.rootStore.shape.deleteShapeByInstanceItem(instanceItem);
      // update segmentation store
      const { shapeType, layer = this.rootStore.config.activeLayerIndex } = instanceItem.cameras[camera]?.frames[currentFrame] || {};
      if (shapeType === ShapeType.PIXEL) {
        this.rootStore.segmentation.update(instanceItem, layer);
      }
    }

    // remove relationship
    const { prevRelationships, currRelationships } = this.rootStore.relationship.deleteFromFramesByInstanceItem(instanceItem, camera, unlockedFrames);
    const { prevState, currState } = instanceItem.remove(camera, unlockedFrames);
    const basicInfo = instanceItem.instance.getBasicInfo();
    this.rootStore.undo.push({
      instances: prevState ? [{ ...prevBasicInfo, children: [prevState] }] : [],
      relationships: [...prevRelationships],
    }, {
      instances: currState ? [{ ...basicInfo, children: [currState] }] : [],
      relationships: [...currRelationships],
    });

    this.rootStore.nifti?.updateShape(instanceItem, unlockedFrames);

    if (instanceItem.isEmpty) {
      this.deleteInstanceItem(instanceItem);
    }
    if (instanceItem.instance.isEmpty) {
      this.deleteInstance(instanceItem.instance);
    }
    if (!instanceItem.cameras[camera]) {
      this.selectInstanceItem(null);
      this.selectInstance(instanceItem.instance.isEmpty ? null : instanceItem.instance);
    }
  };

  /**
   * delete frames from instance items
   * @param instanceItems
   */
  deleteFramesFromInstanceItems = (instanceItems: { instanceItem: InstanceItem, frames: number[], camera?: string }[]) => {
    const { currentCamera, currentFrame } = this.rootStore.frame;
    const prevInstanceMap: { [instanceId: string]: IInstance } = {};
    const currInstanceMap: { [instanceId: string]: IInstance } = {};
    const prevRelationshipMap: { [id: string]: IRelationship } = {};
    const currRelationshipMap: { [id: string]: IRelationship } = {};
    for (let i = 0; i < instanceItems.length; i += 1) {
      const { instanceItem, frames, camera = currentCamera } = instanceItems[i];
      const { instance } = instanceItem;
      const { id: instanceId } = instance;

      const unlockedFrames = frames.filter((frameIndex) => {
        const frameData = instanceItem.cameras[camera]?.frames[frameIndex];
        return !frameData?.deleteLocked;
      });

      if (camera === currentCamera && unlockedFrames.includes(currentFrame)) {
        // remove shape
        this.rootStore.shape.deleteShapeByInstanceItem(instanceItem);
        // update segmentation store
        const { shapeType, layer = this.rootStore.config.activeLayerIndex } = instanceItem.cameras[camera]?.frames[currentFrame] || {};
        if (shapeType === ShapeType.PIXEL) {
          this.rootStore.segmentation.update(instanceItem, layer);
        }
      }

      // remove relationship
      const { prevRelationships, currRelationships } = this.rootStore.relationship.deleteFromFramesByInstanceItem(instanceItem, camera, unlockedFrames);
      prevRelationships.forEach((r) => {
        if (!prevRelationshipMap[r.id]) {
          prevRelationshipMap[r.id] = r;
        }
      });
      currRelationships.forEach((r) => {
        currRelationshipMap[r.id] = r;
      });
      const prevInstanceBasicInfo = instance.getBasicInfo();
      const { prevState, currState } = instanceItem.remove(camera, unlockedFrames);
      if (prevState) {
        if (!prevInstanceMap[instanceId]) {
          prevInstanceMap[instanceId] = {
            ...prevInstanceBasicInfo,
            children: [],
          };
        }
        prevInstanceMap[instanceId].children.push(prevState);
      }
      if (currState) {
        if (!currInstanceMap[instanceId]) {
          currInstanceMap[instanceId] = {
            ...instance.getBasicInfo(),
            children: [],
          };
        }
        currInstanceMap[instanceId].children.push(currState);
      }

      this.rootStore.nifti?.updateShape(instanceItem, unlockedFrames);

      if (instanceItem.isEmpty) {
        this.deleteInstanceItem(instanceItem);
      }
      if (instance.isEmpty) {
        this.deleteInstance(instance);
      }
      if (!instanceItem.cameras[camera]) {
        this.selectInstanceItem(null);
        this.selectInstance(instance.isEmpty ? null : instance);
      }
    }
    if (Object.values(prevInstanceMap).length > 0) {
      this.rootStore.undo.push({
        instances: Object.values(prevInstanceMap),
        relationships: Object.values(prevRelationshipMap),
      }, {
        instances: Object.values(currInstanceMap),
        relationships: Object.values(currRelationshipMap),
      });
    }
  };

  deleteInstanceItemByFrame = (
    instanceItem: InstanceItem,
    frame: number,
    camera = this.rootStore.frame.currentCamera,
  ) => {
    const { currentCamera, currentFrame } = this.rootStore.frame;
    const prevInstanceBasicInfo = instanceItem.instance.getBasicInfo();
    if (camera === currentCamera && frame === currentFrame) {
      // remove shape
      this.rootStore.shape.deleteShapeByInstanceItem(instanceItem);
      // update segmentation store
      const { shapeType, layer = this.rootStore.config.activeLayerIndex } = instanceItem.cameras[camera]?.frames[currentFrame] || {};
      if (shapeType === ShapeType.PIXEL) {
        this.rootStore.segmentation.update(instanceItem, layer);
      }
    }
    // remove relationship
    const { prevRelationships, currRelationships } = this.rootStore.relationship.deleteFromFramesByInstanceItem(instanceItem, camera, [currentFrame]);

    const { prevState, currState } = instanceItem.remove(camera, [frame]);

    this.rootStore.nifti?.updateShape(instanceItem, [frame]);

    if (instanceItem.isEmpty) {
      this.deleteInstanceItem(instanceItem);
    }
    if (instanceItem.instance.isEmpty) {
      this.deleteInstance(instanceItem.instance);
    }
    return {
      prevState: {
        instances: prevState ? [{ ...prevInstanceBasicInfo, children: [prevState] }] : [],
        relationships: [...prevRelationships],
      },
      currState: {
        instances: currState ? [{ ...instanceItem.instance.getBasicInfo(), children: [currState] }] : [],
        relationships: [...currRelationships],
      },
    };
  };

  /**
   * delete frames from instance item by type
   * @param instanceItem
   * @param type
   * @param camera
   */
  deleteFramesFromInstanceItemByType = (
    instanceItem: InstanceItem,
    type: 'current' | 'key' | 'following' | 'all' = 'current',
    camera = this.rootStore.frame.currentCamera,
  ) => {
    const { currentFrame } = this.rootStore.frame;
    switch (type) {
      case 'current':
        this.deleteFramesFromInstanceItem(instanceItem, [currentFrame]);
        break;
      case 'key': {
        const nextKeyFrames = getNextKeyFrames(1, currentFrame, instanceItem.cameras[camera].frames);
        const nextKeyFrame = nextKeyFrames[0];
        if (nextKeyFrame) {
          this.deleteFramesFromInstanceItem(
            instanceItem,
            Array.from({ length: nextKeyFrame - currentFrame }).map((_, index) => currentFrame + index),
          );
        }
        break;
      }
      case 'following': {
        const allFrames = Object.keys(instanceItem.cameras[camera].frames).map((f) => parseInt(f, 10));
        const index = allFrames.indexOf(currentFrame);
        this.deleteFramesFromInstanceItem(
          instanceItem,
          allFrames.slice(index, allFrames.length),
        );
        break;
      }
      case 'all': {
        const allFrames = Object.keys(instanceItem.cameras[camera].frames).map((f) => parseInt(f, 10));
        this.deleteFramesFromInstanceItem(
          instanceItem,
          allFrames,
        );
        break;
      }
      default:
    }
  };

  /**
   * update frame shape info for instance item
   * @param instanceItem
   * @param frameIndex
   * @param shapeType
   * @param shape
   * @param layer
   * @param order
   * @param camera
   */
  updateFrameShapeForInstanceItem(instanceItem: InstanceItem, frameIndex: number, shapeType: ShapeType, shape: ShapeData, layer?: number, order?: number, camera = this.rootStore.frame.currentCamera) {
    const { autoInterpolation, activeLayerIndex } = this.rootStore.config;
    let shapeLayer = layer;
    if (shapeLayer === undefined) {
      const frameData = instanceItem.cameras[camera]?.frames[frameIndex];
      if (frameData?.layer !== undefined) {
        shapeLayer = frameData.layer;
      } else {
        shapeLayer = activeLayerIndex;
      }
    }
    const prevBasicInfo = instanceItem.instance.getBasicInfo();
    const { prevState, currState } = instanceItem.updateShape(
      camera,
      frameIndex,
      autoInterpolation && shapeType !== ShapeType.PIXEL,
      shapeType,
      shape,
      shapeLayer,
      order,
    );
    const basicInfo = instanceItem.instance.getBasicInfo();
    const instanceState: Record<string, { prev: IInstance, curr: IInstance }> = {
      [basicInfo.id]: {
        prev: { ...prevBasicInfo, children: prevState ? [prevState] : [] },
        curr: { ...basicInfo, children: currState ? [currState] : [] },
      }
    };
    const relationshipState: { prev: IRelationship[], curr: IRelationship[] } = {
      prev: [],
      curr: [],
    };

    const { currentCamera, currentFrame } = this.rootStore.frame;
    if (shapeType === ShapeType.PIXEL && camera === currentCamera && frameIndex === currentFrame) {
      const stat = this.rootStore.segmentation.update(instanceItem, shapeLayer, shape as PixelData);
      if (stat) {
        Object.keys(stat.instanceState).forEach((id) => {
          if (instanceState[id]) {
            const { prev, curr } = stat.instanceState[id];
            instanceState[id].prev.children = [...instanceState[id].prev.children, ...prev.children];
            instanceState[id].curr.children = [...instanceState[id].curr.children, ...curr.children];
          } else {
            instanceState[id] = stat.instanceState[id];
          }
        });
        relationshipState.prev = stat.relationshipState.prev;
        relationshipState.curr = stat.relationshipState.curr;
      }
    }

    this.rootStore.nifti?.updateShape(instanceItem, [frameIndex]);

    this.rootStore.undo.push({
      instances: Object.values(instanceState).map((i) => i.prev),
      relationships: relationshipState.prev,
    }, {
      instances: Object.values(instanceState).map((i) => i.curr),
      relationships: relationshipState.curr,
    });
  }

  /**
   * update frame shape info for instance items list
   * @param instanceItems
   */
  updateFrameShapeForInstanceItems(instanceItems: { instanceItem: InstanceItem, frameIndex: number, shapeType: ShapeType, shape?: ShapeData, layer?: number, order?: number, camera?: string }[]) {
    const prevInstanceMap: { [instanceId: string]: IInstance } = {};
    const currInstanceMap: { [instanceId: string]: IInstance } = {};
    const prevRelationshipMap: { [id: string]: IRelationship } = {};
    const currRelationshipMap: { [id: string]: IRelationship } = {};
    const { currentCamera, currentFrame } = this.rootStore.frame;
    for (let i = 0; i < instanceItems.length; i += 1) {
      const { instanceItem, frameIndex, shapeType, shape, layer, order, camera = this.rootStore.frame.currentCamera } = instanceItems[i];
      let state;
      const { autoInterpolation, activeLayerIndex } = this.rootStore.config;
      let shapeLayer = layer;
      if (shapeLayer === undefined) {
        const frameData = instanceItem.cameras[camera]?.frames[frameIndex];
        if (frameData?.layer !== undefined) {
          shapeLayer = frameData.layer;
        } else {
          shapeLayer = activeLayerIndex;
        }
      }
      const prevBasicInfo = instanceItem.instance.getBasicInfo();
      if (shape) {
        // update
        state = instanceItem.updateShape(
          camera,
          frameIndex,
          autoInterpolation && shapeType !== ShapeType.PIXEL,
          shapeType,
          shape,
          shapeLayer,
          order,
        );
        if (shapeType === ShapeType.PIXEL && camera === currentCamera && frameIndex === currentFrame) {
          const stat = this.rootStore.segmentation.update(instanceItem, shapeLayer, shape as PixelData);
          if (stat) {
            Object.keys(stat.instanceState).forEach((id) => {
              const { prev, curr } = stat.instanceState[id];
              if (!prevInstanceMap[id]) {
                prevInstanceMap[id] = prev;
              }
              currInstanceMap[id] = curr;
            });
            stat.relationshipState.prev.forEach((r) => {
              if (!prevRelationshipMap[r.id]) {
                prevRelationshipMap[r.id] = r;
              }
            });
            stat.relationshipState.curr.forEach((r) => {
              currRelationshipMap[r.id] = r;
            });
          }
        }
      } else {
        // delete
        this.rootStore.shape.deleteShapeByInstanceItem(instanceItem);
        // delete relationship
        const { prevRelationships, currRelationships } = this.rootStore.relationship.deleteFromFramesByInstanceItem(instanceItem, camera, [this.rootStore.frame.currentFrame]);
        prevRelationships.forEach((r) => {
          if (!prevRelationshipMap[r.id]) {
            prevRelationshipMap[r.id] = r;
          }
        });
        currRelationships.forEach((r) => {
          currRelationshipMap[r.id] = r;
        });

        state = instanceItem.remove(camera, [frameIndex]);
        if (shapeType === ShapeType.PIXEL) {
          this.rootStore.segmentation.update(instanceItem, shapeLayer);
        }
        if (instanceItem.isEmpty) {
          this.deleteInstanceItem(instanceItem);
        }
        if (instanceItem.instance.isEmpty) {
          this.deleteInstance(instanceItem.instance);
        }
        if (!instanceItem.cameras[camera]) {
          this.selectInstanceItem(null);
          this.selectInstance(instanceItem.instance.isEmpty ? null : instanceItem.instance);
        }
      }

      if (state) {
        const { prevState, currState } = state;
        const { instance } = instanceItem;
        const { id: instanceId } = instance;
        if (!prevInstanceMap[instanceId]) {
          prevInstanceMap[instanceId] = {
            ...prevBasicInfo,
            children: [],
          };
        }
        if (prevState) {
          prevInstanceMap[instanceId].children.push(prevState);
        }
        if (currState) {
          if (!currInstanceMap[instanceId]) {
            currInstanceMap[instanceId] = {
              ...instance.getBasicInfo(),
              children: [],
            };
          }
          currInstanceMap[instanceId].children.push(currState);
        }
      }

      this.rootStore.nifti?.updateShape(instanceItem, [frameIndex]);
    }
    this.rootStore.undo.push({
      instances: Object.values(prevInstanceMap),
      relationships: Object.values(prevRelationshipMap),
    }, {
      instances: Object.values(currInstanceMap),
      relationships: Object.values(currRelationshipMap),
    });
  }

  /**
   * move instance item
   * @param targetCategory
   * @param targetCategoryItem
   * @param targetInstanceId
   * @param targetInstanceItemId
   * @param frames
   * @param newInstanceAttr
   * @param newInstanceItemAttr
   */
  moveInstanceItem(targetCategory: string, targetCategoryItem: string, targetInstanceId: string, targetInstanceItemId: string, frames: number[], newDynamicInstanceAttr?: DynamicAttributes[], newInstanceAttr?: any, newInstanceItemAttr?: any) {
    if (!this.isSingleSelected) {
      return;
    }
    const selectedInstanceItem = this.selectedInstanceItems[0];
    const currPrevInstance = selectedInstanceItem.instance.getBasicInfo();
    const targetInstance = targetInstanceId === 'new'
      ? this.createInstance(targetCategory)
      : this.instances[targetInstanceId];
    if (targetInstance) {
      const targetPrevInstance = targetInstance.getBasicInfo();

      const targetInstanceItemDefaultNumber = this.rootStore.config.instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL
        ? selectedInstanceItem.number
        : undefined;
      const targetInstanceItem = targetInstanceItemId === 'new'
        ? this.createInstanceItem(targetInstance, targetCategoryItem, targetInstanceItemDefaultNumber)
        : targetInstance.items[targetInstanceItemId];
      if (targetInstanceItem) {
        // FIXME: current only support single camera
        const { currentCamera, currentFrame } = this.rootStore.frame;

        // update target instance item
        let currIsPixelShape = false;
        const currInstanceItem = selectedInstanceItem;
        const framesData = frames.map((frameIndex) => {
          const frameJson = currInstanceItem.cameras[currentCamera].frames[frameIndex].toJSON();
          if (frameIndex === currentFrame && frameJson.shapeType === ShapeType.PIXEL) {
            currIsPixelShape = true;
          }
          if (targetCategory === currInstanceItem.instance.category && targetCategoryItem === currInstanceItem.name) {
            return frameJson;
          }
          if (newInstanceItemAttr) {
            frameJson.attributes = newInstanceItemAttr;
          } else {
            delete frameJson.attributes;
          }
          return frameJson;
        });

        const { prevState: targetPrevState, currState: targetCurrState } = targetInstanceItem.updateFramesFromData(currentCamera, framesData);
        if (newInstanceAttr) {
          targetInstance.setAttributes(newInstanceAttr);
        }
        // update relationships
        const prevRelationships: { [id: string]: IRelationship } = {};
        const currRelationships: { [id: string]: IRelationship } = {};
        currInstanceItem.cameras[currentCamera].relationships.forEach((r) => {
          if (!prevRelationships[r.id]) {
            prevRelationships[r.id] = r.toJSON();
          }
          r.deleteFromFrames(frames);
          this.rootStore.relationship.deleteConnection(r);
          this.rootStore.relationship.createConnection(r);
          currRelationships[r.id] = r.toJSON();
        });
        // remove from current instance item
        const { prevState, currState } = currInstanceItem.remove(currentCamera, frames);

        // update instance dynamic attributes
        if (newDynamicInstanceAttr && !targetInstance.dynamicAttributes?.[currentCamera]) {
          targetInstance.setDynamicAttributesByCamera(currentCamera, newDynamicInstanceAttr);
        }
        if (currInstanceItem.isEmpty) {
          this.deleteInstanceItem(currInstanceItem);
        }
        if (currInstanceItem.instance.isEmpty) {
          this.deleteInstance(currInstanceItem.instance);
        }

        const targetCurrInstance = targetInstance.getBasicInfo();
        // update state
        if (currInstanceItem.instance.id === targetInstanceItem.instance.id) {
          // same instance
          const prevInstance: IInstance = { ...targetPrevInstance, children: [] }; // target instance old
          if (targetPrevState) {
            prevInstance.children.push(targetPrevState);
          }
          if (prevState) {
            prevInstance.children.push(prevState);
          }
          const currInstance: IInstance = { ...targetCurrInstance, children: [] };
          if (targetCurrState) {
            currInstance.children.push(targetCurrState);
          }
          if (currState) {
            currInstance.children.push(currState);
          }
          this.rootStore.undo.push({
            instances: prevInstance.children.length > 0 ? [prevInstance] : [],
            relationships: Object.values(prevRelationships),
          }, {
            instances: currInstance.children.length > 0 ? [currInstance] : [],
            relationships: Object.values(currRelationships),
          });
        } else {
          this.rootStore.undo.push({
            instances: [
              ...prevState ? [{ ...currPrevInstance, children: prevState ? [prevState] : [] }] : [],
              ...targetPrevState ? [{ ...targetPrevInstance, children: targetPrevState ? [targetPrevState] : [] }] : [],
            ],
            relationships: Object.values(prevRelationships),
          }, {
            instances: [
              { ...targetCurrInstance, children: currState ? [currState] : [] },
              { ...targetCurrInstance, children: targetCurrState ? [targetCurrState] : [] },
            ],
            relationships: Object.values(currRelationships),
          });
        }

        // change instance item
        if (currIsPixelShape) {
          this.rootStore.segmentation.changeInstanceItemForInfoMap(currInstanceItem, targetInstanceItem);
        }
        // uppdate shapes
        const currentShape = this.rootStore.shape.getShapeByInstanceItem(currInstanceItem);
        if (currentShape) {
          this.rootStore.shape.deleteShape(currentShape);
        }
        this.rootStore.shape.setupShape(targetInstanceItem);
      }
    }
  }

  /**
   * get target instance & affected instance with same number
   * @param sourceInstance
   * @param targetCategory
   * @param targetInstanceNum
   */
  getTargetInstance(
    sourceInstance: Instance,
    targetCategory: string,
    targetInstanceNum?: number,
  ) {
    const { numberingMode } = this.rootStore.config;
    let targetInstance: Instance | undefined;
    let isTargetInstanceNewCreated = false;
    let affectedInstanceInGlobalMode: Instance | undefined;
    if (targetInstanceNum !== undefined) {
      targetInstance = Object.values(this.instances).find((i) => i.category === targetCategory && i.number === targetInstanceNum);
      // if not found, find same number when in global mode
      if (!targetInstance && numberingMode === NumberingMode.GLOBAL) {
        affectedInstanceInGlobalMode = Object.values(this.instances).find((i) => i.number === targetInstanceNum);
      }
    }
    if (!targetInstance) {
      if (!affectedInstanceInGlobalMode?.isNumberChangeDisabled) {
        targetInstance = this.createInstance(targetCategory, targetInstanceNum);
      }
      if (targetInstance) {
        isTargetInstanceNewCreated = true;
        // set instance attributes
        const newInstanceAttributes = updateAttributesByFieldsCompare(
          sourceInstance.categoryRef.labelConfig?.fields || [],
          targetInstance.categoryRef.labelConfig?.fields || [],
          sourceInstance.attributes,
          {},
        );
        targetInstance.setAttributes(newInstanceAttributes);
      }
    }
    return {
      targetInstance,
      isTargetInstanceNewCreated,
      affectedInstanceInGlobalMode,
    };
  }

  /**
   * get target instance item in target instance
   * @param targetInstance
   * @param targetCategoryItem
   * @param isTargetInstanceNewCreated
   * @param sourceInstanceItemNumber
   */
  getTargetInstanceItem(
    targetInstance: Instance,
    targetCategoryItem: string,
    isTargetInstanceNewCreated: boolean,
    sourceInstanceItemNumber?: number,
  ) {
    const existingItems = targetInstance.getItemsByName(targetCategoryItem);
    const existingItemWithSameNumber = existingItems.find((i) => i.number === sourceInstanceItemNumber);
    if (existingItemWithSameNumber) {
      return existingItemWithSameNumber;
    }

    const isSingle = targetInstance.categoryRef.children.find((c) => c.name === targetCategoryItem)?.count === 1;
    if (isSingle && existingItems.length > 1) {
      throw new Error(i18n.translate('CHANGE_CATEGORY_ERROR_ITEM_COUNT'));
    }

    const { instanceItemNumberingMode } = this.rootStore.config;
    let targetInstanceItem: InstanceItem | undefined;
    if (isSingle && existingItems.length === 1) {
      targetInstanceItem = existingItems[0];
    }
    if (!targetInstanceItem) {
      let targetInstanceItemNumber: number | undefined;
      if (instanceItemNumberingMode === InstanceItemNumberingMode.GLOBAL) {
        if (isTargetInstanceNewCreated) {
          // keep item number in global mode when target instance is newly created
          targetInstanceItemNumber = sourceInstanceItemNumber;
        }
      } else if (!isSingle && existingItems.findIndex((item) => item.number === sourceInstanceItemNumber) < 0) {
        // multi-item instance, keep item number if not exist
        targetInstanceItemNumber = sourceInstanceItemNumber;
      }
      targetInstanceItem = this.createInstanceItem(targetInstance, targetCategoryItem, targetInstanceItemNumber);
    }
    return targetInstanceItem;
  }

  /**
   * change category for instance item
   * @param sourceInstanceItem
   * @param targetCategory
   * @param targetCategoryItem
   * @param affectedCameraFrames
   * @param targetInstanceNum
   */
  changeCategoryForInstanceItemPure(
    sourceInstanceItem: InstanceItem,
    targetCategory: string,
    targetCategoryItem: string,
    affectedCameraFrames: Record<string, number[]>,
    targetInstanceNum?: number,
  ) {
    const {
      categoryItemRef: sourceCategoryItemRef,
      instance: sourceInstance,
      number: sourceInstanceItemNumber,
    } = sourceInstanceItem;
    const {
      targetInstance,
      isTargetInstanceNewCreated,
      affectedInstanceInGlobalMode,
    } = this.getTargetInstance(sourceInstance, targetCategory, targetInstanceNum);

    if (!targetInstance) {
      throw new Error(i18n.translate('CHANGE_CATEGORY_ERROR_NO_TARGET_INSTANCE'));
    }

    if (
      this.reorganizingInstance?.category === targetInstance.category &&
      this.reorganizingInstance?.number === targetInstance.number &&
      this.reorganizingInstance?.id !== targetInstance.id
    ) {
      // update reorganizing instance if target instance is newly created
      this.reorganizingInstance = targetInstance;
    }

    const prevSourceInstanceBasicInfo = sourceInstance.getBasicInfo();
    const prevTargetInstanceBasicInfo = targetInstance.getBasicInfo();

    const targetInstanceItem = this.getTargetInstanceItem(
      targetInstance,
      targetCategoryItem,
      isTargetInstanceNewCreated,
      sourceInstanceItemNumber,
    );

    if (!targetInstanceItem) {
      throw new Error(i18n.translate('CHANGE_CATEGORY_ERROR_NO_TARGET_INSTANCE_ITEM'));
    }

    const { currentFrame } = this.rootStore.frame;
    const { categoryItemRef: targetCategoryItemRef } = targetInstanceItem;
    let instanceItemMovedTo: InstanceItem | undefined;

    const prevInstanceMap: Record<string, IInstance> = {};
    const currInstanceMap: Record<string, IInstance> = {};
    const prevRelationshipMap: Record<string, IRelationship> = {};
    const currRelationshipMap: Record<string, IRelationship> = {};
    const updateInstanceMap = (
      instanceMap: Record<string, IInstance>,
      instance: Instance,
      stat?: IInstanceItem,
      prevInstanceBasicInfo?: any,
    ) => {
      if (!instanceMap[instance.id]) {
        if (prevInstanceBasicInfo) {
          instanceMap[instance.id] = {
            ...prevInstanceBasicInfo,
            children: [],
          };
        } else {
          instanceMap[instance.id] = {
            ...instance.getBasicInfo(),
            children: [],
          };
        }
      }

      if (stat) {
        const index = instanceMap[instance.id].children.findIndex((child) => child.id === stat?.id);
        if (index >= 0) {
          instanceMap[instance.id].children[index] = {
            ...stat,
            cameras: instanceMap[instance.id].children[index].cameras.concat(stat.cameras),
          };
        } else {
          instanceMap[instance.id].children.push(stat);
        }
      }
    };

    const updatedFramesSet = new Set<number>();
    const affectedCameras = Object.keys(affectedCameraFrames);
    for (let c = 0; c < affectedCameras.length; c += 1) {
      const cameraName = affectedCameras[c];
      const sourceFramesCount = Object.values(sourceInstanceItem.cameras[cameraName]?.frames || {}).length;
      const targetFramesCount = Object.values(targetInstanceItem.cameras[cameraName]?.frames || {}).length;
      const { relationships: sourceRelationships } = sourceInstanceItem.cameras[cameraName] || {};
      const { relationships: targetRelationships } = targetInstanceItem.cameras[cameraName] || {};

      const frames = affectedCameraFrames[cameraName];
      // frames data to target instance item
      const framesData: FrameData[] = [];
      // frames data moved from target to new instance item
      const movedFrameData: FrameData[] = [];
      // relationships updated
      const updatedRelationships: Relationship[] = [];

      for (let i = 0; i < frames.length; i += 1) {
        const frameIndex = frames[i];
        const sourceFrameData = sourceInstanceItem.cameras[cameraName]?.frames[frameIndex];
        const targetFrameData = targetInstanceItem.cameras[cameraName]?.frames[frameIndex];

        if (
          (sourceInstance.category === targetCategory
            ? sourceFrameData?.numberChangeAllowed
            : sourceFrameData?.categoryChangeAllowed) &&
          (!targetFrameData || targetFrameData.numberChangeAllowed)
        ) {
          updatedFramesSet.add(frameIndex);
          const frameDataJson = sourceFrameData.toJSON();
          if ((targetCategoryItem !== sourceInstanceItem.name || targetCategory !== sourceInstance.category)
            && sourceFrameData.attrChangeAllowed) {
            // update attributes when category not same
            frameDataJson.attributes = updateAttributesByFieldsCompare(
              sourceCategoryItemRef?.labelConfig?.fields || [],
              targetCategoryItemRef.labelConfig?.fields || [],
              sourceFrameData.attributes,
              {},
            );
          }
          framesData.push(frameDataJson);

          // move target frame to new
          if (targetFrameData) {
            movedFrameData.push(targetFrameData.toJSON());
          }

          if (frameIndex === currentFrame) {
            // change instance item for pixel shape
            if (sourceFrameData.shapeType === ShapeType.PIXEL) {
              this.rootStore.segmentation.changeInstanceItemForInfoMap(sourceInstanceItem, targetInstanceItem);
            }
            // delete shape in current frame
            const shape = this.rootStore.shape.getShapeByInstanceItem(sourceInstanceItem, cameraName);
            if (shape) {
              this.rootStore.shape.deleteShape(shape);
            }
          }
        }
      }

      // should move frames to new instance item
      const movedFramesCount = movedFrameData.length;
      if (movedFramesCount > 0) {
        if (!instanceItemMovedTo) {
          const instanceMovedTo = this.createInstance(targetCategory);
          if (instanceMovedTo) {
            instanceItemMovedTo = this.createInstanceItem(instanceMovedTo, targetCategoryItem);
          }
        }
        if (instanceItemMovedTo) {
          instanceItemMovedTo.updateFramesFromData(cameraName, movedFrameData);

          if (targetRelationships) {
            const shouldMoveRelationship = movedFramesCount === targetFramesCount;
            for (let i = 0; i < targetRelationships.length; i += 1) {
              const r = targetRelationships[i];
              if (!prevRelationshipMap[r.id]) {
                prevRelationshipMap[r.id] = r.toJSON();
              }
              if (shouldMoveRelationship) {
                if (r.fromInstanceItem.id === targetInstanceItem.id) {
                  r.fromInstanceItem = instanceItemMovedTo!;
                } else {
                  r.toInstanceItem = instanceItemMovedTo!;
                }
                instanceItemMovedTo!.cameras[cameraName].relationships.push(r);
                currRelationshipMap[r.id] = r.toJSON();
              } else {
                r.deleteFromFrames(movedFrameData.map((f) => f.frameIndex));
                if (Object.values(r.frames).length > 0) {
                  currRelationshipMap[r.id] = r.toJSON();
                } else {
                  this.rootStore.relationship.deleteRelationship(r);
                }
              }
              updatedRelationships.push(r);
            }
          }
        }
      }

      const framesCount = framesData.length;
      if (framesCount > 0) {
        const { dynamicAttributes: sourceInstanceDynamicAttr } = sourceInstance;
        const sourceInstanceDynamicAttrByCamera = cloneDeep(sourceInstanceDynamicAttr?.[cameraName]);

        const framesIndex = framesData.map((f) => f.frameIndex);
        // remove frames from source instance item
        const sourceStat = sourceInstanceItem.remove(cameraName, framesIndex);
        updateInstanceMap(prevInstanceMap, sourceInstance, sourceStat.prevState, prevSourceInstanceBasicInfo);
        updateInstanceMap(currInstanceMap, sourceInstance, sourceStat.currState);
        // update frames for target instance item
        const targetStat = targetInstanceItem.updateFramesFromData(cameraName, framesData);
        if (!isTargetInstanceNewCreated) {
          updateInstanceMap(prevInstanceMap, targetInstance, targetStat.prevState, prevTargetInstanceBasicInfo);
        }
        updateInstanceMap(currInstanceMap, targetInstance, targetStat.currState);
        // update instance dynamic attributes
        const sourceFields = sourceInstance.categoryRef?.labelConfigDynamic?.fields || [];
        const targetFields = targetInstance.categoryRef?.labelConfigDynamic?.fields || [];
        const updatedFrames = Array.from(updatedFramesSet);
        const { dynamicAttributes: targetInstanceDynamicAttr } = targetInstance;
        const targetInstanceDynamicAttrByCamera = targetInstanceDynamicAttr?.[cameraName];

        const updatedDynamicAttrs = [];
        for (let index = 0; index < updatedFrames.length; index += 1) {
          const frameIndex = updatedFrames[index];
          if (targetCategoryItem === sourceInstanceItem.name && targetCategory === sourceInstance.category) {
            if (targetInstanceDynamicAttrByCamera) {
              if (!targetInstanceDynamicAttrByCamera[frameIndex]) {
                updatedDynamicAttrs.push({ frameIndex, attributes: sourceInstanceDynamicAttrByCamera?.[frameIndex]?.attributes });
              }
            } else {
              const updatedDynamicAttrbutes = updateAttributesByFieldsCompare(
                sourceFields,
                targetFields,
                sourceInstanceDynamicAttrByCamera?.[frameIndex]?.attributes,
                {}
              );
              updatedDynamicAttrs.push({ frameIndex, attributes: updatedDynamicAttrbutes });
            }
          } else if (
            (targetInstanceDynamicAttrByCamera && !targetInstanceDynamicAttrByCamera[frameIndex]) ||
            (!targetInstanceDynamicAttrByCamera)
          ) {
            const updatedDynamicAttrbutes = updateAttributesByFieldsCompare(
              sourceFields,
              targetFields,
              sourceInstanceDynamicAttrByCamera?.[frameIndex]?.attributes,
              {}
            );
            updatedDynamicAttrs.push({ frameIndex, attributes: updatedDynamicAttrbutes });
          }
        }
        targetInstance.setDynamicAttributesByCamera(cameraName, updatedDynamicAttrs);
        sourceInstance.deleteDynamicAttributesByCamera(cameraName, Array.from(updatedFramesSet), sourceInstanceItem.id);

        // update relationships
        if (sourceRelationships) {
          const shouldMoveRelationship = framesCount === sourceFramesCount;
          for (let i = 0; i < sourceRelationships.length; i += 1) {
            const r = sourceRelationships[i];
            if (!prevRelationshipMap[r.id]) {
              prevRelationshipMap[r.id] = r.toJSON();
            }
            if (shouldMoveRelationship) {
              if (r.fromInstanceItem.id === sourceInstanceItem.id && r.toInstanceItem.id !== targetInstanceItem.id) {
                r.fromInstanceItem = targetInstanceItem;
                targetInstanceItem.cameras[cameraName].relationships.push(r);
                currRelationshipMap[r.id] = r.toJSON();
              } else if (r.toInstanceItem.id === sourceInstanceItem.id && r.fromInstanceItem.id !== targetInstanceItem.id) {
                r.toInstanceItem = targetInstanceItem;
                targetInstanceItem.cameras[cameraName].relationships.push(r);
                currRelationshipMap[r.id] = r.toJSON();
              }
            } else {
              r.deleteFromFrames(framesIndex);
              if (Object.values(r.frames).length > 0) {
                currRelationshipMap[r.id] = r.toJSON();
              } else {
                this.rootStore.relationship.deleteRelationship(r);
              }
            }
            updatedRelationships.push(r);
          }
        }

        if (sourceInstanceItem.isEmpty) {
          this.deleteInstanceItem(sourceInstanceItem);
          currInstanceMap[sourceInstance.id].children = [];
        }
        if (sourceInstance.isEmpty) {
          this.deleteInstance(sourceInstance);
          delete currInstanceMap[sourceInstance.id];
        }
      }

      // setup shapes
      this.rootStore.shape.setupShape(targetInstanceItem, cameraName);
      if (instanceItemMovedTo) {
        this.rootStore.shape.setupShape(instanceItemMovedTo, cameraName);
      }
      // setup connections
      updatedRelationships.forEach((r) => {
        this.rootStore.relationship.deleteConnection(r);
        this.rootStore.relationship.createConnection(r);
      });
    }

    const prevInstances = Object.values(prevInstanceMap);
    const currInstances = Object.values(currInstanceMap);
    if (instanceItemMovedTo) {
      currInstances.push(instanceItemMovedTo.instance.toJSON());
    }
    if (affectedInstanceInGlobalMode) {
      prevInstances.push({
        ...affectedInstanceInGlobalMode.getBasicInfo(),
        children: [],
      });
      affectedInstanceInGlobalMode.number = this.getNextInstanceNumber(affectedInstanceInGlobalMode.category);
      currInstances.push({
        ...affectedInstanceInGlobalMode.getBasicInfo(),
        children: [],
      });
    }
    const prevRelationships = Object.values(prevRelationshipMap);
    const currRelationships = Object.values(currRelationshipMap);
    return {
      targetInstanceItem,
      instanceItemMovedTo,
      updatedFramesSet,
      prevState: {
        instances: prevInstances,
        relationships: prevRelationships,
      },
      currState: {
        instances: currInstances,
        relationships: currRelationships,
      },
    };
  }

  changeCategoryForInstance({ targetCategoryName, pendingInstanceItems }: {
    targetCategoryName: string;
    pendingInstanceItems: {
      [instanceItemId: string]: {
        sourceInstanceItem: InstanceItem;
        affectedCameraFrames: Record<string, number[]>;
      }
    }
  }) {
    const allItems = Object.values(pendingInstanceItems);
    if (allItems.length <= 0) {
      message.error(i18n.translate('FILTER_MOVE_TO_EMPTY'));
      return;
    }
    const prevInstanceMap: Record<string, IInstance> = {};
    const currInstanceMap: Record<string, IInstance> = {};
    const prevRelationshipMap: Record<string, IRelationship> = {};
    const currRelationshipMap: Record<string, IRelationship> = {};
    try {
      let targetInstance: Instance | undefined;

      for (let i = 0; i < allItems.length; i += 1) {
        const { sourceInstanceItem, affectedCameraFrames } = allItems[i];
        const { targetInstanceItem, prevState, currState } = this.changeCategoryForInstanceItemPure(
          sourceInstanceItem,
          targetCategoryName,
          sourceInstanceItem.name,
          affectedCameraFrames,
          targetInstance?.number,
        );
        targetInstance = targetInstanceItem.instance;

        // eslint-disable-next-line no-loop-func
        prevState.instances.forEach((instance) => {
          // target instance  instance
          // prev state  instance
          if (instance.id === targetInstance?.id) {
            return;
          }
          if (!prevInstanceMap[instance.id]) {
            prevInstanceMap[instance.id] = instance;
          } else {
            prevInstanceMap[instance.id].children.push(...instance.children);
          }
        });
        currState.instances.forEach((instance) => {
          if (!currInstanceMap[instance.id]) {
            currInstanceMap[instance.id] = instance;
          } else {
            currInstanceMap[instance.id].children.push(...instance.children);
          }
        });
        prevState.relationships.forEach((r) => {
          if (!prevRelationshipMap[r.id]) {
            prevRelationshipMap[r.id] = r;
          }
        });
        currState.relationships.forEach((r) => {
          currRelationshipMap[r.id] = r;
        });
      }

      this.rootStore.undo.push({
        instances: Object.values(prevInstanceMap),
        relationships: Object.values(prevRelationshipMap),
      }, {
        instances: Object.values(currInstanceMap),
        relationships: Object.values(currRelationshipMap),
      });
      this.rootStore.filter.filterInstances();
      // select
      this.selectInstance(targetInstance);
      // open attributes modal
      this.autoOpenAttributesModal(true);
    } catch (e) {
      message.error((e as Error).message);
    }
  }

  changeCategoryForInstanceItem(
    sourceInstanceItem: InstanceItem,
    targetCategory: string,
    targetCategoryItem: string,
    affectedCameraFrames: Record<string, number[]>,
    targetInstanceNum?: number,
  ) {
    try {
      const {
        targetInstanceItem,
        instanceItemMovedTo,
        updatedFramesSet,
        prevState,
        currState,
      } = this.changeCategoryForInstanceItemPure(
        sourceInstanceItem,
        targetCategory,
        targetCategoryItem,
        affectedCameraFrames,
        targetInstanceNum,
      );
      const {
        instances: prevInstances,
        relationships: prevRelationships,
      } = prevState;
      const {
        instances: currInstances,
        relationships: currRelationships,
      } = currState;
      if (prevInstances.length > 0 || currInstances.length > 0) {
        // save to undo/redo stack
        this.rootStore.undo.push({
          instances: prevInstances,
          relationships: prevRelationships,
        }, {
          instances: currInstances,
          relationships: currRelationships,
        });
        // filter
        this.rootStore.filter.filterInstances();
        // select
        this.selectInstanceItem(targetInstanceItem);
        // open attributes modal only when category changes
        if (targetCategory !== sourceInstanceItem.instance.category || targetCategoryItem !== sourceInstanceItem.name) {
          this.autoOpenAttributesModal(true);
        }
        message.success(i18n.translate('CHANGE_CATEGORY_SUCCESS', {
          values: {
            source: sourceInstanceItem.label,
            target: targetInstanceItem.label,
            frames: formatFrames(Array.from(updatedFramesSet)),
          },
        }));
        if (instanceItemMovedTo) {
          message.success(i18n.translate('CHANGE_CATEGORY_SUCCESS', {
            values: {
              source: targetInstanceItem.label,
              target: instanceItemMovedTo.label,
              frames: formatFrames(Object.keys(instanceItemMovedTo.frameStatus).map((f) => Number(f))),
            },
          }));
        }
      } else {
        message.warning(i18n.translate('CHANGE_CATEGORY_WARNING'));
      }
    } catch (e) {
      message.error((e as Error).message);
    }
  }

  /**
   * change category for instance items
   * @param selectedInstanceInfo
   * @param targetCategory
   * @param targetCategoryItem
   * @param attrs
  */
  changeCategoryForInstanceItems(
    selectedInstanceInfo: {
      id: string; instanceId: string; instanceItemId?: string; camera?: string; frames: number[]; order: number;
    }[],
    targetCategory: string,
    targetCategoryItem: string,
    attrs?: {
      static?: any;
      dynamic?: any;
      item?: any;
    } | undefined
  ) {
    const prevInstanceMap: Record<string, IInstance> = {};
    const currInstanceMap: Record<string, IInstance> = {};
    const getInstancesState = (instance: Instance, instanceItem: InstanceItem, camera: string) => {
      const newInstance: IInstance = {
        ...instance.getBasicInfo(),
        children: [],
      };
      const cameraData = instanceItem.cameras[camera];
      if (cameraData && !cameraData.isEmpty) {
        newInstance.children.push({
          ...instanceItem.getBasicInfo(),
          cameras: [cameraData.toJSON()],
        });
      }

      return newInstance;
    };
    const selectInstanceInfoMap = new Map();

    selectedInstanceInfo?.forEach((instanceInfo: {
      id: string; instanceId: string; instanceItemId?: string; camera?: string; frames: number[]; order: number;
    }) => {
      // delete old and add attributes
      const { instanceId, instanceItemId, camera, frames } = instanceInfo;
      if (!camera) {
        return;
      }
      const sourceInstance = this.getInstanceById(instanceId);
      let sourceInstanceItem: InstanceItem | null = null;
      if (sourceInstance && instanceItemId) {
        sourceInstanceItem = sourceInstance.items[instanceItemId];
      }
      if (!sourceInstanceItem) {
        return;
      }
      // if selected category is same with instance's current ,then return
      if (sourceInstance.categoryRef.className === targetCategory && sourceInstanceItem.categoryItemRef.name === targetCategoryItem) {
        return;
      }
      const key = `${instanceId}-${instanceItemId}`;
      let targetInstance: Instance | undefined;
      let targetInstanceItem: InstanceItem | undefined;
      if (selectInstanceInfoMap.has(key)) {
        const { targetInstanceId, targetInstanceItemId } = selectInstanceInfoMap.get(key);
        targetInstance = this.getInstanceById(targetInstanceId);
        if (targetInstance && targetInstanceItemId) {
          targetInstanceItem = targetInstance.items[targetInstanceItemId];
        }
      } else {
        // create new
        try {
          targetInstance = this.createInstance(targetCategory);
          if (targetInstance) {
            targetInstanceItem = this.createInstanceItem(targetInstance, targetCategoryItem);
          }
        } catch (e) {
          message.error((e as Error).message);
          return;
        }
        if (!targetInstance || !targetInstanceItem) {
          return;
        }
        selectInstanceInfoMap.set(key, { targetInstanceId: targetInstance.id, targetInstanceItemId: targetInstanceItem.id });
      }
      if (!targetInstance || !targetInstanceItem) {
        return;
      }
      if (!prevInstanceMap[sourceInstance.id]) {
        prevInstanceMap[sourceInstance.id] = getInstancesState(sourceInstance, sourceInstanceItem, camera);
      }
      // delete attributes  for source instance's item by frame
      sourceInstance.deleteDynamicAttributesByCamera(camera, frames, sourceInstanceItem.id);
      const { currentFrame } = this.rootStore.frame;

      frames.forEach((frame) => {
        // frames data to target instance item
        const sourceFrameData = sourceInstanceItem!.cameras[camera]?.frames[frame];
        if (!sourceFrameData) {
          return;
        }
        const frameDataJson = sourceFrameData.toJSON();
        targetInstanceItem!.updateFramesFromData(camera, [frameDataJson]);

        if (frame === currentFrame) {
          // change instance item for pixel shape
          if (sourceFrameData.shapeType === ShapeType.PIXEL) {
            this.rootStore.segmentation.changeInstanceItemForInfoMap(sourceInstanceItem!, targetInstanceItem!);
          }
          // delete shape in current frame
          const shape = this.rootStore.shape.getShapeByInstanceItem(sourceInstanceItem!, camera);
          if (shape) {
            this.rootStore.shape.deleteShape(shape);
          }
        }

        const { currState } = this.deleteInstanceItemByFrame(sourceInstanceItem!, frame, camera);
        currInstanceMap[sourceInstance.id] = currState ? currState.instances[0] : { ...sourceInstanceItem!.instance.getBasicInfo(), children: [] };
      });

      // set attributes for target instance and instance item
      if (attrs) {
        const { static: newInstanceAttr, dynamic: newInstanceDynamicAttr, item: newInstanceItemAttr } = attrs;
        if (newInstanceAttr) {
          targetInstance?.setAttributes(newInstanceAttr);
        }
        const updatedFrameAttributes: DynamicAttributes[] = [];
        frames.forEach((frame) => {
          if (newInstanceItemAttr) {
            targetInstanceItem!.setAttributesByFrame(camera, { [frame]: { attributes: newInstanceItemAttr } });
          }
          if (newInstanceDynamicAttr) {
            updatedFrameAttributes.push({
              frameIndex: frame,
              attributes: newInstanceDynamicAttr
            });
          }
        });

        targetInstance?.setDynamicAttributesByCamera(camera, updatedFrameAttributes);
      }

      // setup shapes
      this.rootStore.shape.setupShape(targetInstanceItem, camera);
      currInstanceMap[targetInstance.id] = getInstancesState(targetInstanceItem.instance, targetInstanceItem, camera);
    });

    const prevInstances = Object.values(prevInstanceMap);
    const currInstances = Object.values(currInstanceMap);
    // filter
    this.rootStore.filter.filterInstances();
    if (prevInstances.length > 0 || currInstances.length > 0) {
      // save to undo/redo stack
      this.rootStore.undo.push({
        instances: prevInstances,
      }, {
        instances: currInstances,
      });

      message.success(i18n.translate('CHANGE_CATEGORY_BATCH_SUCCESS'));
    } else {
      message.warning(i18n.translate('CHANGE_CATEGORY_WARNING'));
    }
  }

  /**
   * change layer for selected instance item
   * @param targetLayer
   */
  changeLayer(targetLayer: number) {
    if (this.isSingleSelected) {
      const selectedInstanceItem = this.selectedInstanceItems[0];
      const { currentCamera, currentFrame } = this.rootStore.frame;
      const frameData = selectedInstanceItem.cameras[currentCamera]?.frames[currentFrame];
      if (!frameData?.locked && frameData?.layer !== targetLayer) {
        const prevInstanceMap: Record<string, IInstance> = {};
        const currInstanceMap: Record<string, IInstance> = {};
        let prevRelationships: IRelationship[] | undefined;
        let currRelationships: IRelationship[] | undefined;

        // update layer
        if (frameData.shapeType === ShapeType.PIXEL) {
          // for pixel shape, layer change may affect self or others
          const { instanceState, relationshipState } = this.rootStore.segmentation.changeLayer(selectedInstanceItem, frameData.layer, targetLayer);
          Object.keys(instanceState).forEach((id) => {
            const { prev, curr } = instanceState[id];
            if (!prevInstanceMap[id]) {
              prevInstanceMap[id] = prev;
            }
            currInstanceMap[id] = curr;
          });
          prevRelationships = relationshipState.prev;
          currRelationships = relationshipState.curr;
        } else {
          // not pixel shape, only need to update layer & order
          const instanceBasicInfo = selectedInstanceItem.instance.getBasicInfo();
          const itemBasicInfo = selectedInstanceItem.getBasicInfo();
          prevInstanceMap[instanceBasicInfo.id] = {
            ...instanceBasicInfo,
            children: [{
              ...itemBasicInfo,
              cameras: [{
                camera: currentCamera,
                frames: [frameData.toJSON()],
              }]
            }]
          };
          frameData.layer = targetLayer;
          frameData.order = this.rootStore.frame.getNextShapeOrder(targetLayer, currentFrame, currentCamera);
          currInstanceMap[instanceBasicInfo.id] = {
            ...instanceBasicInfo,
            children: [{
              ...itemBasicInfo,
              cameras: [{
                camera: currentCamera,
                frames: [frameData.toJSON()],
              }],
            }]
          };
        }

        this.rootStore.undo.push({
          instances: Object.values(prevInstanceMap),
          relationships: prevRelationships,
        }, {
          instances: Object.values(currInstanceMap),
          relationships: currRelationships,
        });

        this.rootStore.config.setContextMenuVisible(false);
        this.rootStore.shape.setupShape(selectedInstanceItem, currentCamera);
        if (!selectedInstanceItem.cameras[currentCamera]) {
          this.selectInstanceItem(null);
          this.selectInstance(selectedInstanceItem.instance.isEmpty ? null : selectedInstanceItem.instance);
          this.rootStore.config.setActiveLayerIndex(targetLayer);
        } else {
          this.selectInstanceItem(selectedInstanceItem);
        }
        message.success(i18n.translate('LAYER_CHANGED_SUCCESS', {
          values: {
            label: selectedInstanceItem.label,
            layer: this.rootStore.config.layers[targetLayer],
          },
        }));
      }
    }
  }

  /**
   * open attributes modal automatically for selected instance
   * @param ignoreAttributes ignore whether if frameData's attributes existed or not
   */
  autoOpenAttributesModal(ignoreAttributes = false) {
    if (
      !this.rootStore.config.preferences.autoOpenAttributesModal ||
      this.isMultiSelected ||
      this.selectedInstances.length <= 0
    ) {
      return false;
    }

    const { currentCamera, currentFrame } = this.rootStore.frame;
    const shouldOpen = (instanceItem: InstanceItem) => {
      const frameData = instanceItem.cameras[currentCamera]?.frames[currentFrame];

      if (frameData?.isOCR && !frameData?.OCRText) {
        this.rootStore.config.shouldSyncOCR = true;
        return true;
      }

      if (frameData?.isFormula && !frameData?.formulaText) {
        this.rootStore.config.shouldSyncOCR = true;
        return true;
      }

      if (
        instanceItem.categoryItemRef.labelConfig &&
        (ignoreAttributes || !frameData?.attributes)
      ) {
        this.rootStore.config.setAttributesModalVisible(true);
        return true;
      }

      return false;
    };

    if (this.selectedInstanceItems.length === 1) {
      const selectedInstanceItem = this.selectedInstanceItems[0];
      const open = shouldOpen(selectedInstanceItem);
      if (open) {
        this.rootStore.config.setAttributesModalVisible(true);
        return true;
      }
    }

    const selectedInstance = this.selectedInstances[0];
    const { labelConfig, labelConfigDynamic } = selectedInstance.categoryRef;
    if (
      (labelConfig && (ignoreAttributes || !selectedInstance.attributes)) ||
      (labelConfigDynamic && (ignoreAttributes || !selectedInstance.dynamicAttributes || !selectedInstance.dynamicAttributes[currentCamera]?.[currentFrame]?.attributes))
    ) {
      this.rootStore.config.setAttributesModalVisible(true);
      return true;
    }

    if (this.selectedInstanceItems.length <= 0 && Object.values(selectedInstance.items).some((i) => shouldOpen(i))) {
      // no instance item selected
      this.rootStore.config.setAttributesModalVisible(true);
      return true;
    }

    return false;
  }

  /**
   * move to next pending instance item & open attributes modal if needed
   */
  openAttributesModalFromPending() {
    const item = this.attrPendingInstanceItems.shift();
    if (item) {
      this.selectInstanceItem(item);
      const open = this.autoOpenAttributesModal();
      if (open) {
        // async to set last pending instance item
        setTimeout(() => {
          this.lastPendingInstanceItem = item;
        }, 0);
        // focus
        if (this.rootStore.config.viewMode === ViewMode.DEFAULT) {
          this.rootStore.shape.fitShapes();
        } else {
          // this.rootStore.grid.gridCanvas?.fitShapes();
        }
      } else {
        // move to next
        this.openAttributesModalFromPending();
      }
    } else {
      // no any pending instance item
      this.lastPendingInstanceItem = null;
    }
  }

  /**
   * open attributes modal for instance items one by one
   * @param instanceItems
   */
  openAttributesModalForInstanceItems(instanceItems: InstanceItem[]) {
    this.attrPendingInstanceItems = instanceItems;
    this.lastPendingInstanceItem = null;
    this.openAttributesModalFromPending();
  }

  /**
   * set dragging type by draggable id
   * @param draggableId
   */
  setDraggingType(draggableId: string) {
    const [type] = draggableId.split('_');
    this.draggingType = type as DraggableType;
  }

  /**
   * update instances or instance items by dragging
   * @param destination
   */
  updateByDragging(destination?: { droppableId: string; index: number }) {
    if (
      !destination ||
      !this.rootStore.config.autoTracking ||
      (this.rootStore.config.autoTracking && !this.rootStore.config.inexistentGraphicsCardVisibility)
    ) {
      return;
    }

    const { droppableId, index } = destination;

    // ========== drag instance ==========
    // reorder all instances number in the category
    // just update instances number to 1, 2, 3, ...
    if (this.draggingType === DraggableType.INSTANCE) {
      const category = droppableId;
      const selectedInstance = this.selectedInstances[0];
      if (!selectedInstance || selectedInstance.category !== category) {
        return;
      }

      const categoryInstances = [...this.categoryInstancesMap[category]];
      const originIndex = categoryInstances.indexOf(selectedInstance);
      if (originIndex === index) {
        // same order, no need to udpate
        return;
      }

      // reorder
      categoryInstances.splice(originIndex, 1);
      categoryInstances.splice(index, 0, selectedInstance);

      const prevInstances: IInstance[] = [];
      const currInstances: IInstance[] = [];
      categoryInstances.forEach((instance, i) => {
        const number = i + 1;
        if (instance.number !== number) {
          // update number
          const basicInfo = instance.getBasicInfo();
          instance.number = number;
          prevInstances.push({ ...basicInfo, children: [] });
          currInstances.push({ ...basicInfo, children: [], number });
          // update shapes in the instance
          Object.values(instance.items).forEach((item) => {
            Object.values(item.cameras).forEach((cameraData) => {
              this.rootStore.shape.setupShape(item, cameraData.camera);
            });
          });
        }
      });
      this.rootStore.undo.push({ instances: prevInstances }, { instances: currInstances });

      // reselect
      this.selectInstance(selectedInstance);
    }

    // ========== drag instance item in one camera ==========
    // reorder all instance items (same name) number
    // should move camera data from source to target (not just update number), keep other camera data not change
    if (this.draggingType === DraggableType.ITEM) {
      const [targetId, targetCamera] = droppableId.split('|DS|');
      const camera = targetCamera || this.rootStore.frame.currentCamera;
      const targetInstance = this.instances[targetId];
      const selectedInstanceItem = this.selectedInstanceItems[0];
      if (!targetInstance || !selectedInstanceItem) {
        return;
      }

      const { name: selectedName, instance: selectedInstance, categoryItemRef } = selectedInstanceItem;
      const selectedNameItemsCount = targetInstance.getItemsByName(selectedName).filter((item) => !item.cameras[camera]?.isEmpty).length;
      if (selectedInstance.id !== targetId && (selectedNameItemsCount === categoryItemRef.count || selectedNameItemsCount === categoryItemRef.maxCount)) {
        // cannot dragged any instance item in because of count limits
        return;
      }

      const sameInstance = selectedInstance.id === targetId;
      const getInstancesState = (instances: Instance[]) => instances.map((instance) => {
        const newInstance: IInstance = {
          ...instance.getBasicInfo(),
          children: [],
        };
        instance.getItemsByName(selectedName).forEach((item) => {
          const cameraData = item.cameras[camera];
          if (cameraData && !cameraData.isEmpty) {
            newInstance.children.push({
              ...item.getBasicInfo(),
              cameras: [cameraData.toJSON()],
            });
          }
        });
        return newInstance;
      });
      const prevInstances = getInstancesState([
        targetInstance,
        ...!sameInstance ? [selectedInstance] : [],
      ]);
      const prevRelationships: { [id: string]: IRelationship } = {};
      const currRelationships: { [id: string]: IRelationship } = {};

      // current selected number (updated when new number given)
      let selectedNumber = selectedInstanceItem.number;
      // update camera data in instance item
      const updateCameraData = (number: number, cameraData: CameraData, relationships: Relationship[], originInstanceItem: InstanceItem) => {
        let targetInstanceItem = targetInstance.getItemsByName(selectedName).find((item) => item.number === number); // existed instance item with the number
        if (!targetInstanceItem) {
          // should create
          const newInstanceItem: IInstanceItem = {
            id: uuidv4(),
            name: selectedName,
            number,
            cameras: [],
          };
          targetInstance.createItemFromData(newInstanceItem);
          targetInstanceItem = targetInstance.items[newInstanceItem.id];
        }
        targetInstanceItem.createCameraFromData(cameraData);
        this.rootStore.shape.setupShape(targetInstanceItem, camera);

        // update relationships related
        const { deleteConnection, createConnection } = this.rootStore.relationship;
        const targetCameraData = targetInstanceItem.cameras[camera];
        for (let i = 0; i < relationships.length; i += 1) {
          const relationship = relationships[i];
          if (!prevRelationships[relationship.id]) {
            prevRelationships[relationship.id] = relationship.toJSON();
          }
          deleteConnection(relationship);
          if (originInstanceItem === relationship.fromInstanceItem) {
            relationship.fromInstanceItem = targetInstanceItem;
          } else if (originInstanceItem === relationship.toInstanceItem) {
            relationship.toInstanceItem = targetInstanceItem;
          }
          targetCameraData.addRelationship(relationship);
          createConnection(relationship);
          currRelationships[relationship.id] = relationship.toJSON();
        }
      };

      if (targetCamera !== undefined) {
        // drag to existing camera
        const targetInstanceItems = Object.values(targetInstance.items)
          .filter((item) => !item.cameras[camera]?.isEmpty)
          .sort((a, b) => {
            const { children } = targetInstance.categoryRef;
            const aIndex = children.findIndex((c) => c.name === a.name);
            const bIndex = children.findIndex((c) => c.name === b.name);
            if (aIndex < bIndex) {
              return -1;
            }
            if (aIndex > bIndex) {
              return 1;
            }
            return a.number - b.number;
          });

        const selectedNameFirstItemIndex = targetInstanceItems.findIndex((item) => item.name === selectedName);
        const selectedNameLastItemIndex = selectedNameFirstItemIndex + selectedNameItemsCount - (sameInstance ? 1 : 0);
        const targetCameraDataList = targetInstanceItems.slice(selectedNameFirstItemIndex, selectedNameLastItemIndex + 1).map((item) => ({
          id: item.id,
          cameraData: item.cameras[camera].toJSON(),
          relationships: [...item.cameras[camera].relationships],
        }));

        const exactIndex = Math.min(Math.max(index, selectedNameFirstItemIndex), selectedNameLastItemIndex) - selectedNameFirstItemIndex;
        if (sameInstance) {
          // dragging in the same instance
          const originIndex = targetInstanceItems.indexOf(selectedInstanceItem) - selectedNameFirstItemIndex;
          if (originIndex === exactIndex) {
            // same order in the same instance, no need to update
            return;
          }
          targetCameraDataList.splice(originIndex, 1);
        }

        targetCameraDataList.splice(exactIndex, 0, {
          id: selectedInstanceItem.id,
          cameraData: selectedInstanceItem.cameras[camera].toJSON(),
          relationships: [...selectedInstanceItem.cameras[camera].relationships],
        });

        targetCameraDataList.forEach((d, i) => {
          const number = i + 1;
          if (d.id === selectedInstanceItem.id) {
            selectedNumber = number; // update selected number
          }

          const originTargetInstanceItem = targetInstance.items[d.id];
          if (!originTargetInstanceItem || (originTargetInstanceItem.name === selectedName && originTargetInstanceItem.number !== number)) {
            // should create new, or should update camera data because number changed
            updateCameraData(number, d.cameraData, d.relationships, originTargetInstanceItem || selectedInstanceItem);
          }
        });

        targetInstance.getItemsByName(selectedName).forEach((item) => {
          if (item.number > targetCameraDataList.length) {
            delete item.cameras[camera];
            if (item.isEmpty) {
              this.deleteInstanceItem(item);
            }
          }
        });
      } else if (!sameInstance) {
        // append to target instance, no instance item in current camera
        selectedNumber = this.getNextInstanceItemNumber(targetInstance.id, selectedName, camera);
        const cameraData = selectedInstanceItem.cameras[camera];
        updateCameraData(selectedNumber, cameraData.toJSON(), [...cameraData.relationships], selectedInstanceItem);
      }

      if (!sameInstance) {
        // delete dragged item
        const shape = this.rootStore.shape.getShapeByInstanceItem(selectedInstanceItem, camera);
        if (shape) {
          this.rootStore.shape.deleteShape(shape);
        }
        delete selectedInstanceItem.cameras[camera];
        if (selectedInstanceItem.isEmpty) {
          this.deleteInstanceItem(selectedInstanceItem);
        }
        if (selectedInstance.isEmpty) {
          this.deleteInstance(selectedInstance);
        }
        // update instance dynamic attributes
        const { currentFrame } = this.rootStore.frame;
        if (targetInstance.dynamicAttributes?.[camera]?.[currentFrame]) {
          selectedInstance.setDynamicAttributesByCamera(camera, [targetInstance?.dynamicAttributes?.[camera]?.[currentFrame]]);
        } else if (!targetInstance?.dynamicAttributes?.[camera]?.[currentFrame] && selectedInstance.dynamicAttributes?.[camera]?.[currentFrame]) {
          targetInstance.setDynamicAttributesByCamera(camera, [selectedInstance.dynamicAttributes?.[camera]?.[currentFrame]]);
        }
        if (selectedInstance?.dynamicAttributes?.[camera]?.[currentFrame]) {
          selectedInstance.deleteDynamicAttributesByCamera(camera, [currentFrame], selectedInstanceItem.id);
        }
      }

      const currInstances = getInstancesState([
        targetInstance,
        ...!sameInstance && !selectedInstance.isEmpty ? [selectedInstance] : [],
      ]);
      this.rootStore.undo.push({
        instances: prevInstances,
        relationships: Object.values(prevRelationships),
      }, {
        instances: currInstances,
        relationships: Object.values(currRelationships),
      });

      // reselect
      const currentSelectedInstanceItem = targetInstance.getItemsByName(selectedName).find((item) => item.number === selectedNumber);
      this.selectInstanceItem(currentSelectedInstanceItem);
    }
  }

  /**
   * get instances json data (for save)
   * @param options
   */
  instancesJSON(options?: {
    minify?: boolean;
    noLock?: boolean;
  }): IInstance[] {
    return Object.values(this.instances).map((instance) => instance.toJSON(options));
  }

  /**
   * is current frame attributes
   * @getter
   */
  getCurrentDynamicAttributesByInstance(instance: Instance) {
    const { currentCamera, currentFrame } = this.rootStore.frame;
    const { attributes = {} } = instance.dynamicAttributes?.[currentCamera]?.[currentFrame] || {};
    return attributes;
  }

  /**
   * switch between instances or instance items
   */
  switch() {
    // default switch between instances
    let searchClassName = '.sidebar-category-instance__label';
    const selectedInstanceItem = this.selectedInstanceItems[0];
    if (selectedInstanceItem && !selectedInstanceItem.instance.isSingle) {
      // item in multiple instance selected, switch between instance items
      searchClassName = '.sidebar-category-instance__item, .sidebar-category-instance__label.single';
    }
    const nodes = Array.from(document.querySelectorAll(searchClassName));
    const selectedIndex = nodes.findIndex((i) => i.classList.contains('selected'));
    const nextIndex = selectedIndex === nodes.length - 1 ? 0 : selectedIndex + 1;
    const nextNodes = nodes.slice(nextIndex).filter((i) => !i.classList.contains('hidden'));
    const nextNode = nextNodes.length > 0 ? nextNodes[0] : nodes.find((i) => !i.classList.contains('hidden'));
    if (nextNode) {
      ['mousedown', 'mouseup'].forEach((eventName) => {
        nextNode.dispatchEvent(new MouseEvent(eventName, {
          view: window,
          bubbles: true,
          cancelable: true,
          buttons: 1,
        }));
      });
    }
  }

  /**
   * set locked for all category shapes in current frame
   * @param category
   * @param locked
   */
  setLockedForCategory(category: string, locked: boolean) {
    const { updateShapeLockedState } = this.rootStore.shape;
    const { currentFrame } = this.rootStore.frame;
    const prevInstances: IInstance[] = [];
    const currInstances: IInstance[] = [];
    const allInstances = Object.values(this.instances);
    for (let i = 0; i < allInstances.length; i += 1) {
      const instance = allInstances[i];
      if (instance.category === category && instance.frameStatus[currentFrame] !== undefined) {
        const basicInfo = instance.getBasicInfo();
        const prevItems: IInstanceItem[] = [];
        const currItems: IInstanceItem[] = [];
        const items = Object.values(instance.items);
        for (let j = 0; j < items.length; j += 1) {
          const item = items[j];
          const { prevState, currState } = item.setLockedForAllCameras(currentFrame, locked);
          if (prevState) {
            prevItems.push(prevState);
          }
          if (currState) {
            currItems.push(currState);
            currState.cameras.forEach(({ camera }) => {
              updateShapeLockedState(item, camera, locked);
            });
          }
        }
        if (prevItems.length > 0) {
          prevInstances.push({ ...basicInfo, children: prevItems });
        }
        if (currItems.length > 0) {
          currInstances.push({ ...basicInfo, children: currItems });
        }
      }
    }
    this.rootStore.undo.push({
      instances: prevInstances,
    }, {
      instances: currInstances,
    });
  }

  /**
   * is all layer shapes locked in current frame
   * @param camera
   * @param layer
   */
  isLayerLocked(camera: string, layer: number) {
    const { currentFrame } = this.rootStore.frame;
    const { activeLayerIndex } = this.rootStore.config;
    let locked = true;
    let currentEmpty = true;
    const allInstances = Object.values(this.instances);
    for (let i = 0; i < allInstances.length; i += 1) {
      const instance = allInstances[i];
      const items = Object.values(instance.items);
      for (let j = 0; j < items.length; j += 1) {
        const item = items[j];
        const frameData = item.cameras[camera]?.frames[currentFrame];
        if (frameData && frameData.matrixChangeAllowed) {
          const { layer: l = activeLayerIndex } = frameData;
          if (l === layer) {
            currentEmpty = false;
            if (!frameData.locked) {
              locked = false;
            }
          }
        }
        if (!locked && !currentEmpty) {
          break;
        }
      }
      if (!locked && !currentEmpty) {
        break;
      }
    }
    return {
      locked,
      currentEmpty,
    };
  }

  /**
   * set locked for all layer shapes in current frame
   * @param camera
   * @param layer
   * @param locked
   */
  setLockedForLayer(camera: string, layer: number, locked: boolean) {
    const { updateShapeLockedState } = this.rootStore.shape;
    const { currentFrame } = this.rootStore.frame;
    const { activeLayerIndex } = this.rootStore.config;
    const prevInstances: IInstance[] = [];
    const currInstances: IInstance[] = [];
    const allInstances = Object.values(this.instances);
    for (let i = 0; i < allInstances.length; i += 1) {
      const instance = allInstances[i];
      const basicInfo = instance.getBasicInfo();
      const prevItems: IInstanceItem[] = [];
      const currItems: IInstanceItem[] = [];
      const items = Object.values(instance.items);
      for (let j = 0; j < items.length; j += 1) {
        const item = items[j];
        const frameData = item.cameras[camera]?.frames[currentFrame];
        if (frameData) {
          const { layer: l = activeLayerIndex } = frameData;
          if (l === layer) {
            const { prevState, currState } = item.setLocked(camera, currentFrame, locked);
            if (prevState) {
              prevItems.push(prevState);
            }
            if (currState) {
              currItems.push(currState);
            }
            updateShapeLockedState(item, camera, locked);
          }
        }
      }
      if (prevItems.length > 0) {
        prevInstances.push({ ...basicInfo, children: prevItems });
      }
      if (currItems.length > 0) {
        currInstances.push({ ...basicInfo, children: currItems });
      }
    }
    this.rootStore.undo.push({
      instances: prevInstances,
    }, {
      instances: currInstances,
    });
  }

  /**
   * lock or unlock all shapes in current frame
   */
  setAllLocked(locked: boolean) {
    const { updateShapeLockedState } = this.rootStore.shape;
    const { currentFrame } = this.rootStore.frame;
    const prevInstances: IInstance[] = [];
    const currInstances: IInstance[] = [];
    const allInstances = Object.values(this.instances);
    for (let i = 0; i < allInstances.length; i += 1) {
      const instance = allInstances[i];
      if (instance.frameStatus[currentFrame] !== undefined) {
        const basicInfo = instance.getBasicInfo();
        const prevItems: IInstanceItem[] = [];
        const currItems: IInstanceItem[] = [];
        const items = Object.values(instance.items);
        for (let j = 0; j < items.length; j += 1) {
          const item = items[j];
          const { prevState, currState } = item.setLockedForAllCameras(currentFrame, locked);
          if (prevState) {
            prevItems.push(prevState);
          }
          if (currState) {
            currItems.push(currState);
            currState.cameras.forEach(({ camera }) => {
              updateShapeLockedState(item, camera, locked);
            });
          }
        }
        if (prevItems.length > 0) {
          prevInstances.push({ ...basicInfo, children: prevItems });
        }
        if (currItems.length > 0) {
          currInstances.push({ ...basicInfo, children: currItems });
        }
      }
    }
    this.rootStore.undo.push({
      instances: prevInstances,
    }, {
      instances: currInstances,
    });
  }

  /**
   * toggle locked status for selected items
   */
  toggleLockedForSelectedItems() {
    const { updateShapeLockedState } = this.rootStore.shape;
    const { currentFrame, currentCamera } = this.rootStore.frame;
    const selectedItems = this.selectedInstanceItems.length > 0
      ? this.selectedInstanceItems
      : this.selectedInstances.map((i) => Object.values(i.items)).flat();
    const items = selectedItems.filter((item) => item.cameras[currentCamera]?.frames[currentFrame]?.matrixChangeAllowed);
    if (items.length > 0) {
      const currAllLocked = items.every((item) => item.frameLocked[currentFrame]);
      const prevInstances: Record<string, IInstance> = {};
      const currInstances: Record<string, IInstance> = {};
      for (let i = 0; i < items.length; i += 1) {
        const item = items[i];
        const basicInfo = item.instance.getBasicInfo();
        const { prevState, currState } = item.setLocked(currentCamera, currentFrame, !currAllLocked);
        if (prevState) {
          if (!prevInstances[item.instance.id]) {
            prevInstances[item.instance.id] = { ...basicInfo, children: [] };
          }
          prevInstances[item.instance.id].children.push(prevState);
        }
        if (currState) {
          if (!currInstances[item.instance.id]) {
            currInstances[item.instance.id] = { ...basicInfo, children: [] };
          }
          currInstances[item.instance.id].children.push(currState);
          currState.cameras.forEach(({ camera }) => {
            updateShapeLockedState(item, camera, !currAllLocked);
          });
        }
      }
      this.rootStore.undo.push({
        instances: Object.values(prevInstances),
      }, {
        instances: Object.values(currInstances),
      });
    }
  }

  /**
   * get interpolator worker, create it if has not been initialized
   */
  getInterpolator() {
    if (!this.interpolator) {
      this.interpolator = new InterpolationWorker();
      this.interpolator?.addEventListener('message', (e) => {
        const { type, data } = e.data;

        if (type === 'interpolated') {
          const timestamp = Date.now();
          const { camera, frames } = data as InterpolationResult;
          frames.forEach(({ frameIndex, shapes }) => {
            shapes.forEach((shape) => {
              const { instanceItemId } = shape;
              const dataKey = `${frameIndex}-${camera}`;
              const shapeKey = `${instanceItemId}-${camera}`;

              if (!this.interpolatedShapes[dataKey]) {
                this.interpolatedShapes[dataKey] = {
                  timestamp,
                  frameIndex,
                  camera,
                  shapes: {},
                };
              } else {
                this.interpolatedShapes[dataKey].timestamp = timestamp;
              }
              this.interpolatedShapes[dataKey].shapes[shapeKey] = shape;
            });
          });
          this.resolveInterpolatedShapes();
        }

        if (type === 'pixel-resolved') {
          const instanceItems: {
            instanceItem: InstanceItem;
            camera: string;
            frameIndex: number;
            shapeType: ShapeType;
            shape?: ShapeData;
            layer: number;
            order: number;
          }[] = [];

          const { camera, frameIndex, shapes } = data as ShapeResolutionResult;
          for (let i = 0; i < shapes.length; i += 1) {
            const { shapeType, shape, layer, instanceId, instanceItemId } = shapes[i];
            const instanceItem = this.instances[instanceId]?.items[instanceItemId];
            if (instanceItem) {
              instanceItems.push({
                instanceItem,
                camera,
                frameIndex,
                shapeType,
                shape,
                layer,
                order: 0,
              });
            }
          }

          if (instanceItems.length > 0) {
            this.updateFrameShapeForInstanceItems(instanceItems);
            if (frameIndex === this.rootStore.frame.currentFrame) {
              instanceItems.forEach(({ instanceItem }) => {
                this.rootStore.shape.setupShape(instanceItem, camera);
              });
            }
            this.interpolatedFrames.add(frameIndex);
            this.showInterpolatedSuccessMessage();
          }
          this.resolveInterpolatedShapes();
        }
      });
    }
    return this.interpolator;
  }

  /**
   * interpolate frames by base frame
   * @param baseFrame
   * @param frames
   * @param camera
   * @param instanceItems
   */
  interpolate(
    baseFrame: number,
    frames: number[],
    camera = this.rootStore.frame.currentCamera,
    instanceItems?: InstanceItem[],
  ) {
    const { niftiParser, attributes, getReadableImage } = this.rootStore.frame;
    const data: InterpolationData = {
      interpolateUrl: `${this.rootStore.config.interpolateProxy}/interpolate`,
      baseUrl: getReadableImage(camera, baseFrame),
      baseSize: [attributes[camera][baseFrame]?.imageWidth ?? 0, attributes[camera][baseFrame]?.imageHeight ?? 0],
      baseRotation: attributes[camera][baseFrame]?.rotation ?? 0,
      baseShapes: [],
      camera,
      frames: frames.map((frameIndex) => ({
        frameIndex,
        url: getReadableImage(camera, frameIndex),
      })),
      isBase64: !!niftiParser,
    };

    const items = instanceItems ?? Object.values(this.instances).map((instance) => Object.values(instance.items)).flat();
    for (let i = 0; i < items.length; i += 1) {
      const instanceItem = items[i];
      const cameraData = instanceItem.cameras[camera];
      if (cameraData) {
        const frameData = cameraData.frames[baseFrame];
        if (interpolationSupportedShapeTypes.includes(frameData?.shapeType)) {
          data.baseShapes.push({
            shapeType: frameData.shapeType,
            shape: frameData.shape,
            layer: frameData.layer,
            order: frameData.order,
            instanceId: instanceItem.instance.id,
            instanceItemId: instanceItem.id,
          });
        }
      }
    }

    if (data.baseShapes.length > 0) {
      this.getInterpolator()?.postMessage({
        type: 'interpolate',
        data,
      });
      message.info(i18n.translate('INTERPOLATION_FRAMES_INFO', { values: { frames: formatFrames(frames) } }));
    } else {
      message.warn(i18n.translate('INTERPOLATION_FRAMES_WARN'));
    }
  }

  /**
   * resolve interpolated shapes, including:
   * 1) resolve pixel shapes overlaps
   */
  resolveInterpolatedShapes() {
    const sorted = Object.values(this.interpolatedShapes).sort((a, b) => a.timestamp - b.timestamp);

    const oldest = sorted[0];
    if (oldest !== undefined) {
      const { frameIndex, camera, shapes } = oldest;
      const dataKey = `${frameIndex}-${camera}`;
      delete this.interpolatedShapes[dataKey];
      const rotation = this.rootStore.frame.getFrameRotationForCamera(camera, frameIndex);
      const data: ShapeResolutionData = {
        camera,
        frameIndex,
        rotation,
        shapes: Object.values(shapes),
        originPixelShapes: [],
        pixelLocked: this.rootStore.segmentation.locked,
      };
      Object.values(this.instances).forEach((instance) => {
        Object.values(instance.items).forEach((instanceItem) => {
          const frameData = instanceItem.cameras[camera]?.frames[frameIndex];
          if (frameData?.shapeType === ShapeType.PIXEL) {
            const shapeKey = `${instanceItem.id}-${camera}`;
            if (!shapes[shapeKey]) {
              data.originPixelShapes.push({
                shape: frameData.shape as PixelData,
                locked: frameData.locked,
                layer: frameData.layer,
                instanceId: instance.id,
                instanceItemId: instanceItem.id,
              });
            }
          }
        });
      });
      this.getInterpolator()?.postMessage({
        type: 'pixel-resolve',
        data,
      });
    }
  }

  /**
   * show interpolated success message
   */
  showInterpolatedSuccessMessage = debounce(() => {
    const frames = Array.from(this.interpolatedFrames).sort((a, b) => a - b);
    frames.forEach((frameIndex) => this.interpolatedFrames.delete(frameIndex));
    message.success(i18n.translate('INTERPOLATION_FRAMES_SUCCESS', { values: { frames: formatFrames(frames) } }));
  }, 500);

  /**
   * enter reorganize mode
   * @param instanceItemName
   */
  enterReorganizeMode(instanceItemName?: string) {
    this.exitReorganizeMode();

    if (this.selectedInstances.length === 1 && (
      instanceItemName || this.selectedInstanceItems.length === 1
    )) {
      this.reorganizingInstance = this.selectedInstances[0];
      this.reorganizingInstanceItemName = instanceItemName || this.selectedInstanceItems[0].name;

      let label = '';
      if (this.reorganizingInstance.isSingle) {
        // single
        label = i18n.translate('CHANGE_CATEGORY_REORGANIZE_ITEM_LABEL_SINGLE', {
          values: {
            instance: this.reorganizingInstance.label,
          },
        });
      } else {
        const itemLabel = this.reorganizingInstance.categoryRef.children.find((c) => c.name === this.reorganizingInstanceItemName)?.displayName;
        label = i18n.translate('CHANGE_CATEGORY_REORGANIZE_ITEM_LABEL', {
          values: {
            instance: this.reorganizingInstance.label,
            item: itemLabel || this.reorganizingInstanceItemName,
          },
        });
      }

      this.reorganizingMsg = message.open({
        content: React.createElement(Message, {
          message: i18n.translate('CHANGE_CATEGORY_REORGANIZE_TIP', { values: { label } }),
          btn: i18n.translate('CHANGE_CATEGORY_REORGANIZE_FINISH'),
          onBtnClick: () => this.exitReorganizeMode(),
        }),
        duration: 0,
        icon: null,
        className: 'mode-message',
      });
    }
  }

  /**
   * exit reorganize mode
   */
  exitReorganizeMode() {
    if (this.reorganizingMsg) {
      this.reorganizingMsg();
    }
    this.reorganizingInstance = undefined;
    this.reorganizingInstanceItemName = undefined;
    this.reorganizingMsg = undefined;
  }

  /**
   * try to reorganize
   */
  tryReorganize() {
    if (this.reorganizingInstance && this.reorganizingInstanceItemName && this.isSingleSelected) {
      const selectedInstanceItem = this.selectedInstanceItems[0];
      const { currentCamera, currentFrame } = this.rootStore.frame;
      const frameData = selectedInstanceItem.cameras[currentCamera]?.frames[currentFrame];
      if (selectedInstanceItem.instance.id === this.reorganizingInstance.id) {
        if (!frameData?.numberChangeAllowed) {
          // same instance, but number change is disabled
          return;
        }
      } else if (
        (!frameData?.numberChangeAllowed && selectedInstanceItem.instance.number !== this.reorganizingInstance.number) ||
        (!frameData?.categoryChangeAllowed && selectedInstanceItem.instance.category !== this.reorganizingInstance.category)
      ) {
        return;
      }
      const currentTool = getToolTypeFromFrameData(frameData);
      if (currentTool) {
        const categoryItem = this.reorganizingInstance.categoryRef.children.find((c) => c.name === this.reorganizingInstanceItemName);
        if (categoryItem) {
          const tools = categoryItem.tools.map((t) => t.type);
          if (includeSameTool(currentTool, tools)) {
            // same shape type, do reorganize
            const targetInstanceNum = (
              selectedInstanceItem.instance.id === this.reorganizingInstance.id &&
              selectedInstanceItem.name === this.reorganizingInstanceItemName
            )
              ? undefined // in same instance, and same instance item name, should move to new instance
              : this.reorganizingInstance.number;
            this.changeCategoryForInstanceItem(
              selectedInstanceItem,
              this.reorganizingInstance.category,
              this.reorganizingInstanceItemName,
              { [currentCamera]: [currentFrame] },
              targetInstanceNum,
            );
          } else {
            message.warning(i18n.translate('CHANGE_CATEGORY_REORGANIZE_DIFF_SHAPE'));
          }
        }
      }
    }
  }
}
