import React from 'react';
import { observer } from 'mobx-react';
import { Tooltip } from 'antd';
import cx from 'classnames';
import { Hide } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { ViewMode } from '../../types';

const HideOthers = observer(() => {
  const { viewMode, hideOthers } = store.config;
  const disabled = !store.initialized || viewMode === ViewMode.GRID;
  return (
    <Tooltip placement="bottom" title={i18n.translate('TOOLBAR_TIP_HIDE')}>
      <div
        className={cx('icon-button', {
          'icon-button--disabled': disabled,
          'icon-button--active': hideOthers,
        })}
        onClick={() => {
          if (!disabled) {
            store.config.setHideOthers(!hideOthers);
          }
        }}
      >
        <Hide />
      </div>
    </Tooltip>
  );
});

export default HideOthers;
