// eslint-disable-next-line @typescript-eslint/naming-convention
export enum TOOL_MODE {
  LABELING = 'LABELING',
  REWORK = 'REWORK',
  QA = 'QA',
  QA_RO = 'QA_RO',
  QA_RW = 'QA_RW',
  AUDIT = 'AUDIT',
  PREVIEW = 'PREVIEW',
}

export function isQA(mode: TOOL_MODE) {
  return mode === TOOL_MODE.QA
        || mode === TOOL_MODE.QA_RO
        || mode === TOOL_MODE.QA_RW;
}

export function isReviewEditable(mode: TOOL_MODE) {
  return isQA(mode) || mode === TOOL_MODE.AUDIT;
}

export function isAnnotationReadonly(mode: TOOL_MODE) {
  return mode === TOOL_MODE.QA_RO || mode === TOOL_MODE.AUDIT || mode === TOOL_MODE.PREVIEW;
}

export function isRework(mode: TOOL_MODE) {
  return mode === TOOL_MODE.REWORK;
}

export function isPreview(mode: TOOL_MODE) {
  return mode === TOOL_MODE.PREVIEW;
}
