import React from 'react';
import { observer } from 'mobx-react';
import { Tooltip } from 'antd';
import cx from 'classnames';
import { Continuous } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';

const ContinuousMode = observer(() => {
  const { continuousMode } = store.config;
  const disabled = !store.initialized || store.isPreview;
  return (
    <Tooltip placement="bottom" title={i18n.translate('TOOLBAR_TIP_CONTINUOUS')}>
      <div
        className={cx('icon-button', {
          'icon-button--disabled': disabled,
          'icon-button--active': continuousMode,
        })}
        onClick={() => {
          if (!disabled) {
            store.config.setContinuousMode(!continuousMode);
          }
        }}
      >
        <Continuous />
      </div>
    </Tooltip>
  );
});

export default ContinuousMode;
