export interface Subtitle {
  id?: string;
  start: number;
  end: number;
  source: string;
  target?: string;
  pretranslation?: string;
}

export enum StatusType {
  TRANSLATION = 'TRANSLATION',
  MARK = 'MARK',
  REPLACE = 'REPLACE',
}

export type StatusItem = SubtitleStatus | MarkStatus | ReplaceStatus;

export interface Status {
  type: StatusType;
  before: StatusItem[];
  after: StatusItem[];
}

export interface SubtitleStatus {
  index: number;
  anchor: number;
  content?: string;
}

export interface MarkStatus {
  index: number;
}

export interface ReplaceStatus {
  index: number;
  content?: string;
}

export interface SearchResult {
  rowIndex: number;
  start: number;
  end: number;
  text: string;
}

export enum ReviewResultType {
  APPROVE = 'approve',
  REJECT = 'reject',
  PENDING = 'pending',
}

export interface ReviewItem {
  type: ReviewResultType;
  comment?: string;
}
