import React, { useState } from 'react';
import { observer } from 'mobx-react';
import { Collapse } from 'antd';
import { CaretRightOutlined, ExclamationCircleFilled, ArrowRightOutlined, EyeOutlined, EyeInvisibleOutlined } from '@ant-design/icons';
import cx from 'classnames';
import { Warning, Sync } from '../../../common/icons';
import ImagePreview from '../../../common/modal/ImagePreview';
import ValidationItem from './ValidationItem';
import { getReviewRelatedInstanceInfo } from '../review/ReviewModal';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { ValidationType, ReviewResult } from '../../types';

const ValidationPanel = observer(() => {
  const titles = {
    [ValidationType.ATTR_EMPTY]: i18n.translate('VALIDATION_TYPE_ATTR_EMPTY'),
    [ValidationType.INSTANCE_ITEM_COUNT]: i18n.translate('VALIDATION_TYPE_INSTANCE_ITEM_COUNT'),
    [ValidationType.INVALID_SHAPE]: i18n.translate('VALIDATION_TYPE_INVALID_SHAPE'),
    [ValidationType.OUT_OF_BOUNDARY]: i18n.translate('VALIDATION_TYPE_OUT_OF_BOUNDARY'),
    [ValidationType.CUSTOM]: i18n.translate('VALIDATION_TYPE_CUSTOM'),
    [ValidationType.EMPTY_AREA]: i18n.translate('VALIDATION_TYPE_EMPTY_AREA'),
    [ValidationType.FULLY_COVERED]: i18n.translate('VALIDATION_TYPE_FULLY_COVERED'),
    [ValidationType.FRAME_VALID_ANNOTATE]: i18n.translate('VALIDATION_TYPE_FRAME_VALID_ANNOTATE'),
  };
  const [previewImg, setPreviewImg] = useState('');

  const renderReviews = () => {
    const reviews = store.review.allReviews.filter((r) => r.result === ReviewResult.REJECT);
    if (reviews.length <= 0) {
      return null;
    }
    return (
      <Collapse.Panel
        key="reviews"
        header={i18n.translate('VALIDATION_REVIEWS')}
        extra={(
          <div>
            <span className="result-count">
              {reviews.length}
            </span>
          </div>
        )}
      >
        {reviews.map((r) => {
          const { infoStr, instance, instanceItem } = getReviewRelatedInstanceInfo(r);
          let displayColor;
          if (instanceItem) {
            displayColor = instanceItem.categoryItemRef.displayColor;
          } else if (instance) {
            displayColor = '#FFFFFF';
          }
          return (
            <div
              key={r.id}
              className="warning-item"
            >
              <div className="warning-title">
                <div className="name">
                  <div
                    className="cat-color-dot"
                    style={{ backgroundColor: displayColor || '#DC4624' }}
                  />
                  {infoStr}
                </div>
                {instance && (
                  <div
                    className="action"
                    onClick={() => {
                      if (r.camera !== store.frame.currentCamera) {
                        store.frame.setCamera(r.camera);
                      }
                      store.frame.setFrame(r.frameIndex);
                      if (instanceItem) {
                        store.instance.selectInstanceItem(instanceItem, true);
                      } else if (instance) {
                        store.instance.selectInstance(instance, true);
                      }
                    }}
                  >
                    <ArrowRightOutlined />
                  </div>
                )}
              </div>
              <div className="warning-msg">
                {!store.frame.isSingleCamera && `[C${store.frame.cameraNames.indexOf(r.camera) + 1}] `}
                {`[${i18n.translate('FRAME_ATTRIBUTES_FRAME', { values: { frame: r.frameIndex + 1 } })}] `}
                {r.type?.join(';')}
                <div
                  className="img-container"
                  onClick={(e) => {
                    if ((e.target as HTMLElement).tagName === 'IMG') {
                      setPreviewImg((e.target as HTMLImageElement).src);
                    }
                  }}
                  // eslint-disable-next-line react/no-danger
                  dangerouslySetInnerHTML={{ __html: r.comment || '' }}
                />
              </div>
            </div>
          );
        })}
      </Collapse.Panel>
    );
  };

  const renderWarnings = (type: ValidationType) => {
    const hasError = type === ValidationType.CUSTOM && store.validation.hasCustomError;
    const showCheckLayerIcon = type === ValidationType.EMPTY_AREA;
    const CheckLayerIcon = store.validation.checkLayerVisible ? EyeOutlined : EyeInvisibleOutlined;
    const warnings = store.validation.warnings.filter((w) => w.type === type);
    if (warnings.length <= 0 && !hasError) {
      return null;
    }
    return (
      <Collapse.Panel
        key={type}
        header={titles[type]}
        extra={(
          <div>
            {hasError && (
              <ExclamationCircleFilled style={{ color: 'rgba(220, 70, 36)', marginRight: 4 }} />
            )}
            {showCheckLayerIcon && (
              <CheckLayerIcon
                style={{ marginRight: 4 }}
                onClick={(e) => {
                  e.stopPropagation();
                  store.validation.setCheckLayerVisibility(!store.validation.checkLayerVisible);
                }}
              />
            )}
            <span className="result-count">
              {warnings.length}
            </span>
          </div>
        )}
      >
        {hasError && <div className="error">{i18n.translate('VALIDATION_CUSTOM_ERROR')}</div>}
        {warnings.map((warning) => <ValidationItem key={warning.id} warning={warning} />)}
      </Collapse.Panel>
    );
  };

  return (
    <div className="scroller">
      <div className="sidebar-validation-header">
        <div>
          <Warning />
          <span style={{ paddingLeft: 8 }}>
            {i18n.translate('VALIDATION_TITLE', { values: { count: store.validation.warningCount } })}
          </span>
        </div>
        <div>
          {store.validation.canvasChecking && (
            <div
              style={{ fontSize: 12, color: 'rgba(255, 255, 255, 0.6)', paddingRight: 4, cursor: 'pointer' }}
              onClick={store.validation.stopValidate}
            >
              {i18n.translate('VALIDATION_STOP')}
            </div>
          )}
          <div
            className={cx('icon-button', {
              spinning: store.validation.checking || store.validation.canvasChecking,
            })}
            onClick={store.validation.sync}
          >
            <Sync />
          </div>
        </div>
      </div>
      <div className="sidebar-validation-content">
        <Collapse
          ghost
          expandIcon={({ isActive }) => (
            <CaretRightOutlined
              style={{
                ...isActive ? {
                  top: 11,
                  transform: 'rotate(90deg)',
                } : {
                  color: 'rgba(255, 255, 255, 0.6)',
                },
              }}
            />
          )}
        >
          {renderReviews()}
          {Object.values(ValidationType).map((type) => renderWarnings(type))}
        </Collapse>
      </div>
      {previewImg && <ImagePreview maskClosable src={previewImg} onClose={() => setPreviewImg('')} />}
    </div>
  );
});

export default ValidationPanel;
