import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Select, Radio, Form } from 'antd';
import { ImageTextField } from '@appen-china/easy-form';
import ImagePreview from '../../../common/modal/ImagePreview';
import Modal from '../../../common/modal/Modal';
import rootStore from '../../store/RootStore';
import { Instance, Review, ReviewResult } from '../../types';
import i18n from '../../locales';
import './ReviewPanel.scss';

interface ReviewPanelProps {
  instance: Instance | null;
  review?: Review;
}

const radioOptions = [
  ReviewResult.APPROVE,
  ReviewResult.REJECT,
  ReviewResult.SUSPEND
];

const formItemLayoutVertical = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 6 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 24 },
  },
};

const ReviewPanel = ({ instance, review }: ReviewPanelProps) => {
  const [form] = Form.useForm();
  const [previewImg, setPreviewImg] = useState<string>('');
  const [label, setLabel] = useState<string>('');
  const isReview = rootStore.review.isEnabled && !rootStore.review.drawMode;

  useEffect(() => {
    if (instance) {
      const { category } = instance;
      const { selectedNumber, categoryInstancesMap } = rootStore.instance;
      const str = `${category} （${selectedNumber}/${categoryInstancesMap[category]?.length || 0}）`;
      setLabel(str);
    }
    if (review) {
      form?.setFieldsValue({ ...review });
    } else {
      form?.resetFields();
    }
  }, [instance, review, rootStore.setting.reviewVisible]);

  const handleSave = async () => {
    if (isReview && instance) {
      const results = await form.validateFields();
      if (!results.errorFields) {
        if (results.result) {
          rootStore.review.setReview({ instanceId: instance.id, ...results } as Review);
        }
        rootStore.setting.setReviewVisible(false);
      }
    } else {
      rootStore.setting.setReviewVisible(false);
    }
  };

  const handleDelete = () => {
    if (instance?.id) {
      rootStore.review.deleteReviewByInstance(instance?.id);
      rootStore.setting.setReviewVisible(false);
    }
  };

  if (rootStore.review.drawMode && !review) return null;

  return (
    <Modal
      visible={rootStore.setting.reviewVisible}
      draggable
      closable={false}
      maskClosable
      dragId="phonetic-transcription-review"
      className={rootStore.setting.reviewId}
      title={isReview ? i18n.translate('QC_MODAL_TITLE') : i18n.translate(`QC_RESULT_${review?.result.toLocaleUpperCase()}`)}
      onClose={handleSave}
    >
      <div className="praat-quanlity">
        <div className="praat-quanlity-label">
          <div>{isReview ? label : `${label} ${review?.type?.join(', ')};`}</div>
          {!isReview && (
            <div
              className="img-container"
              onClick={(e) => {
                if ((e.target as HTMLElement).tagName === 'IMG') {
                  setPreviewImg((e.target as HTMLImageElement).src);
                }
              }}
              // eslint-disable-next-line react/no-danger
              dangerouslySetInnerHTML={{ __html: review?.comment || '' }}
            />
          )}
        </div>
        <div className="praat-quanlity-form">
          {isReview && (
            <>
              <Form
                form={form}
                size="small"
                layout="vertical"
              >
                <Form.Item name="result">
                  <Radio.Group style={{ width: '100%' }}>
                    {radioOptions.map((type) => (
                      <Radio.Button key={type} value={type} className={type}>
                        {i18n.translate(`QC_${type.toUpperCase()}`)}
                      </Radio.Button>
                    ))}
                  </Radio.Group>
                </Form.Item>
                <Form.Item
                  className="clearStyle"
                  noStyle
                  shouldUpdate={(prevValues, curValues) => prevValues.result !== curValues.result}
                  {...formItemLayoutVertical}
                >
                  {({ getFieldValue }) => {
                    if (getFieldValue('result') === ReviewResult.REJECT) {
                      return (
                        <>
                          <Form.Item
                            name="type"
                            label={i18n.translate('QC_REASON_LABEL')}
                            rules={[{ required: true, message: i18n.translate('QC_REASON_LABEL') }]}
                          >
                            <Select
                              mode="multiple"
                              showArrow
                              style={{ width: '100%' }}
                              dropdownClassName="speech-qc-selected"
                            >
                              {rootStore.review.issueTypes.map((item) => (
                                <Select.Option value={item} key={item}>{item}</Select.Option>
                              ))}
                            </Select>
                          </Form.Item>
                          <Form.Item name="comment">
                            <ImageTextField uploader={rootStore.saveFile} />
                          </Form.Item>
                        </>
                      );
                    }
                  }}
                </Form.Item>
              </Form>
              <div className="review-action">
                <div
                  className="review-btn"
                  onClick={handleDelete}
                >
                  {i18n.translate('COMMON_DELETE')}
                </div>
                <div
                  className="review-btn save"
                  onClick={handleSave}
                >
                  {i18n.translate('COMMON_SAVE')}
                </div>
              </div>
            </>
          )}
        </div>
        {previewImg && <ImagePreview src={previewImg} maskClosable onClose={() => setPreviewImg('')} />}
      </div>
    </Modal>
  );
};

export default observer(ReviewPanel);
