import React from 'react';
import { computed, makeObservable } from 'mobx';
import { observer } from 'mobx-react';
import cx from 'classnames';
import Collapse, { Panel } from '../../common/collapse/Collapse';
import ShapeLabel from '../../common/sidebar/ShapeLabel';
import Relationships from './Relationships';
import RelationshipsStore from '../store/RelationshipsStore';
import ReviewsStore from '../store/ReviewsStore';
import i18n from '../locales';
import { Ontology, CategoryInstancesMap, Instance, ReviewResult } from '../types';
import './Sidebar.scss';

interface SidebarProps {
  readonly: boolean;
  ontology: Ontology[];
  currentFrame: number;
  currentInstancesMap: {[instanceId: string]: boolean};
  categoryInstancesMap: CategoryInstancesMap;
  instances: {[instanceId: string]: Instance};
  selectedInstanceId: string;
  selectedCategoryName: string;
  hasValidation: boolean;
  selectInstance: (instanceId: string, isManual?: boolean) => void;
  selectCategory: (categoryName: string) => void;
  addShapeToInstance: (instanceId: string) => void;
  deleteShapeFromInstance: (instanceId: string, type?: 'current' | 'key' | 'following' | 'all') => void;
  openInstanceMove: () => void;
  renderValidator: () => JSX.Element | false;
  getInstanceLabel: (instance: Instance) => string;
}

@observer
class Sidebar extends React.Component<SidebarProps> {
  scroller = React.createRef<HTMLDivElement>();

  constructor(props: SidebarProps) {
    super(props);
    makeObservable(this);
  }

  @computed get categoryInstancesIds() {
    const categoryInstancesIds: {[category: string]: string[]} = {};
    const { categoryInstancesMap } = this.props;
    Object.keys(categoryInstancesMap).forEach((category) => {
      categoryInstancesIds[category] = Object.keys(categoryInstancesMap[category]).sort((a, b) => categoryInstancesMap[category][a] - categoryInstancesMap[category][b]);
    });
    return categoryInstancesIds;
  }

  componentDidUpdate(prevProps: SidebarProps) {
    if (prevProps.selectedInstanceId !== this.props.selectedInstanceId) {
      this.scrollTo(this.props.selectedInstanceId);
    }
  }

  scrollTo(instanceId: string) {
    if (instanceId) {
      const dom = document.getElementById(`shape-label-${instanceId}`);
      if (dom && this.scroller.current) {
        const { top: sTop, bottom: sBottom } = this.scroller.current.getBoundingClientRect();
        const { top: dTop, bottom: dBottom } = dom.getBoundingClientRect();
        if (dTop < sTop || dBottom > sBottom) {
          this.scroller.current.scrollBy({ top: dTop - sTop });
        }
      }
    }
  }

  renderInstance(instanceId: string) {
    const { readonly, currentFrame, instances, currentInstancesMap, selectedInstanceId, selectInstance, addShapeToInstance, deleteShapeFromInstance, openInstanceMove } = this.props;
    const instance = instances[instanceId];
    const reviewResult = ReviewsStore.getReviewResult(currentFrame, instanceId);
    return (
      <ShapeLabel
        id={`shape-label-${instanceId}`}
        key={instanceId}
        label={instance.number.toString()}
        selected={selectedInstanceId === instanceId}
        hidden={!currentInstancesMap[instanceId]}
        menuItems={[
          [
            { label: i18n.translate('MOVE_TITLE'), disabled: readonly, onClick: openInstanceMove },
          ],
          [
            { label: i18n.translate('DELETE_CURRENT_FRAME'), disabled: readonly, onClick: () => deleteShapeFromInstance(instanceId) },
            { label: i18n.translate('DELETE_FOLLOWING_FRAMES_UNTIL_KEY'), disabled: readonly, onClick: () => deleteShapeFromInstance(instanceId, 'key') },
            { label: i18n.translate('DELETE_FOLLOWING_FRAMES'), disabled: readonly, onClick: () => deleteShapeFromInstance(instanceId, 'following') },
            { label: i18n.translate('DELETE_ALL_FRAMES'), disabled: readonly, onClick: () => deleteShapeFromInstance(instanceId, 'all') },
          ],
        ]}
        onClick={() => selectInstance(instanceId, true)}
        {...!readonly && {
          onAddClick: () => addShapeToInstance(instanceId),
        }}
        {...(!ReviewsStore.isReadonly && reviewResult === ReviewResult.APPROVE) && {
          approved: true,
        }}
        {...(!ReviewsStore.isReadonly && reviewResult === ReviewResult.SUSPEND) && {
          suspending: true,
        }}
        {...reviewResult === ReviewResult.REJECT && {
          rejected: true,
        }}
        {...(ReviewsStore.isEditable || (ReviewsStore.isReadonly && reviewResult === ReviewResult.REJECT)) && {
          onReviewClick: () => ReviewsStore.setInstance(instanceId),
        }}
      />
    );
  }

  renderCategory({ class_name, display_name, display_color }: Ontology) {
    const { selectedCategoryName, selectCategory } = this.props;
    return (
      <div
        key={class_name}
        className={cx('sidebar-category', {
          selected: class_name === selectedCategoryName,
        })}
      >
        <div
          className="sidebar-category-title"
          onClick={() => selectCategory(class_name)}
        >
          <div className="sidebar-category-title__dot" style={{ backgroundColor: display_color }} />
          {display_name || class_name}
        </div>
        {this.categoryInstancesIds[class_name] && this.categoryInstancesIds[class_name].length > 0 && (
          <div className="sidebar-category-content">
            {this.categoryInstancesIds[class_name].map((instanceId) => this.renderInstance(instanceId))}
          </div>
        )}
      </div>
    );
  }

  renderInstances() {
    return this.props.ontology.map((o) => this.renderCategory(o));
  }

  render() {
    const { readonly, currentFrame, instances, hasValidation, renderValidator, getInstanceLabel } = this.props;
    return (
      <div
        className="sidebar"
        style={{ paddingBottom: hasValidation ? 32 : 0 }}
      >
        {RelationshipsStore.getConfig() ? (
          <Collapse>
            <Panel title={i18n.translate('SIDEBAR_INSTANCE_LABEL')} rows={3} contentRef={this.scroller}>
              {this.renderInstances()}
            </Panel>
            <Panel title={i18n.translate('SIDEBAR_RELATIONSHIP_LABEL')} rows={1}>
              <Relationships
                readonly={readonly}
                currentFrame={currentFrame}
                instances={instances}
                getInstanceLabel={getInstanceLabel}
              />
            </Panel>
          </Collapse>
        ) : (
          <div ref={this.scroller} className="scroll">
            {this.renderInstances()}
          </div>
        )}
        {renderValidator()}
      </div>
    );
  }
}

export default React.memo(Sidebar, (prevProps, nextProps) => !(
  prevProps.readonly !== nextProps.readonly ||
  prevProps.currentFrame !== nextProps.currentFrame ||
  prevProps.selectedInstanceId !== nextProps.selectedInstanceId ||
  prevProps.selectedCategoryName !== nextProps.selectedCategoryName ||
  JSON.stringify(prevProps.currentInstancesMap) !== JSON.stringify(nextProps.currentInstancesMap) ||
  JSON.stringify(prevProps.categoryInstancesMap) !== JSON.stringify(nextProps.categoryInstancesMap)
));
