import React, { ReactNode, useEffect, useState } from 'react';
import { observer } from 'mobx-react';
import { toJS, reaction } from 'mobx';
import cx from 'classnames';
import { debounce } from 'lodash';
import { Radio, InputNumber, Dropdown, Menu, Tooltip } from 'antd';
import { CaretDownOutlined, CheckOutlined } from '@ant-design/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';
import {
  Tracks, RegionPlay, SingleLoop, OverallPlay, DoubleLeft, Pause,
  CaretRight, DoubleRight, PreviousArrows, NextArrows,
} from '../../../common/icons';
import { formatTimeCallback } from '../../utils';
import { MediaModes, TimeAction, Instance } from '../../types';
import './index.scss';

interface PlayControlsProps {
  currentTime: number;
  instance: Instance | null;
}

const PlayControls = ({ currentTime, instance }: PlayControlsProps) => {
  const [info, setInfo] = useState<ReactNode>('');
  const [hideTracks, setHideTracks] = useState<number[]>([]);
  const [visible, setVisible] = useState<boolean>(false);

  useEffect(() => {
    reaction(
      () => store.waveform.hideChannels,
      () => {
        setHideTracks(store.waveform.hideChannels);
      },
    );
  }, []);

  useEffect(() => {
    if (instance) {
      const { start, end } = instance;
      const newInfo = (
        <>
          {formatTimeCallback(start, 5000)}
          -
          {formatTimeCallback(end, 5000)}
          &nbsp;&nbsp;&nbsp;&nbsp;
          {i18n.translate('DURATION')}
          {formatTimeCallback((end - start), 5000)}
        </>
      );
      setInfo(newInfo);
    } else {
      setInfo('');
    }
  }, [instance]);

  const handleFilter = (channelIndex: number) => {
    const hideChannels = toJS(hideTracks);
    const index = hideChannels.indexOf(channelIndex);
    if (index >= 0) {
      hideChannels.splice(index, 1);
    } else {
      hideChannels.push(channelIndex);
    }
    store.waveform.sethideChannels(hideChannels);
  };

  const handleMode = (mode: MediaModes) => {
    handleBlur();
    store.waveform.setPlayMode(mode);
  };

  const handleSeek = (action: TimeAction) => {
    store.waveform.setSeekDuration(action);
  };

  const togglePlaying = () => {
    store.waveform.handlePlay();
  };

  const onSpeedChange = (speed: number) => {
    handleBlur();
    store.waveform.setSpeed(speed);
  };

  const onZoomChange = (zoom: number) => {
    handleBlur();
    store.setting.setZoom(zoom);
  };

  const handleBlur = () => {
    if (document.activeElement?.tagName.toLocaleUpperCase() !== 'BODY') {
      (document.activeElement as any)?.blur();
    }
  };

  // previous
  const selectPrev = () => {
    const { selectedIndex, displayInstances, setSelectedInstance } = store.instance;
    let index = selectedIndex - 1;
    if (selectedIndex <= 0) {
      index = displayInstances.length - 1;
    }
    const selectedInstance = displayInstances[index];
    if (selectedInstance) {
      setSelectedInstance(selectedInstance.id);
    }
  };

  // next
  const selectNext = () => {
    const { selectedIndex, displayInstances, setSelectedInstance } = store.instance;
    let index = selectedIndex + 1;
    if (selectedIndex >= displayInstances.length - 1) {
      index = 0;
    }
    const selectedInstance = displayInstances[index];
    if (selectedInstance) {
      setSelectedInstance(selectedInstance.id);
    }
  };

  const modeIcons = {
    region_play: <RegionPlay viewBox="-4 -4 24 24" />,
    region_loop: <SingleLoop viewBox="-4 -4 24 24" />,
    overall: <OverallPlay viewBox="-4 -4 24 24" />,
  };

  return (
    <>
      <div className="control-container flex-box flex-align-center">
        <div className="control-left flex-box flex-align-center">
          <Dropdown
            overlayClassName="tracks-dropdown"
            visible={visible}
            overlay={() => (
              <Menu className="tracks-dropdown-menus">
                <Menu.Item className="tracks-label tracks-label-title" key="label">{i18n.translate('VISIBLE_TRACKS_TIP')}</Menu.Item>
                {store.waveform.channelsOptions.map(({ index }) => (
                  <Menu.Item
                    key={index}
                    className={cx('tracks-label', {
                      'tracks-label-active': hideTracks.indexOf(index) < 0
                    })}
                    onClick={() => { handleFilter(index); }}
                  >
                    <CheckOutlined />
                    {/* { index === -1 ? i18n.translate('ORIGINAL_AUDIO') : `${i18n.translate('CHANNEL')} ${index + 1}` } */}
                    { `${i18n.translate('CHANNEL')} ${index + 1}` }
                  </Menu.Item>
                ))}
              </Menu>
            )}
            placement="topLeft"
          >
            <div className="filter-button flex-box flex-align-center" onClick={() => { setVisible(!visible); }}>
              <span className="control-icon">
                <Tracks />
              </span>
              <CaretDownOutlined className={cx('filter-button-icon', { active: visible })} />
            </div>
          </Dropdown>
          <Radio.Group
            value={store.waveform.playMode}
            buttonStyle="solid"
            className="play-mode"
            onChange={(e) => handleMode(e.target.value!)}
          >
            {Object.values(MediaModes).map((mode) => (
              <Tooltip
                title={i18n.translate(`PLAY_MODE_TIP_${mode.toUpperCase()}`)}
                key={mode}
              >
                <Radio.Button value={mode}>
                  <span className="control-icon">{modeIcons[mode]}</span>
                </Radio.Button>
              </Tooltip>
            ))}
          </Radio.Group>
        </div>
        <div className="control-center flex-box flex-align-center">
          <div className="duration">{formatTimeCallback(store.waveform.currentTime, 5000)}</div>
          <div className="paly-control flex-box flex-align-center">
            <div
              className={cx('control-icon scale', {
                disabled: currentTime <= 0
              })}
              onClick={() => { if (currentTime > 0)handleSeek(TimeAction.BACKWARD); }}
            >
              <DoubleLeft />
            </div>
            <div className="control-icon" onClick={togglePlaying}>
              {store.waveform.isPlaying ? <Pause /> : <CaretRight />}
            </div>
            <div
              className={cx('control-icon scale', {
                disabled: currentTime >= store.waveform.duration
              })}
              onClick={() => { if (currentTime < store.waveform.duration)handleSeek(TimeAction.FORWARD); }}
            >
              <DoubleRight />
            </div>
          </div>
        </div>
        <div className="control-right flex-box flex-align-center">
          <div className="control-item">
            <span>
              {i18n.translate('SPEED_LABEL')}
              ：
            </span>
            <InputNumber min={0.25} max={4} step={0.25} value={store.waveform.speed} onChange={onSpeedChange} />
          </div>
          <div className="control-item">
            <span>
              {i18n.translate('ZOOM_LABEL')}
              ：
            </span>
            <InputNumber min={1} max={1000} step={1} value={store.setting.zoom} onChange={debounce((e) => onZoomChange(e), 50)} />
          </div>
        </div>
      </div>
      {info && (
        <div className="selected-info">
          <div className="instance-label">
            <div className="number">{store.instance.selectedIndex + 1}</div>
            <div className="label">
              <span className="dot" style={{ background: instance?.color }} />
              {instance?.label}
            </div>
            <div className="info">{info}</div>
          </div>
          <div className="action">
            <div className="action-item" onClick={selectPrev}><PreviousArrows /></div>
            <div className="action-item" onClick={selectNext}><NextArrows /></div>
          </div>
        </div>
      )}
    </>
  );
};

export default observer(PlayControls);
