import React from 'react';
import { FieldControlType, FieldValue } from '@appen/easy-form/es/types';
import { FieldConfig } from '../../types';
import './InstanceAttributes.scss';

interface AttributeValueItemProps {
  fieldName: string;
  fieldsMap: { [fieldName: string]: FieldConfig };
  values: any;
  showLabel?: boolean;
  showValue?: boolean;
}

const AttributeValueItem = ({ fieldName, fieldsMap, values = {}, showLabel = true, showValue = true }: AttributeValueItemProps) => {
  if (!showLabel && !showValue) {
    return null;
  }

  const field = (fieldsMap || {})[fieldName];
  const fieldValue = values[fieldName];

  let displayValue = '';
  if (showValue && fieldValue !== undefined) {
    if (field?.options) {
      const getOptionLabel = (value: FieldValue) => {
        const option = field.options!.find((o) => o.value === value);
        return (option && option.label) ? option.label : value.toString();
      };
      displayValue = Array.isArray(fieldValue) ? fieldValue.map(getOptionLabel).toString() : getOptionLabel(fieldValue);
    } else {
      displayValue = fieldValue.toString();
    }
  }

  return (
    <div className="value-item">
      {showLabel && (
        <div className="value-item-label">
          {field?.label || field?.name || fieldName}
          {showValue && ':'}
        </div>
      )}
      {showValue && (
        <div className="value-item-value">
          {
            field?.type === FieldControlType.IMAGETEXT
              // eslint-disable-next-line react/no-danger
              ? <div dangerouslySetInnerHTML={{ __html: displayValue }} />
              : displayValue
          }
        </div>
      )}
    </div>
  );
};

export default AttributeValueItem;
