import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Select, Radio, Form, Row, Col } from 'antd';
import { ImageTextField } from '@appen/easy-form';
import ImagePreview from '../../../common/modal/ImagePreview';
import rootStore from '../../store/RootStore';
import { Instance, Review, ReviewResult, ReviewData } from '../../types';
import i18n from '../../locales';
import './ReviewPanel.scss';

interface ReviewPanelProps {
  instance: Instance | null;
  review?: Review;
}

const radioOptions = [
  ReviewResult.APPROVE,
  ReviewResult.REJECT,
  ReviewResult.SUSPEND
];

const formItemLayoutVertical = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 6 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 24 },
  },
};

const ReviewPanel = ({ instance, review }: ReviewPanelProps) => {
  const [form] = Form.useForm();
  const [previewImg, setPreviewImg] = useState<string>('');
  const [label, setLabel] = useState<string>('');
  const [currentValues, setCurrentValues] = useState<ReviewData|null>(null);

  useEffect(() => {
    if (instance) {
      const { category } = instance;
      const { selectedNumber } = rootStore.instance;
      const str = `${category} ${selectedNumber}`;
      setLabel(str);
    }
  }, [instance]);

  useEffect(() => {
    if (review) {
      setCurrentValues({
        result: review.result,
        type: review.type,
        comment: review.comment,
      });
      if (form) {
        form.setFieldsValue({ ...review });
      }
    } else {
      setCurrentValues(null);
      form.resetFields();
    }
  }, [review]);

  const handleValuesChange = (_val:any, values:any) => {
    if (values.result === ReviewResult.REJECT) {
      if (!values.type || values.type.length === 0) {
        rootStore.review.setIsSelectInstanceEnable(false);
      } else {
        rootStore.review.setIsSelectInstanceEnable(true);
      }
      rootStore.review.setCurrentReview({ instanceId: instance!.id, ...values } as Review);
    } else {
      rootStore.review.setIsSelectInstanceEnable(true);
      rootStore.review.setCurrentReview({ instanceId: instance!.id, result: values.result });
    }
  };
  return (
    <div className="praat-quanlity">
      <div className="praat-quanlity-label">
        {label}
      </div>
      <div className="praat-quanlity-form">
        {rootStore.review.isEnabled ? (
          <Form
            form={form}
            size="small"
            layout="vertical"
            initialValues={{
              result: review?.result,
              type: review?.type,
              comment: review?.comment
            }}
            onValuesChange={handleValuesChange}
          >
            <Form.Item name="result">
              <Radio.Group style={{ width: '100%' }}>
                <Row>
                  {radioOptions.map((type, i) => (
                    <Col key={type} span={8}>
                      <Radio.Button value={type} className={type}>
                        <span>{i18n.translate(`QC_${type.toUpperCase()}`)}</span>
                        <span className="hint">{i + 1}</span>
                      </Radio.Button>
                    </Col>
                  ))}
                </Row>
              </Radio.Group>
            </Form.Item>
            <Form.Item
              className="clearStyle"
              noStyle
              shouldUpdate={(prevValues, curValues) => prevValues.result !== curValues.result}
              {...formItemLayoutVertical}
            >
              {({ getFieldValue }) => {
                if (getFieldValue('result') === ReviewResult.REJECT) {
                  return (
                    <>
                      <Form.Item
                        name="type"
                        label={i18n.translate('QC_REASON_LABEL')}
                        rules={[{ required: true, message: i18n.translate('QC_REASON_LABEL') }]}
                      >
                        <Select
                          mode="multiple"
                          showArrow
                          style={{ width: '100%' }}
                          dropdownClassName="speech-qc-selected"
                        >
                          {rootStore.review.issueTypes.map((item) => (
                            <Select.Option value={item} key={item}>{item}</Select.Option>
                          ))}
                        </Select>
                      </Form.Item>
                      <Form.Item name="comment">
                        <ImageTextField uploader={rootStore.saveFile} />
                      </Form.Item>
                    </>
                  );
                }
              }}
            </Form.Item>
          </Form>
        ) : (
          <>
            <div style={{ fontSize: 18, fontWeight: 600 }}>
              {currentValues?.result && i18n.translate(`QC_${currentValues.result.toUpperCase()}`)}
            </div>
            <div style={{ marginTop: 16 }}>
              <div>{i18n.translate('QC_NO_REASON')}</div>
              <div style={{ color: 'rgba(255, 255, 255, 0.6)' }}>{currentValues?.type?.join(', ')}</div>
            </div>
            <div style={{ marginTop: 16 }}>
              <div>{i18n.translate('QC_COMMENT_LABEL')}</div>
              <div
                style={{ color: 'rgba(255, 255, 255, 0.6)' }}
                className="img-container"
                onClick={(e) => {
                  if ((e.target as HTMLElement).tagName === 'IMG') {
                    setPreviewImg((e.target as HTMLImageElement).src);
                  }
                }}
                // eslint-disable-next-line react/no-danger
                dangerouslySetInnerHTML={{ __html: currentValues?.comment || '' }}
              />
            </div>
          </>
        )}
      </div>
      {previewImg && <ImagePreview src={previewImg} maskClosable onClose={() => setPreviewImg('')} />}
    </div>
  );
};

export default observer(ReviewPanel);
