import React, { useState, useEffect } from 'react';
import rootStore from '../../store/RootStore';
import './index.scss';

export interface SideMenusProps {
  loading: boolean;
  reference: string;
}

const SideMenus = (props: SideMenusProps) => {
  const [refercence, setReference] = useState<string[]>([]);
  useEffect(() => {
    if (props.reference && typeof props.reference === 'string') {
      setReference(props.reference.split(','));
    }
  }, [props.reference]);

  const handleInput = (val: string) => {
    const { selectedInstance, updateInstances, setUpdatedInstances } = rootStore.instance;
    if (selectedInstance) {
      const newInstance = {
        [selectedInstance.id]: {
          ...selectedInstance,
          text: selectedInstance.text ? selectedInstance.text + val : val,
        }
      };
      updateInstances(newInstance);
      setUpdatedInstances(newInstance);
    }
  };

  return (
    <div
      className="side-menus-container"
      onContextMenu={(e) => e.preventDefault()}
    >
      {refercence.map((char, i) => <span key={`${char + i}`} onClick={() => { handleInput(char); }} className="char">{char}</span>)}
    </div>
  );
};

export default SideMenus;
