import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Tooltip, Switch, Slider, Input, Row, Col } from 'antd';
import { CloseOutlined, RedoOutlined } from '@ant-design/icons';
import cx from 'classnames';
import { Palette } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';

const DisplaySettings = observer(() => {
  const { config, updateConfig } = store.shape;

  const [visible, setVisible] = useState(false);
  const [alpha, setAlpha] = useState('');
  const [toleranceAlpha, setToleranceAlpha] = useState('');
  const [borderAlpha, setBorderAlpha] = useState('');
  const [borderWidth, setBorderWidth] = useState('');
  const [dotRadius, setDotRadius] = useState('');
  const [vertexSize, setVertexSize] = useState('');

  useEffect(() => {
    setAlpha(`${config.alpha}%`);
  }, [config, config.alpha]);

  useEffect(() => {
    setToleranceAlpha(`${config.toleranceAlpha}%`);
  }, [config, config.toleranceAlpha]);

  useEffect(() => {
    setBorderAlpha(`${config.borderAlpha}%`);
  }, [config, config.borderAlpha]);

  useEffect(() => {
    setBorderWidth(`${config.borderWidth}`);
  }, [config, config.borderWidth]);

  useEffect(() => {
    setDotRadius(`${config.dotRadius}`);
  }, [config, config.dotRadius]);

  useEffect(() => {
    setVertexSize(`${config.vertexSize}`);
  }, [config, config.vertexSize]);

  const changeAlpha = () => {
    const num = Number(alpha);
    if (!Number.isNaN(num) && num >= 0 && num <= 100 && num !== config.alpha) {
      const int = parseInt(alpha, 10);
      updateConfig({ alpha: int });
    } else {
      setAlpha(`${config.alpha}%`);
    }
  };

  const changeToleranceAlpha = () => {
    const num = Number(toleranceAlpha);
    if (!Number.isNaN(num) && num >= 0 && num <= 100 && num !== config.toleranceAlpha) {
      const int = parseInt(toleranceAlpha, 10);
      updateConfig({ toleranceAlpha: int });
    } else {
      setAlpha(`${config.toleranceAlpha}%`);
    }
  };

  const changeBorderAlpha = () => {
    const num = Number(borderAlpha);
    if (!Number.isNaN(num) && num >= 0 && num <= 100 && num !== config.borderAlpha) {
      const int = parseInt(borderAlpha, 10);
      updateConfig({ borderAlpha: int });
    } else {
      setBorderAlpha(`${config.borderAlpha}%`);
    }
  };

  const changeBorderWidth = () => {
    const num = Number(borderWidth);
    if (!Number.isNaN(num) && num >= 1 && num <= 10 && num !== config.borderWidth) {
      const int = parseInt(borderWidth, 10);
      updateConfig({ borderWidth: int });
    } else {
      setBorderWidth(`${config.borderWidth}`);
    }
  };

  const changeDotRadius = () => {
    const num = Number(dotRadius);
    if (!Number.isNaN(num) && num >= 1 && num <= 10 && num !== config.dotRadius) {
      const int = parseInt(dotRadius, 10);
      updateConfig({ dotRadius: int });
    } else {
      setDotRadius(`${config.dotRadius}`);
    }
  };

  const changeVertexSize = () => {
    const num = Number(vertexSize);
    if (!Number.isNaN(num) && num >= 0 && num <= 10 && num !== config.vertexSize) {
      const int = parseInt(vertexSize, 10);
      updateConfig({ vertexSize: int });
    } else {
      setVertexSize(`${config.vertexSize}`);
    }
  };

  return (
    <>
      <Tooltip placement="bottom" title={i18n.translate('TOOLBAR_TIP_DISPLAY')}>
        <div
          className={cx('icon-button', {
            'icon-button--active': visible,
          })}
          onClick={() => setVisible(true)}
        >
          <Palette />
        </div>
      </Tooltip>
      {visible && (
        <div className="toolbar-panel display-panel">
          <div className="title">
            {i18n.translate('DISPLAY_TITLE')}
            <div className="close" onClick={() => setVisible(false)}>
              <CloseOutlined />
            </div>
          </div>
          <div className="item" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', color: '#FFFFFF' }}>
            {i18n.translate('DISPLAY_SHAPE_STYLE')}
            <div className="reset" onClick={store.shape.resetConfig}>
              <RedoOutlined />
            </div>
          </div>
