import Zn from './zn';
import En from './en';

const locale = () => {
  const lang = navigator.language;
  return function(word) {
    let translated;
    switch (lang) {
      case 'zh-CN':
        translated = Zn[word];
        break;
      default:
        translated = En[word];
        break;
    }
    return translated || word;
  }
}

export default locale();
