import React from 'react';
import { Tooltip } from 'antd';
import screenfull from 'screenfull';
import ReviewMode from './ReviewMode';
import UndoRedo from './UndoRedo';
import ViewMode from './ViewMode';
import ContinuousMode from './ContinuousMode';
import HideOthers from './HideOthers';
import HideAll from './HideAll';
import MeasurementBox from './MeasurementBox';
import AttributesMode from './AttributesMode';
import LabelMode from './LabelMode';
// import ActivePanels from './ActivePanels';
import DisplaySettings from './DisplaySettings';
import Save from './Save';
import Guide from './Guide';
import AppenLogo from '../../../common/AppenLogo';
import Preferences from './Preferences';
import Measurer from './Measurer';
import PredictiveModeButton from './PredictiveMode';
import {
  // ClearAll,
  Fullscreen,
} from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { isAppenCloud } from '../../../../utils';
import './Toolbar.scss';

interface ToolbarProps {
  onSave: () => void;
}

class Toolbar extends React.Component<ToolbarProps> {
  handleScreenFull = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle().then(() => {
        store.fullscreenRequested = true;
      });
    }
  };

  render() {
    return (
      <div className="toolbar">
        <div>
          {isAppenCloud() && (
            <div className="logo">
              <AppenLogo />
            </div>
          )}
          <ReviewMode />
          <UndoRedo />
          <Guide />
        </div>
        <div className="center">
          <ViewMode />
        </div>
        <div>
          <Measurer />
          <div className="divider" />
          <PredictiveModeButton />
          <ContinuousMode />
          <HideOthers />
          <HideAll />
          <div className="divider" />
          {/* <div className="icon-button" onClick={this.handleClearConfirm}>
            <ClearAll />
          </div> */}
          <MeasurementBox />
          <AttributesMode />
          <LabelMode />
          {/* <ActivePanels /> */}
          <DisplaySettings />
          <Save onSave={this.props.onSave} />
          <Preferences />
          <div className="divider" />
          <Tooltip placement="bottom" title={i18n.translate('TOOLBAR_TIP_FULLSCREEN')}>
            <div className="icon-button" onClick={this.handleScreenFull}>
              <Fullscreen />
            </div>
          </Tooltip>
        </div>
      </div>
    );
  }
}

export default Toolbar;
