import { Language } from '../../../utils/constants';
import CN from './zh-CN';
import US from './en-US';
import JP from './ja-JP';
import KR from './ko-KR';

class Internationalization {
  locale = Language.EN_US;

  get isUS() {
    return this.locale === Language.EN_US;
  }

  setLocale = (locale?: string) => {
    const languages = Object.values(Language);
    if (locale !== undefined) {
      // get from props
      if (languages.includes(locale as Language)) {
        this.locale = locale as Language;
      }
    } else if (navigator.language) {
      // get from browser
      const language = languages.find((l) => l.slice(0, 2) === navigator.language.slice(0, 2));
      if (language) {
        this.locale = language;
      }
    }
  };

  translate = (key: string, options?: any) => {
    let message;
    switch (this.locale) {
      case Language.ZH_CN:
        message = CN[key];
        break;
      case Language.JA_JP:
        message = JP[key];
        break;
      case Language.KO_KR:
        message = KR[key];
        break;
      default:
        message = US[key];
        break;
    }
    if (message) {
      let msg = message;
      if (options && options.values) {
        Object.keys(options.values).forEach((k) => {
          msg = msg.replace(new RegExp(`{${k}}`, 'g'), options.values[k]);
        });
      }
      return msg;
    }
    return key;
  };
}

export default new Internationalization();
