import React, { useEffect, useState } from 'react';
import { observer } from 'mobx-react';
import { Select, Radio, Button, Form, Row, Col } from 'antd';
import { ImageTextField } from '@appen/easy-form';
import Modal from '../../common/modal/Modal';
import ImagePreview from '../../common/modal/ImagePreview';
import ReviewsStore from '../store/ReviewsStore';
import i18n from '../locales';
import { Instance, ReviewResult } from '../types';

interface ReviewProps {
  currentFrame: number;
  instances: {[instanceId: string]: Instance};
  saveFile: (file: File) => Promise<string>;
  onReviewed: () => void;
  getInstanceLabel: (instance: Instance) => string;
}

const Review = ({ currentFrame, instances, saveFile, onReviewed, getInstanceLabel }: ReviewProps) => {
  const [previewImg, setPreviewImg] = useState('');
  const [result, setResult] = useState(ReviewResult.APPROVE);
  const { currentInstanceId } = ReviewsStore;
  const currentInstance = instances[currentInstanceId];
  const currentReview = ReviewsStore.getReview(currentFrame, currentInstanceId);

  useEffect(() => {
    setResult(currentReview?.result || ReviewResult.APPROVE);
  }, [currentInstanceId]);

  if (!currentInstanceId) {
    return null;
  }

  return (
    <>
      <Modal
        visible
        title={currentInstance && getInstanceLabel(currentInstance)}
        onClose={() => ReviewsStore.setInstance()}
      >
        {ReviewsStore.isEditable ? (
          <Form
            layout="vertical"
            initialValues={{
              ...currentReview,
              result: currentReview?.result || ReviewResult.APPROVE,
            }}
            onFinish={(values) => {
              ReviewsStore.setReview(currentFrame, currentInstanceId, values);
              ReviewsStore.setInstance();
              onReviewed();
            }}
          >
            <Form.Item name="result">
              <Radio.Group style={{ width: '100%' }} onChange={(e) => setResult(e.target.value)}>
                <Row>
                  {ReviewsStore.reviewResultTypes.map((type) => (
                    <Col key={type} span={8}>
                      <Radio value={type}>{i18n.translate(`REVIEW_${type.toUpperCase()}`)}</Radio>
                    </Col>
                  ))}
                </Row>
              </Radio.Group>
            </Form.Item>
            {result !== ReviewResult.APPROVE && (
              <>
                <Form.Item name="type" label={i18n.translate('REVIEW_REASON_LABEL')} rules={[{ required: true, message: i18n.translate('REVIEW_NO_REASON') }]}>
                  <Select mode="multiple">
                    {ReviewsStore.issueTypes.map((item) => (
                      <Select.Option value={item} key={item}>{item}</Select.Option>
                    ))}
                  </Select>
                </Form.Item>
                <Form.Item name="comment" label={i18n.translate('REVIEW_COMMENT_LABEL')}>
                  <ImageTextField uploader={saveFile} />
                </Form.Item>
              </>
            )}
            <Button type="primary" htmlType="submit">{i18n.translate('COMMON_SAVE')}</Button>
          </Form>
        ) : (
          <>
            <div style={{ fontSize: 18, fontWeight: 600 }}>
              {currentReview?.result && i18n.translate(`REVIEW_${currentReview.result.toUpperCase()}`)}
            </div>
            <div style={{ marginTop: 16 }}>
              <div>{i18n.translate('REVIEW_REASON_LABEL')}</div>
              <div style={{ color: 'rgba(255, 255, 255, 0.6)' }}>{currentReview?.type?.join(', ')}</div>
            </div>
            <div style={{ marginTop: 16 }}>
              <div>{i18n.translate('REVIEW_COMMENT_LABEL')}</div>
              <div
                style={{ color: 'rgba(255, 255, 255, 0.6)' }}
                className="img-container"
                onClick={(e) => {
                  if ((e.target as HTMLElement).tagName === 'IMG') {
                    setPreviewImg((e.target as HTMLImageElement).src);
                  }
                }}
                // eslint-disable-next-line react/no-danger
                dangerouslySetInnerHTML={{ __html: currentReview?.comment || '' }}
              />
            </div>
          </>
        )}
      </Modal>
      {previewImg && <ImagePreview src={previewImg} onClose={() => setPreviewImg('')} />}
    </>
  );
};

export default observer(Review);
