import React, { useEffect, useState } from 'react';
import { observer } from 'mobx-react';
import cx from 'classnames';
import { debounce } from 'lodash';
import { Tooltip, Popover, Row, Col, Divider, Input, notification } from 'antd';
import { ArrowsAltOutlined, ShrinkOutlined, SaveOutlined, QuestionOutlined, ReloadOutlined } from '@ant-design/icons';
import { Hide, Redo, Undo, Edit, Validate, Warning, Offset, MinusCircleFilled, PlusCircleFilled } from '../../../common/icons';
import AppenLogo from '../../../common/AppenLogo';
import store from '../../store/RootStore';
import { OffsetAction } from '../../types';
import i18n from '../../locales';
import './index.scss';

interface Props {
  readonly?: boolean;
  onSave: () => void;
  handleUndo: () => void;
  handleRedo: () => void;
  handleScreenFull: () => void;
  reload: () => void;
}

const Toolbar = ({
  readonly = false,
  onSave,
  handleUndo,
  handleRedo,
  handleScreenFull,
  reload,
}: Props) => {
  useEffect(() => {
    if (store.setting.screenfull.isEnabled) {
      store.setting.screenfull.on('change', initScreenStatus);
    }
    return () => {
      if (store.setting.screenfull.isEnabled) {
        store.setting.screenfull.off('change', initScreenStatus);
      }
    };
  }, []);
  const [offsetVisible, setOffsetVisible] = useState<boolean>(false);

  const initScreenStatus = () => {
    store.setting.setFullScreen(!store.setting.isFullScreen);
  };

  const onVisibleChange = (visible: boolean) => {
    setOffsetVisible(visible);
    if (!visible) {
      store.waveform.resetOverallOffset();
    }
  };

  const HelpContent = (
    <Row style={{ width: 400 }} gutter={16} align="top">
      <Col span={24}>
        <Divider plain><b>{i18n.translate('HELP_TITLE')}</b></Divider>
      </Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Delete</span>
        {' / '}
        <span className="help-button">Backspace</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_POINTS_DELETE')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Mouse Left</span>
        + Move
      </Col>
      <Col span={14}>{i18n.translate('HELP_POINTS_CREATE')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">S</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_CONTINUOUS')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Control</span>
        {' / '}
        <span className="help-button">Command</span>
        + Click
      </Col>
      <Col span={14}>{i18n.translate('HELP_PLAY')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Mouse Wheel</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_MOUSEWHEEL')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl + R</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_REFRESH')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl + Z</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_UNDO')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl + Y</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_REDO')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">F</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_FULLSCREEN')}</Col>
    </Row>
  );

  const ReviewModeContent = (
    <div className="mode-tips">
      <div className="title">{i18n.translate('REVIEW_MODE_TIPS_TITLE')}</div>
      <p>{i18n.translate('REVIEW_MODE_TIPS_1')}</p>
      <p />
      <p>{i18n.translate('REVIEW_MODE_TIPS_2')}</p>
      <p>{i18n.translate('REVIEW_MODE_TIPS_3')}</p>
      <p>{i18n.translate('REVIEW_MODE_TIPS_4')}</p>
    </div>
  );

  const AnnotationModeContent = (
    <div className="mode-tips">
      <div className="title">{i18n.translate('ANNOTATION_MODE_TIPS_TITLE')}</div>
      <p>{i18n.translate('ANNOTATION_MODE_TIPS_1')}</p>
    </div>
  );

  const setOffset = (_offset: number, action?: OffsetAction) => {
    let offset = _offset;
    if (action === OffsetAction.INDENT) {
      offset -= 0.01;
    } else if (action === OffsetAction.BLANK) {
      offset += 0.01;
    }
    offset = Math.floor(Math.round(offset * 100)) / 100;
    store.waveform.setOverallOffset(offset);
  };

  const handleOffsetInput = (e: any) => {
    const offset = Number(e.target.value);
    if (!Number.isNaN(offset)) {
      store.waveform.setOverallOffset(offset);
    } else {
      notification.error({ message: i18n.translate('CONTROL_OFFSET_ERROR') });
    }
  };

  const OffsetContent = () => {
    const { waveform } = store;
    const maxOffset = Math.max(...waveform.channelOffset);
    const minOffset = Math.min(...waveform.channelOffset);
    return (
      <div className="view-offset">
        <div
          className={cx('operation-icon', {
            'operation-icon--disabled': -minOffset >= (waveform.duration / 2)
          })}
          onClick={debounce(() => setOffset(waveform.overallOffset, OffsetAction.INDENT), 50)}
        >
          <MinusCircleFilled />
        </div>
        <Input
          className="offset-input"
          onPressEnter={handleOffsetInput}
          defaultValue={waveform.overallOffset}
        />
        <div
          className={cx('operation-icon', {
            'operation-icon--disabled': maxOffset >= (waveform.duration / 2),
          })}
          onClick={debounce(() => setOffset(waveform.overallOffset, OffsetAction.BLANK), 50)}
        >
          <PlusCircleFilled />
        </div>
      </div>
    );
  };

  return (
    <div className="toolbar">
      <div>
        <div className="logo">
          <AppenLogo fillOpacity="1" />
        </div>
        {store.review.isEnabled && (
          <div className="mode-box">
            <div className="mode-button">
              <Popover overlayClassName="multi_channel-popover" content={AnnotationModeContent} placement="bottomRight">
                <div
                  className={cx('icon-button', {
                    'icon-button--active': store.review.drawMode,
                    'icon-button--disabled': !store.review.isEditable,
                  })}
                  onClick={() => { store.review.setDrawMode(true); }}
                >
                  <Edit />
                </div>
              </Popover>
              <Popover overlayClassName="multi_channel-popover" content={ReviewModeContent} placement="bottomRight">
                <div
                  className={cx('icon-button', {
                    'icon-button--active': !store.review.drawMode,
                  })}
                  onClick={() => { store.review.setDrawMode(false); }}
                >
                  <Validate />
                </div>
              </Popover>
            </div>
            {!store.review.drawMode && (
              <div className="mode-tip">
                <span className="anticon anticon-picture mode-tip-icon">
                  <Warning />
                </span>
                {i18n.translate('TOOLBAR_MODE_TIP')}
              </div>
            )}
            <div className="divider" />
          </div>
        )}
        <div
          className={cx('icon-button design-icon', {
            'icon-button--disabled': store.undo.undoDisabled || readonly
          })}
          onClick={handleUndo}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_UNDO', { values: { shortcut: 'Ctrl+Z' } })}
            placement="bottom"
          >
            <Undo />
          </Tooltip>
        </div>
        <div
          className={cx('icon-button design-icon', {
            'icon-button--disabled': store.undo.redoDisabled || readonly
          })}
          onClick={handleRedo}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_REDO', { values: { shortcut: 'Ctrl+Y' } })}
            placement="bottom"
          >
            <Redo />
          </Tooltip>
        </div>
        <div
          className="icon-button"
          style={{ fontSize: '16px' }}
          onClick={reload}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_REFRESH')}
            placement="bottom"
          >
            <ReloadOutlined />
          </Tooltip>
        </div>
        <div className="icon-button">
          <Popover overlayClassName="multi_channel-popover" content={HelpContent} placement="bottomRight">
            <QuestionOutlined />
          </Popover>
        </div>
      </div>
      <div>
        <Tooltip
          title={`${i18n.translate('TOOLBAR_TIP_SOLO')} (Ctrl + W)`}
          placement="bottom"
        >
          <div
            className={cx('icon-button design-icon', {
              'icon-button--active': store.setting.isSolo
            })}
            onClick={() => {
              store.setting.setIsSolo(!store.setting.isSolo);
            }}
          >
            <Hide />
          </div>
        </Tooltip>
        <Popover
          overlayClassName="multi_channel-popover offset-popover overall-offset-popover"
          content={<OffsetContent key={store.waveform.overallOffset} />}
          placement="bottom"
          onVisibleChange={onVisibleChange}
          title={`${i18n.translate('TOOLBAR_TIP_OFFSET')}:`}
        >
          <div
            className={cx('icon-button design-icon', {
              'icon-button--active': offsetVisible
            })}
          >
            <Offset />
          </div>
        </Popover>
        <div
          className={cx('icon-button', {
            'icon-button--disabled': store.isPreview
          })}
          onClick={onSave}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_SAVE')}
            placement="bottom"
          >
            <SaveOutlined />
          </Tooltip>
        </div>
        <div className="icon-button" onClick={handleScreenFull}>
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_FULLSCREEN')}
            placement="bottom"
          >
            {store.setting.isFullScreen ? <ShrinkOutlined /> : <ArrowsAltOutlined />}
          </Tooltip>
        </div>
      </div>
    </div>
  );
};

export default observer(Toolbar);
