import React, { useState, useRef, useEffect } from 'react';
import { Input, Switch, Form, ConfigProvider, Tooltip } from 'antd';
import { TextAreaRef } from 'antd/es/input/TextArea';
import { CaretDownOutlined } from '@ant-design/icons';
import cx from 'classnames';
import Formula from '../../../common/latex/Formula';
import HighlightHelper, { HighlightHelperHandle } from '../../../common/latex/HighlightHelper';
import i18n from '../../locales';
import { examples } from '../../data/formula-examples.json';
import './FormulaForm.scss';

const FormulaExampleSelector = ({ category, items, onSelect }: {
  category: string;
  items: { value: string }[];
  onSelect: (item: string) => void;
}) => {
  const selectorRef = useRef<HTMLDivElement>(null);
  const [active, setActive] = useState(false);

  useEffect(() => {
    const listener = (e: MouseEvent) => {
      if (selectorRef.current?.contains(e.target as HTMLElement)) {
        return;
      }
      setActive(false);
    };
    window.addEventListener('mousedown', listener);
    return () => {
      window.removeEventListener('mousedown', listener);
    };
  }, []);

  const renderMenu = () => {
    if (!active) {
      return null;
    }

    const { offsetLeft = 0 } = selectorRef.current || {};
    const deltaX = Math.max(480 - (606 - offsetLeft), 0);
    return (
      <div
        className="formula-example-selector__menu scroller"
        style={{
          left: -deltaX,
        }}
      >
        {items.map(({ value }) => (
          <Tooltip key={value} title={value} zIndex={10010}>
            <div
              className="formula-example-selector__menu-item"
              onClick={() => onSelect(value)}
            >
              <Formula text={value} />
            </div>
          </Tooltip>
        ))}
      </div>
    );
  };

  return (
    <div
      ref={selectorRef}
      className={cx('formula-example-selector', {
        active,
      })}
      onMouseDown={(e) => {
        e.preventDefault();
        setActive(true);
      }}
    >
      <div className="formula-example-selector__btn">
        {category}
        <CaretDownOutlined className="formula-example-selector__icon" />
      </div>
      {renderMenu()}
    </div>
  );
};

const FormulaForm = ({ text, onTextChange }: {
  text: string;
  onTextChange: (text: string) => void;
}) => {
  const formulaTextArea = useRef<TextAreaRef>(null);
  const formulaHelper = useRef<HighlightHelperHandle>(null);
  const [showInput, setShowInput] = useState(false);

  useEffect(() => {
    if (!showInput && text) {
      setShowInput(true);
    }
  }, [text]);

  useEffect(() => {
    if (!showInput) {
      onTextChange('');
    }
  }, [showInput]);

  const handleItemAdd = (item: string) => {
    if (formulaTextArea.current && formulaTextArea.current.resizableTextArea) {
      const domTextArea = formulaTextArea.current.resizableTextArea.textArea;
      const txt = domTextArea.value;
      const before = txt.substring(0, domTextArea.selectionStart);
      const after = txt.substring(domTextArea.selectionEnd);
      const newTxt = `${before}${item}${after}`;
      const newSelection = before.length + item.length;
      domTextArea.value = newTxt;
      domTextArea.focus();
      domTextArea.setSelectionRange(newSelection, newSelection);
      onTextChange(newTxt);
    }
  };

  return (
    <ConfigProvider prefixCls="easyform">
      <Form.Item>
        <span style={{ display: 'flex', alignItems: 'center' }}>
          <Switch
            style={{ marginRight: 8 }}
            checked={showInput}
            onChange={(checked) => setShowInput(checked)}
          />
          {i18n.translate('FORMULA_SHOW_INPUT')}
        </span>
      </Form.Item>
      {showInput && (
        <>
          <Form.Item
            colon={false}
            labelCol={{ span: 24 }}
            label={i18n.translate('FORMULA_INPUT_LABEL')}
          >
            <Input.TextArea
              ref={formulaTextArea}
              style={{
                color: 'rgba(0,0,0,0)',
                caretColor: '#FFFFFF',
                zIndex: 2,
              }}
              rows={5}
              wrap="off"
              spellCheck={false}
              value={text}
              onChange={(e) => onTextChange(e.target.value)}
              onScroll={(e) => {
                const { scrollTop, scrollLeft } = e.target as HTMLTextAreaElement;
                formulaHelper.current?.syncScroll(scrollLeft, scrollTop);
              }}
              onMouseDown={(e) => e.stopPropagation()}
            />
            <HighlightHelper
              ref={formulaHelper}
              text={text}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                overflow: 'auto',
                pointerEvents: 'none',
                padding: '5px 12px',
                whiteSpace: 'nowrap',
                userSelect: 'none',
                zIndex: 1,
              }}
            />
            {examples.length > 0 && (
              <div
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  flexWrap: 'wrap',
                  marginTop: 8,
                }}
              >
                {examples.map((c) => (
                  <FormulaExampleSelector
                    key={c.category}
                    category={c.category}
                    items={c.items}
                    onSelect={handleItemAdd}
                  />
                ))}
              </div>
            )}
          </Form.Item>
          <Form.Item
            colon={false}
            labelCol={{ span: 24 }}
            label={i18n.translate('FORMULA_OUTPUT_LABEL')}
          >
            <Formula
              className="formula-container"
              style={{ border: '1px solid rgb(105,105,105)' }}
              text={text}
            />
          </Form.Item>
        </>
      )}
    </ConfigProvider>
  );
};

export default FormulaForm;
