import { Field, Condition, Effect, Rule, FieldControlType, FieldOption, FieldValue } from '@appen-china/easy-form/es/types';
import JobProxy, { RecognizeSource } from '../../libs/JobProxy';
import { ToolMode } from '../../utils/tool-mode';

export interface Ontology {
  key?: string;
  class_name: string;
  display_name?: string;
  display_color: string;
  description?: string;
  translation_required?: boolean;
};

export interface Payload {
  locale?: string;
  issue_types?: string;
  custom_check?: string;
  script_check?: string | boolean;
  submit_check?: string | boolean;
  skip_check_for_invalid_data?: string | boolean;
  jobProxy: JobProxy;
  ontology: Ontology[];
  audio: string;
  video_url?: string;
  toolMode?: ToolMode;
  original_urls?: string;
  show_original?: string | boolean;
  label_config_segment?: string;
  label_config_channel?: string;
  deviation?: string;
  segment_mode?: string;
  segment_overlap?: string;
  global_config?: string;
  invalid_annotatable?: string | boolean;
  auto_transcription?: string | boolean;
  auto_transcription_language?: string;
  auto_transcription_endpoint?: RecognizeSource;
  word_timestamps?: string;
  tag_group?: string;
  min_length?: string;
  max_length?: string;
  measurement_box?: string;
  valid_duration?: ValidDurationMode;
  validity?: string;
  audio_mode?: string;
  style_config?: string;
  word_items?: string;
  translation_required_mode?: string;
  waveform_offset?: string | boolean;
  pcm_sample_rate?: number | string;
  pcm_sample_bits?: number | string;
  pcm_channel_count?: number | string;
  load_in_chunks?: string | boolean;
  show_spectrogram?: string | boolean;
  streaming_load?: string | boolean;
};

export enum TranslationRequired {
  ALL = 'all',
  SELECTED = 'selected',
  VALID = 'valid',
  NONE = 'none',
}

export interface Category {
  className: string;
  displayName: string;
  displayColor: string;
  translationRequired: boolean;
}

export interface LabelConfig {
  fields: Field[];
  conditions?: Condition[];
  effects?: Effect[];
  rules?: Rule[];
}

export interface FieldConfig {
  name: string;
  type: FieldControlType;
  label?: string;
  options?: FieldOption[];
}

export enum ValidationType {
  ATTR_EMPTY = 'attr_empty',
  CUSTOM = 'custom',
  QUALITY = 'quality',
  OVERLAP = 'overlap',
  GLOBAL_ERROR = 'global_error',
  CHANNEL_ERROR = 'channel_error',
  SCRIPT = 'script',
  DURATION_ILLEGAL = 'duration_illegal',
}

export interface ReviewData {
  result: ReviewResult;
  type?: string[];
  comment?: string;
  translation?: string;
  workerName?: string;
}

export interface Review extends ReviewData {
  instanceId: string; // related instance
  validationType?: ValidationType;
  channelIndex?: number;
  isMissing?: boolean;
  start?: number;
  end?: number;
}

export enum ReviewResult {
  APPROVE = 'approve',
  REJECT = 'reject',
  SUSPEND = 'suspend',
}

export enum ReviewColor {
  APPROVE = 0x52C41A,
  APPROVE_BG = 0xCEEBCB,
  REJECT = 0xDC4624,
  REJECT_BG = 0xEED9D7,
  SUSPEND = 0xFA9114,
  SUSPEND_BG = 0xFEE9D0,
}

export interface Element {
  id: string;
  category: string;
  start: number;
  end?: number;
  text?: string;
}

export interface Statistic {
  elements: Element[];
  categories: string[];
  shapes: Set<unknown>;
  summary: { [key: string]: Summary };
  duration: number; // file duration
  approvedWordCount: number;
  rejectedWordCount: number;
  approvedCharCount: number;
  rejectedCharCount: number;
  wordCount: number;
  charCount: number;
}

export interface Summary {
  ids: string[];
  category: string;
  wordCount: number;
  charCount: number;
}

/**
 * 1st: size & position (including shape delete)
 */
export enum EditMode {
  MATRIX_ALLOWED_ONLY = '1',
  MATRIX_LIMITED_ONLY = '0',
}

export interface IPreAnnotationData {
  editMode?: EditMode;
}

export interface Instance {
  id: string;
  start: number;
  end: number;
  channelIndex: number;
  category: string;
  label: string;
  color: string;
  text?: string;
  attributes?: {[key: string]: any};
  isValid: boolean;
  isTranslationRequired?: boolean;
  preAnnotationData?: IPreAnnotationData;
}

export enum UpdateType {
  INIT = 'init',
  MODIFY = 'modify',
  UNDOREDO = 'undoredo',
  REMOVE_MISSED = 'remove_missed',
}

export interface Range {
  start: number;
  end: number;
}

export interface TimeRange {
  startTime: number;
  endTime: number;
}

export interface ChannelOptions {
  index: number;
  mute: boolean;
  volume: number;
}

export enum MediaModes {
  REGION_PLAY = 'region_play',
  REGION_LOOP = 'region_loop',
  OVERALL = 'overall',
}

export enum TimeAction {
  BACKWARD = 'backward',
  FORWARD = 'forward',
}

export enum OffsetAction {
  INDENT = 'indent',
  BLANK = 'blank',
}

export type FormValue = Record<string, FieldValue | FieldValue[]>;

export interface FormConfig {
  fields: Field[];
  conditions?: Condition[];
  effects?: Effect[];
  rules?: Rule[];
}

export interface Warning {
  warningType: string;
  id: string;
  message: string;
  comment?: string;
  channelIndex?: number;
  isMissing?: boolean;
  start?: number;
  end?: number;
}

export enum Align {
  RIGHT = 'right',
  LEFT = 'left',
  CENTER = 'center',
}

export enum Style {
  STROKE = '#34CBD1',
  GREEN = 'rgb(0, 255, 0)',
  WHITE_ALPHA_4 = 'rgba(255, 255, 255,.4)',
  WHITE = 'rgb(255, 255, 255)',
  FONTSIZE = 12,
};

export interface WordTimestamp {
  separator: string;
  words: {
    [channelIndex: number]: { start: number; end: number; word: string; }[]
  };
}

export enum TagType {
  TAG = 'tag',
  STANDALONE = 'standalone',
}

export interface Tag {
  prefix: string;
  suffix: string;
  name: string;
}

export interface Standalone {
  name: string;
  text: string;
}

export interface TagGroup {
  name: string;
  type: TagType;
  tags: Tag[] | Standalone[];
}

export enum ValidDurationMode {
  VALIDITY = 'validity',
  TRANSLATION = 'translations',
};

export interface Validity {
  key: string;
  valid: string[];
  invalid: string[];
}

export enum AudioMode {
  MULTI = 'multi',
  SINGLE = 'single',
}

export interface Styles {
  fill_color?: string;
}

export enum StyleConfigMode {
  SEGMENT_ATTR = 'segment_attr',
  LINE_ATTR = 'line_attr',
}

export interface StyleGroup extends Styles {
  key?: string;
  attributes: any;
}

export interface StyleConfig {
  mode: StyleConfigMode;
  groups: StyleGroup[];
}

export enum MoveType {
  PLUS = 'plus',
  MINUS = 'minus',
}

export interface PreferencesConfig {
  showDecibelValueGuideLine?: boolean;
  snapInSameChannelEnabled?: boolean;
}
