import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { isEqual } from 'lodash';
import { Select, Radio, Form, Row, Col } from 'antd';
import { ImageTextField } from '@appen/easy-form';
import ImagePreview from '../../../common/modal/ImagePreview';
import store from '../../store/RootStore';
import { Review, ReviewResult, ReviewData } from '../../types';
import i18n from '../../locales';
import './index.scss';

interface ReviewPanelProps {
  instanceId: string;
  review?: Review;
}

const radioOptions = [
  ReviewResult.APPROVE,
  ReviewResult.REJECT,
  ReviewResult.SUSPEND
];

const formItemLayoutVertical = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 6 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 24 },
  },
};

const ReviewPanel = ({ instanceId, review }: ReviewPanelProps) => {
  const [form] = Form.useForm();
  const [previewImg, setPreviewImg] = useState<string>('');
  const [currentValues, setCurrentValues] = useState<ReviewData|undefined>(undefined);

  useEffect(() => {
    if (review) {
      const { result, type, comment } = review;
      const isChanged = !isEqual({ result, type, comment }, { ...currentValues });
      if (isChanged) {
        setCurrentValues({
          result: review.result,
          type: review.type,
          comment: review.comment,
        });
        form.resetFields();
      }
    } else if (!review) {
      setCurrentValues(undefined);
      form.resetFields();
    }
  }, [review]);

  const handleValuesChange = (_val:any, values:any) => {
    let newReview: Review = { instanceId, result: ReviewResult.APPROVE };
    if (values.result === ReviewResult.REJECT) {
      newReview = { ...newReview, ...values };
    } else {
      newReview = { ...newReview, result: values.result };
    }
    setCurrentValues(values);
    store.review.handleChangeReview(newReview);
  };

  return (
    <div className="review-container">
      <div className="review-container-form">
        {store.review.isEnabled ? (
          <Form
            form={form}
            size="small"
            layout="vertical"
            initialValues={{
              result: review?.result,
              type: review?.type,
              comment: review?.comment
            }}
            onValuesChange={handleValuesChange}
          >
            <div className="form-top">
              <Form.Item name="result">
                <Radio.Group style={{ width: '100%' }}>
                  <Row>
                    {radioOptions.map((type, i) => (
                      <Col key={type} span={8}>
                        <Radio.Button value={type} className={type} disabled={review?.isMissing && type !== ReviewResult.REJECT}>
                          <span>{i18n.translate(`QC_${type.toUpperCase()}`)}</span>
                          <span className="hint">{i + 1}</span>
                        </Radio.Button>
                      </Col>
                    ))}
                  </Row>
                </Radio.Group>
              </Form.Item>
              {review?.isMissing && (<div className="remove" onClick={() => { store.review.deleteReviewAndInstance(instanceId); }}>{i18n.translate('QC_MISSED_REMOVE')}</div>)}
            </div>
            <Form.Item
              className="clearStyle"
              noStyle
              shouldUpdate={(prevValues, curValues) => prevValues.result !== curValues.result}
              {...formItemLayoutVertical}
            >
              {({ getFieldValue }) => {
                if (getFieldValue('result') === ReviewResult.REJECT) {
                  return (
                    <>
                      <Form.Item
                        name="type"
                        label={i18n.translate('QC_REASON_LABEL')}
                        rules={[{ required: true, message: i18n.translate('QC_REASON_LABEL') }]}
                      >
                        <Select
                          mode="multiple"
                          showArrow
                          style={{ width: '100%' }}
                          dropdownClassName="qc-reason-selected"
                        >
                          {store.review.issueTypes.map((item) => (
                            <Select.Option value={item} key={item}>{item}</Select.Option>
                          ))}
                        </Select>
                      </Form.Item>
                      <Form.Item name="comment">
                        <ImageTextField uploader={store.saveFile} />
                      </Form.Item>
                    </>
                  );
                }
              }}
            </Form.Item>
          </Form>
        ) : (
          <>
            <div style={{ fontSize: 18, fontWeight: 600 }}>
              {currentValues?.result && i18n.translate(`QC_${currentValues.result.toUpperCase()}`)}
            </div>
            <div style={{ marginTop: 16 }}>
              <div>{i18n.translate('QC_NO_REASON')}</div>
              <div style={{ color: 'rgba(255, 255, 255, 0.6)' }}>{currentValues?.type?.join(', ')}</div>
            </div>
            <div style={{ marginTop: 16 }}>
              <div>{i18n.translate('QC_COMMENT_LABEL')}</div>
              <div
                style={{ color: 'rgba(255, 255, 255, 0.6)' }}
                className="img-container"
                onClick={(e) => {
                  if ((e.target as HTMLElement).tagName === 'IMG') {
                    setPreviewImg((e.target as HTMLImageElement).src);
                  }
                }}
                // eslint-disable-next-line react/no-danger
                dangerouslySetInnerHTML={{ __html: currentValues?.comment || '' }}
              />
            </div>
          </>
        )}
      </div>
      {previewImg && <ImagePreview src={previewImg} maskClosable onClose={() => setPreviewImg('')} />}
    </div>
  );
};

export default observer(ReviewPanel);
