import cx from 'classnames';
import { Tooltip, Popover, Row, Col, Divider } from 'antd';
import { ArrowsAltOutlined, ShrinkOutlined, SaveOutlined, QuestionOutlined, ReloadOutlined } from '@ant-design/icons';
import Dropdown, { MenuItemType } from '../../../common/dropdown/Dropdown';
import { Analyse, Redo, Undo, Unfold, Edit, Validate, Warning } from '../../../common/icons';
import AppenLogo from '../../../common/AppenLogo';
import rootStore from '../../store/RootStore';
import { CurveType } from '../../types';
import eventBus, { EventAction } from '../../utils/event';
import i18n from '../../locales';
import './index.scss';

interface Props {
  readonly?: boolean;
  onSave: () => void;
  handleUndo: () => void;
  handleRedo: () => void;
  handleScreenFull: () => void;
  reload: () => void;
}

const Toolbar = ({
  readonly = false,
  onSave,
  handleUndo,
  handleRedo,
  handleScreenFull,
  reload,
}: Props) => {
  useEffect(() => {
    if (rootStore.setting.screenfull.isEnabled) {
      rootStore.setting.screenfull.on('change', initScreenStatus);
    }
    return () => {
      if (rootStore.setting.screenfull.isEnabled) {
        rootStore.setting.screenfull.off('change', initScreenStatus);
      }
    };
  }, []);

  const initScreenStatus = () => {
    rootStore.setting.setFullScreen(!rootStore.setting.isFullScreen);
  };

  const handleCurveVisibleChange = (curve: CurveType) => {
    rootStore.analyse.setCurveVisible(curve);
    eventBus.emit(EventAction.FREQUENCIES_DRAW);
  };

  const HelpContent = (
    <Row style={{ width: 400 }} gutter={16} align="top">
      <Col span={24}>
        <Divider plain><b>{i18n.translate('HELP_TITLE')}</b></Divider>
      </Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Delete</span>
        {' / '}
        <span className="help-button">Backspace</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_POINTS_DELETE')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Enter</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_POINTS_CREATE')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Mouse Wheel</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_MOUSEWHEEL')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Mouse Left + Shift</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_SHIFT')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl/Cmd + 1/2/3...</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_SHAPE_TIER')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button"> Ctrl + W</span>
      </Col>
      <Col span={14}>{i18n.translate('TOOLBAR_TIP_SIDEBAR')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl + R</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_REFRESH')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl + Z</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_UNDO')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">Ctrl + Y</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_REDO')}</Col>
      <Col span={10} className="help-buttons">
        <span className="help-button">F</span>
      </Col>
      <Col span={14}>{i18n.translate('HELP_FULLSCREEN')}</Col>
    </Row>
  );

  const ReviewModeContent = (
    <div className="mode-tips">
      <div className="title">{i18n.translate('REVIEW_MODE_TIPS_TITLE')}</div>
      <p>{i18n.translate('REVIEW_MODE_TIPS_1')}</p>
      <p />
      <p>{i18n.translate('REVIEW_MODE_TIPS_2')}</p>
      <p>{i18n.translate('REVIEW_MODE_TIPS_3')}</p>
      <p>{i18n.translate('REVIEW_MODE_TIPS_4')}</p>
    </div>
  );

  const AnnotationModeContent = (
    <div className="mode-tips">
      <div className="title">{i18n.translate('ANNOTATION_MODE_TIPS_TITLE')}</div>
      <p>{i18n.translate('ANNOTATION_MODE_TIPS_1')}</p>
    </div>
  );

  return (
    <div className="toolbar">
      <div>
        <div className="logo">
          <AppenLogo fillOpacity="1" />
        </div>
        {rootStore.review.isEnabled && (
          <div className="mode-box">
            <div className="mode-button">
              <Popover overlayClassName="praat-popover" content={AnnotationModeContent} placement="bottomRight">
                <div
                  className={cx('icon-button', {
                    'icon-button--active': rootStore.review.drawMode,
                    'icon-button--disabled': !rootStore.review.isEditable,
                  })}
                  onClick={() => { rootStore.review.setDrawMode(true); }}
                >
                  <Edit />
                </div>
              </Popover>
              <Popover overlayClassName="praat-popover" content={ReviewModeContent} placement="bottomRight">
                <div
                  className={cx('icon-button', {
                    'icon-button--active': !rootStore.review.drawMode,
                  })}
                  onClick={() => { rootStore.review.setDrawMode(false); }}
                >
                  <Validate />
                </div>
              </Popover>
            </div>
            {!rootStore.review.drawMode && (
              <div className="mode-tip">
                <span className="anticon anticon-picture mode-tip-icon">
                  <Warning />
                </span>
                {i18n.translate('TOOLBAR_MODE_TIP')}
              </div>
            )}
            <div className="divider" />
          </div>
        )}
        <div
          className={cx('icon-button design-icon', {
            'icon-button--disabled': rootStore.undo.undoDisabled || readonly
          })}
          onClick={handleUndo}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_UNDO', { values: { shortcut: 'Ctrl+Z' } })}
            placement="bottom"
          >
            <Undo />
          </Tooltip>
        </div>
        <div
          className={cx('icon-button design-icon', {
            'icon-button--disabled': rootStore.undo.redoDisabled || readonly
          })}
          onClick={handleRedo}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_REDO', { values: { shortcut: 'Ctrl+Y' } })}
            placement="bottom"
          >
            <Redo />
          </Tooltip>
        </div>
        <div
          className="icon-button"
          style={{ fontSize: '16px' }}
          onClick={reload}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_REFRESH')}
            placement="bottom"
          >
            <ReloadOutlined />
          </Tooltip>
        </div>
        <div className="icon-button">
          <Popover overlayClassName="praat-popover" content={HelpContent} placement="bottomRight">
            <QuestionOutlined />
          </Popover>
        </div>
      </div>
      <div>
        <Tooltip
          title={`${i18n.translate('TOOLBAR_TIP_SIDEBAR')} (Ctrl + W)`}
          placement="bottom"
        >
          <div
            className={cx('icon-button design-icon', {
              'icon-button--active': rootStore.setting.sideBarVisible
            })}
            onClick={() => {
              rootStore.setting.setSideBarVisible(!rootStore.setting.sideBarVisible);
            }}
          >
            <Unfold />
          </div>
        </Tooltip>
        <Dropdown
          arrow
          showIcon
          style={{ paddingRight: 6, height: '100%' }}
          menuStyle={{ width: 160 }}
          menu={[
            { label: i18n.translate('TOOLBAR_TIP_CURVE_VISIBLE'), type: MenuItemType.TITLE },
            ...Object.entries(CurveType).map(([key, mode]) => ({
              label: i18n.translate(key),
              value: mode,
              active: rootStore.analyse.visible[mode],
            })),
          ]}
          onClick={(_label, _index, { value }) => { handleCurveVisibleChange(value); }}
        >
          <div
            className={cx('icon-button design-icon', {
              'icon-button--active': Object.values(rootStore.analyse.visible).find((v) => !!v)
            })}
            style={{ paddingRight: 0 }}
          >
            <Analyse />
          </div>
        </Dropdown>
        <div
          className={cx('icon-button', {
            'icon-button--disabled': rootStore.isPreview
          })}
          onClick={onSave}
        >
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_SAVE')}
            placement="bottom"
          >
            <SaveOutlined />
          </Tooltip>
        </div>
        <div className="icon-button" onClick={handleScreenFull}>
          <Tooltip
            title={i18n.translate('TOOLBAR_TIP_FULLSCREEN')}
            placement="bottom"
          >
            {rootStore.setting.isFullScreen ? <ShrinkOutlined /> : <ArrowsAltOutlined />}
          </Tooltip>
        </div>
      </div>
    </div>
  );
};

export default observer(Toolbar);
