import { Base64 } from 'js-base64';
import { utils } from '@appen/easy-form';
import store from '../store/RootStore';
import i18n from '../locales';
import { runCustomValidation } from '../../../utils';
import { ValidationType, Instance, Ontology, FormConfig } from '../types';
import { IWarning } from '../../common/tabs-menu/Validator';

/**
 * load instance from result
 * @param result
 */
export function loadInstancesFromResult(result: Instance[], ontology: Ontology[]) {
  return result.map((instance) => {
    const { channelIndex } = instance;
    const newCategory = ontology[channelIndex];
    return {
      ...instance,
      category: newCategory?.class_name || `${channelIndex + 1}`,
      label: newCategory?.display_name || newCategory?.class_name || `${channelIndex + 1}`,
    };
  });
}

export function validate(
  customCheck: string[],
  params: {
    resultLink?: string;
  },
) {
  return new Promise<{
    results: IWarning[];
    hasCustomError: boolean;
  }>(async (resolve) => {
    const { resultLink } = params;
    const results: IWarning[] = [];
    let hasCustomError = false;

    if (customCheck) {
      // custom check
      const customResult = await runCustomValidation(customCheck, resultLink!);
      customResult.results.forEach((result) => {
        results.push({ ...result, ...result.info && result.info, warningType: ValidationType.CUSTOM });
      });
      hasCustomError = customResult.hasCustomError;
    }

    resolve({ results, hasCustomError });
  });
}

/* eslint-disable valid-jsdoc */
/**
 * Returns the `requestAnimationFrame` function for the browser, or a shim with
 * `setTimeout` if the function is not found
 *
 * @return {function} Available `requestAnimationFrame` function for the browser
 */
export const requestAnimationFrame = (
  window.requestAnimationFrame ||
  (window as any).webkitRequestAnimationFrame ||
  (window as any).mozRequestAnimationFrame ||
  (window as any).oRequestAnimationFrame ||
  (window as any).msRequestAnimationFrame ||
  ((callback) => setTimeout(callback, 1000 / 60))
).bind(window);

export const cancelAnimationFrame = (
  window.cancelAnimationFrame ||
  (window as any).webkitCancelAnimationFrame ||
  (window as any).mozCancelAnimationFrame ||
  (window as any).oCancelAnimationFrame ||
  (window as any).msCancelAnimationFrame ||
  ((callback) => clearTimeout(callback))
).bind(window);

/**
 * Create a function which will be called at the next requestAnimationFrame
 * cycle
 *
 * @param {function} func The function to call
 *
 * @return {func} The function wrapped within a requestAnimationFrame
 */
export function frame(func: any) {
  return (...args: any) => requestAnimationFrame(() => func(...args));
}

export const timeInterval = (pxPerSec: number) => {
  let retVal = 1;
  if (pxPerSec >= 1000) {
    retVal = 0.01;
  } else if (pxPerSec >= 500) {
    retVal = 0.02;
  } else if (pxPerSec >= 100) {
    retVal = 0.1;
  } else if (pxPerSec >= 50) {
    retVal = 0.2;
  } else if (pxPerSec >= 10) {
    retVal = 1;
  } else if (pxPerSec >= 5) {
    retVal = 12;
  } else if (pxPerSec >= 1) {
    retVal = 24;
  } else if (pxPerSec >= 0.5) {
    retVal = 120;
  } else if (pxPerSec >= 0.01) {
    retVal = 600;
  } else {
    retVal = Math.ceil(1 / pxPerSec) * 60;
  }
  return retVal;
};

export const formatTimeCallback = (secs: number, pxPerSec: number) => {
  let seconds = Number(secs);
  const minutes = Math.floor(seconds / 60);
  seconds %= 60;

  // fill up seconds with zeroes
  let secondsStr = Math.round(seconds).toString();
  if (pxPerSec >= 25 * 10) {
    secondsStr = seconds.toFixed(2);
  } else if (pxPerSec >= 25 * 1) {
    secondsStr = seconds.toFixed(1);
  }
  secondsStr = seconds >= 10 ? secondsStr : `0${secondsStr}`;
  return `${minutes || '00'}:${secondsStr}`;
};

/**
 * Apply a map of styles to an element
 *
 * @param {HTMLElement} el The element that the styles will be applied to
 * @param {Object} styles The map of propName: attribute, both are used as-is
 *
 * @return {HTMLElement} el
 */
export const setStyle = (el: HTMLElement, styles: {[key: string]: any}) => {
  Object.keys(styles).forEach((prop) => {
    if ((el.style as any)[prop] !== styles[prop]) {
      (el.style as any)[prop] = styles[prop];
    }
  });
  return el;
};

/**
 * parse config
 */
export function parseLabelConfig(configStr?: string) {
  if (configStr) {
    try {
      const labelConfig = JSON.parse(Base64.decode(configStr));
      return labelConfig;
    } catch (e) {
      // parse error
    }
  }
  return undefined;
}

export const verifyCurrentForm = (config: FormConfig, instance: Instance, current: number) => {
  if (store.readonly) return { result: true };
  const { updatedValues, updatedFields } = triggerForm(config, instance.attributes);
  const index = updatedFields.findIndex((v) => v.required === true && v.readonly !== true && v.visible !== false && (v.defaultValue === undefined || v.defaultValue === ''));
  if (index < 0) return { result: true, data: updatedValues };
  const err = `${i18n.translate('DATA_ERROR_REQUIRED')} ${updatedFields[index].label}. Segment ${current + 1}`;
  return { result: false, error: err };
};

export const triggerForm = (config: FormConfig, values:{[key: string]: any} = {}) => {
  const { fields: configFields, conditions = [], effects = [], rules = [] } = config;
  const fields = (configFields || []).map((field) => ({
    ...field,
    ...values[field.name] !== undefined && {
      defaultValue: values[field.name],
    },
  }));

  const parsedValue = utils.parseFormFields(fields);
  // trigger rule effects
  const { updatedValues: triggerValues, updatedFields } = rules.reduce(
    (acc, curr) => utils.ruleTrigger(
      curr, acc.updatedFields, acc.updatedValues, fields, conditions, effects,
    ),
    { updatedFields: parsedValue.fields, updatedValues: parsedValue.initialValues },
  );

  const updatedValues = {
    ...triggerValues
  };
  updatedFields.filter((f) => f.visible === false).forEach((f) => {
    delete updatedValues[f.name];
  });
  return {
    updatedValues,
    updatedFields,
  };
};

export const fittingString = (_ctx: CanvasRenderingContext2D, _str: string, maxWidth: number) => {
