  let strWidth = _ctx.measureText(str).width;
  const ellipsis = 'â€¦';
  const ellipsisWidth = _ctx.measureText(ellipsis).width;
  if ((strWidth + ellipsisWidth) <= maxWidth) {
    return str;
  }
  let len = str.length;
  while ((strWidth + ellipsisWidth) >= maxWidth && len > 0) {
    str = str.slice(0, len);
    strWidth = _ctx.measureText(str).width;
    len -= 1;
  }
  return str + ellipsis;
};

export const drawRoundedRect = (rect: { x: number, y: number, width: number, height: number }, r: number, ctx: CanvasRenderingContext2D) => {
  const ptA = { x: rect.x + r, y: rect.y };
  const ptB = { x: rect.x + rect.width, y: rect.y };
  const ptC = { x: rect.x + rect.width, y: rect.y + rect.height };
  const ptD = { x: rect.x, y: rect.y + rect.height };
  const ptE = { x: rect.x, y: rect.y };
  ctx.beginPath();
  ctx.moveTo(ptA.x, ptA.y);
  ctx.arcTo(ptB.x, ptB.y, ptC.x, ptC.y, r);
  ctx.arcTo(ptC.x, ptC.y, ptD.x, ptD.y, r);
  ctx.arcTo(ptD.x, ptD.y, ptE.x, ptE.y, r);
  ctx.arcTo(ptE.x, ptE.y, ptA.x, ptA.y, r);
  ctx.closePath();
  ctx.fill();
};

export const verifyForm = (config: FormConfig, attributes:{[key: string]: any} = {}) => {
  if (store.readonly) return { result: true };
  const { updatedValues, updatedFields } = triggerForm(config, attributes);
  const index = updatedFields.findIndex((v) => v.required === true && v.readonly !== true && v.visible !== false && (v.defaultValue === undefined || v.defaultValue === ''));
  return { result: index < 0, data: updatedValues };
};
