import React from 'react';
import { observer } from 'mobx-react';
import cx from 'classnames';
import { Edit, Validate, Warning } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { ReviewMode } from '../../types';
import { TOOL_MODE } from '../../../../utils/tool-mode';

const ReviewModeSwitch = observer(() => {
  if (!store.reviewable) {
    return null;
  }
  return (
    <>
      {store.jobProxy?.toolMode === TOOL_MODE.QA_RW && (
        <div className="review-mode">
          <div
            className={cx('review-mode-button', { active: store.config.reviewMode === ReviewMode.LABELING })}
            onClick={() => store.config.setReviewMode(ReviewMode.LABELING)}
          >
            <Edit />
          </div>
          <div
            className={cx('review-mode-button', { active: store.config.reviewMode === ReviewMode.REVIEW })}
            onClick={() => store.config.setReviewMode(ReviewMode.REVIEW)}
          >
            <Validate />
          </div>
        </div>
      )}
      {store.config.reviewMode === ReviewMode.REVIEW && (
        <>
          <span style={{ fontSize: 16, display: 'flex', marginLeft: 8 }}><Warning /></span>
          <span style={{ color: 'rgba(255, 255, 255, 0.6)', paddingLeft: 4, paddingRight: 12 }}>
            {store.jobProxy?.toolMode === TOOL_MODE.AUDIT ? i18n.translate('REVIEW_MODE_AUDIT') : i18n.translate('REVIEW_MODE_QA')}
          </span>
        </>
      )}
      <div className="divider" />
    </>
  );
});

export default ReviewModeSwitch;
