import React from 'react';
import cx from 'classnames';
import formatMessage from '../locales';
import { CategoryItem, CategoryPathShape, Point } from '../types';
import './Sidebar.scss';

interface Props {
  categories: CategoryItem[];
  categoryPathShapes: {[categoryName: string]: CategoryPathShape};
  points: {
    [pointIndex: number]: Point;
  };
  selectedPoint: number;
  setSelectedPoint: (point: number) => void;
}

export default ({
  categories = [],
  categoryPathShapes = {},
  points = {},
  selectedPoint,
  setSelectedPoint,
}: Props) => {
  const renderCategory = (category: CategoryItem) => {
    const { name, range = [], keys = [] } = category;
    if (range.length < 2) {
      return null;
    }
    const allPoints = Array.from({ length: range[1] - range[0] + 1 }).map((_, index) => range[0] + index);
    return (
      <div key={name} className="category">
        <div className="name">
          {name}
          {categoryPathShapes[name] === CategoryPathShape.CIRCLE ? `(${formatMessage('CATEGORY_CIRCLE')})` : ''}
        </div>
        <div className="points-container">
          {allPoints.map((p) => (
            <div
              key={p}
              className={cx('point', {
                'point--key': keys.includes(p),
                'point--empty': !points[p] || !points[p].position,
                'point--selected': p === selectedPoint,
                'point--invisible': points[p] && points[p].visible === false,
              })}
              onClick={() => setSelectedPoint(p)}
            >
              {p}
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="sidebar">
      {categories.map((c) => renderCategory(c))}
    </div>
  );
};
