import React from 'react';
import { makeObservable, observable, action } from 'mobx';
import { observer } from 'mobx-react';
import cx from 'classnames';
import rootStore from '../../store/RootStore';
import eventBus, { EventAction } from '../../utils/event';
import { MinusCircleFilled, PlusCircleFilled } from '../../../common/icons';
import './ScalerControl.scss';

class ScalerControl extends React.Component {
  scrollRef: React.RefObject<HTMLDivElement> = React.createRef();

  isDragging = false;

  startX = 0;

  display = { start: 0, end: 1 };

  constructor(props: any) {
    super(props);
    makeObservable(this, {
      isDragging: observable,
      display: observable,
      mouseDown: action,
      mouseLeave: action,
      setDisplay: action,
    });
  }

  componentDidMount() {
    if (this.scrollRef.current) {
      this.scrollRef.current.addEventListener('mousedown', this.mouseDown);
      window.addEventListener('mousemove', this.mouseMove);
      window.addEventListener('mouseup', this.mouseLeave);
    }
    eventBus.on(EventAction.ZOOM, ({ start, end }) => {
      this.setDisplay(start, end);
    });
  }

  componentDidUpdate() {
    if (this.scrollRef.current) {
      this.scrollRef.current.addEventListener('mousedown', this.mouseDown);
      window.addEventListener('mousemove', this.mouseMove);
      window.addEventListener('mouseup', this.mouseLeave);
    }
  }

  mouseDown = (e: MouseEvent) => {
    this.isDragging = true;
    this.startX = e.clientX;
  };

  mouseLeave = () => {
    if (this.isDragging) {
      const { start, end } = this.display;
      this.isDragging = false;
      rootStore.analyse.setDisplayBuffer({
        start,
        end,
        precent: end - start,
      });
    }
  };

  mouseMove = (e: MouseEvent) => {
    const { start, end } = this.display;
    const precent = end - start;
    if (this.isDragging && precent < 1) {
      const { clientWidth } = this.scrollRef.current!.parentElement!;
      const scrollTrackWidth = clientWidth - this.scrollRef.current!.clientWidth - 8;
      const movePrecent = ((e.clientX - this.startX) / scrollTrackWidth) * (1 - precent);
      let left = movePrecent + start;
      if (left < 0) {
        left = 0;
      } else if (left + precent > 1) {
        left = 1 - precent;
      }
      this.setDisplay(left, left + precent);
      this.startX = e.clientX;
    }
  };

  setDisplay = (start: number, end: number) => {
    this.display = {
      start, end
    };
    eventBus.emit(EventAction.DISPLAY_CHANGE, { ...this.display });
  };

  render() {
    const { placement: { scalerHeight } } = rootStore.analyse;
    const { start, end } = this.display;
    const { zoom } = rootStore.setting;
    return (
      <div className="praat-zoom-container" style={{ height: `${scalerHeight}px` }}>
        <div className="zoom-pannel">
          <span className="btn" onClick={() => { eventBus.emit(EventAction.SCALER_RESIZE); }}>all</span>
          <div className="zoom-box">
            <span
              className="zoom-btn"
              onClick={() => { rootStore.setting.setZoom(Math.ceil(zoom) - 1); }}
            >
              <MinusCircleFilled opacity={0.15} />
            </span>
            <div className="zoom">
              {`${zoom}x`}
            </div>
            <span
              className="zoom-btn"
              onClick={() => { rootStore.setting.setZoom(Math.ceil(zoom) + 1); }}
            >
              <PlusCircleFilled opacity={0.15} />
            </span>
          </div>
          <span className="btn" onClick={() => { eventBus.emit(EventAction.SCALER_SEL); }}>sel</span>
          <span className="btn" onClick={() => { eventBus.emit(EventAction.SCALER_BAK); }}>bak</span>
        </div>
        <div className="scroll-box">
          <div
            className={cx('scroll', {
              dragged: this.isDragging,
            })}
            ref={this.scrollRef}
            style={{ width: `calc(${(end - start) * 100}% - 8px)`, minWidth: '20px', left: `calc(${start * 100}% + 4px)` }}
          />
        </div>
      </div>
    );
  }
}

export default observer(ScalerControl);
