import React from 'react';
import { makeObservable, observable, computed, action, reaction, IReactionDisposer } from 'mobx';
import { observer } from 'mobx-react';
import { Col, Row, Select, Radio, Button, Form } from 'antd';
import Modal from '../../../common/modal/Modal';
import TargetAttributes, { TargetAttributesHandle } from './TargetAttributes';
import { ShapeType } from '../../../common/shapes/types';
import InstanceItem from '../../model/InstanceItem';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { formatFrames, getShapeTypeByTool } from '../../utils';
import { CategoryItem, Tool } from '../../types';
import './MovingModal.scss';

class MovingModal extends React.Component {
  /**
   * reaction disposer
   */
  reactionDisposer: IReactionDisposer;

  /**
   * selected category name
   */
  categoryName = '';

  /**
   * category name error
   */
  categoryNameError = '';

  /**
   * selected instance item name
   */
  itemName = '';

  /**
   * item name error
   */
  itemNameError = '';

  /**
   * selected instance id
   */
  instanceId = '';

  /**
   * instance id error
   */
  instanceIdError = '';

  /**
   * selected instance item id
   */
  itemId = '';

  /**
   * item id error
   */
  itemIdError = '';

  /**
   * apply type
   */
  applyType: 'current' | 'following' | 'all' = 'current';

  /**
   * moving instance item current shape type
   */
  currentShapeType?: ShapeType;

  /**
   * moving instance item current isOCR
   */
  currentIsOCR?: boolean;

  /**
   * moving instance item current isFormula
   */
  currentIsFormula?: boolean;

  /**
   * moving instance item current all frames
   */
  currentFrames: number[] = [];

  /**
   * attributes form count
   */
  submitCount = 0;

  /**
   * attributes form values
   */
  attrValues: { type: string; values: any }[] = [];

  /**
   * attributes container ref
   */
  attrRef = React.createRef<TargetAttributesHandle>();

  /**
   * supported category items can be selected
   */
  get supportedCategoryItems() {
    if (!this.categoryName) {
      return [];
    }
    const category = store.ontology.getCategory(this.categoryName);
    if (!category || !category.children) {
      return [];
    }
    return category.children.filter((c) => this.isCategoryItemValid(c));
  }

  /**
   * supported instances can be selected
   */
  get supportedInstances() {
    if (!this.categoryName || !this.itemName) {
      return [];
    }
    const categoryInstances = store.instance.categoryInstancesMap[this.categoryName];
    if (!categoryInstances) {
      return [];
    }
    const category = store.ontology.getCategory(this.categoryName);
    const categoryItem = store.ontology.getCategoryItem(this.categoryName, this.itemName);
    if (!category || !categoryItem) {
      return [];
    }

    const filteredChildren = category.children.filter((c) => this.isCategoryItemValid(c) && c.name !== this.itemName);
    const { selectedInstances, selectedInstanceItems } = store.instance;
    const selectedInstance = selectedInstances[0];
    const selectedInstanceItem = selectedInstanceItems[0];
    return categoryInstances.filter((instance) => {
      if (categoryItem.count === 1 || categoryItem.maxCount === 1) {
        // because we can overwrite target item, so, just self can't be selected
        if (selectedInstance?.id === instance.id && selectedInstanceItem?.name === categoryItem.name) {
          // selected instance item is just the category item to be move
          return false;
        }
        if (filteredChildren.length > 0) {
          // has other category items can be moved to
          return true;
        }
        return instance.id !== selectedInstance?.id;
      }
      return true;
    });
  }

  /**
   * supported instance items can be selected
   */
  get supportedInstanceItems(): (InstanceItem | 'new')[] {
    if (!this.categoryName || !this.itemName || !this.instanceId) {
      return [];
    }
    if (this.instanceId === 'new') {
      return ['new'];
    }
    const categoryItem = store.ontology.getCategoryItem(this.categoryName, this.itemName);
    if (!categoryItem) {
      return [];
    }
    const selectedInstanceItem = store.instance.selectedInstanceItems[0];
    const instance = store.instance.getInstanceById(this.instanceId);
    const items = instance.getItemsByName(this.itemName);
    const instanceItems: (InstanceItem | 'new')[] = [];
    if (categoryItem.count !== undefined) {
      if (items.length < categoryItem.count) {
        instanceItems.push('new');
      }
    } else if (categoryItem.maxCount !== undefined && items.length < categoryItem.maxCount) {
      instanceItems.push('new');
    } else {
      instanceItems.push('new');
    }
    items.forEach((item) => {
      if (item.id !== selectedInstanceItem?.id) {
        instanceItems.push(item);
      }
    });
    return instanceItems;
  }

  /**
   * all overlapping frames
   */
  get allOverlappingFrames() {
    if (this.itemId && this.itemId !== 'new') {
      const { currentCamera } = store.frame;
      const targetInstance = store.instance.getInstanceById(this.instanceId);
      const targetInstanceItem = targetInstance.items[this.itemId];
      return this.currentFrames.filter((frameIndex) => !!targetInstanceItem.cameras[currentCamera]?.frames[frameIndex]);
    }
    return [];
  }

  /**
   * following overlapping frames (based on current frame)
   */
  get followingOverlappingFrames() {
    const { currentFrame } = store.frame;
    const index = this.allOverlappingFrames.findIndex((frame) => frame >= currentFrame);
    return index >= 0 ? this.allOverlappingFrames.slice(index) : [];
  }

  constructor(props: any) {
    super(props);

    makeObservable(this, {
      categoryName: observable,
      categoryNameError: observable,
      itemName: observable,
      itemNameError: observable,
      instanceId: observable,
      instanceIdError: observable,
      itemId: observable,
      itemIdError: observable,
      applyType: observable,
      // currentShapeType: observable,
      // currentIsOCR: observable,
      currentFrames: observable,
      supportedCategoryItems: computed,
      supportedInstances: computed,
      supportedInstanceItems: computed,
      allOverlappingFrames: computed,
      followingOverlappingFrames: computed,
      setCategory: action.bound,
      setInstanceId: action.bound,
      setItemName: action.bound,
      setItemId: action.bound,
      setApplyType: action.bound,
      handleSave: action.bound,
    });

    this.reactionDisposer = reaction(
      () => store.config.movingModalVisible,
      (visible) => {
        this.categoryName = '';
        this.categoryNameError = '';
        this.itemName = '';
        this.itemNameError = '';
        this.instanceId = '';
        this.instanceIdError = '';
        this.itemId = '';
        this.itemIdError = '';
        this.applyType = 'current';
        if (visible && store.instance.isSingleSelected) {
          const selectedInstanceItem = store.instance.selectedInstanceItems[0];
          const { currentCamera, currentFrame } = store.frame;
          const { frames = {} } = selectedInstanceItem.cameras[currentCamera] || {};
          const { shapeType, isOCR, isFormula } = frames[currentFrame];
          this.currentShapeType = shapeType;
          this.currentIsOCR = isOCR;
          this.currentIsFormula = isFormula;
          this.currentFrames = Object.values(frames).map(({ frameIndex }) => frameIndex);
        }
      },
    );
  }

  componentWillUnmount() {
    this.reactionDisposer();
  }

  isCategoryItemValid(categoryItem: CategoryItem) {
    const { tools } = categoryItem;
    const toolTypes = tools.map((t) => t.type);
    if (this.currentIsOCR) {
      return (
        (toolTypes.includes(Tool.OCR) && this.currentShapeType === ShapeType.RECTANGLE) ||
        (toolTypes.includes(Tool.OCR_POLYGON) && this.currentShapeType === ShapeType.POLYGON)
      );
    }
    if (this.currentIsFormula) {
      return toolTypes.includes(Tool.FORMULA_POLYGON) && this.currentShapeType === ShapeType.POLYGON;
    }
    const shapeTypes = tools.map((tool) => getShapeTypeByTool(tool.type));
    return shapeTypes.includes(this.currentShapeType);
  }

  getAffectedFrames(type: 'current' | 'following' | 'all') {
    const { currentFrame } = store.frame;
    if (type === 'current') {
      return [currentFrame];
    }
    if (type === 'following') {
      return this.currentFrames.slice(this.currentFrames.indexOf(currentFrame));
    }
    if (type === 'all') {
      return [...this.currentFrames];
    }
    return [];
  }

  setCategory(category: string) {
    this.categoryNameError = '';
    this.categoryName = category;
    this.itemName = '';
    this.instanceId = '';
    this.itemId = '';
    this.applyType = 'current';
    const { children = [] } = store.ontology.getCategory(category) || {};
    if (children.every((child) => !this.isCategoryItemValid(child))) {
      this.categoryNameError = i18n.translate('MOVE_ERROR_CATEGORY');
    }
  }

  setItemName(item: string) {
    this.itemNameError = '';
    this.itemName = item;
    this.instanceId = '';
    this.itemId = '';
    this.applyType = 'current';
  }

  setInstanceId(id: string) {
    this.instanceIdError = '';
    this.instanceId = id;
    this.itemId = '';
    this.applyType = 'current';
  }

  setItemId(id: string) {
    this.itemIdError = '';
    this.itemId = id;
    this.applyType = 'current';
  }

  setApplyType(type: 'current' | 'following' | 'all') {
    this.applyType = type;
  }

  handleSave() {
    if (!this.categoryName) {
      this.categoryNameError = i18n.translate('MOVE_ERROR_NO_CATEGORY');
    }
    if (!this.itemName) {
      this.itemNameError = i18n.translate('MOVE_ERROR_NO_CATEGORY_ITEM');
    }
    if (this.categoryNameError || this.itemNameError) {
      return;
    }
    if (!this.instanceId) {
      this.instanceIdError = i18n.translate('MOVE_ERROR_NO_INSTANCE');
    }
    if (!this.itemId) {
      this.itemIdError = i18n.translate('MOVE_ERROR_NO_ITEM');
    }
    if (this.instanceIdError || this.itemIdError) {
      return;
    }

    this.submitCount = 0;
    const buttons = this.attrRef.current?.getButtons();
    if (buttons && buttons.length > 0) {
      buttons.forEach((btn) => {
        this.submitCount += 1;
        (btn as HTMLButtonElement).click();
      });
    } else {
      this.finish();
    }
  }

  finish(newInstanceAttr?: any, newInstanceItemAttr?: any) {
    const affectedFrames = this.getAffectedFrames(this.applyType);
    store.instance.moveInstanceItem(
      this.categoryName,
      this.itemName,
      this.instanceId,
      this.itemId,
      affectedFrames,
      newInstanceAttr,
      newInstanceItemAttr,
    );
    store.config.setMovingModalVisible(false);
  }

  checkFinished = (valuesItem: { type: string; values: any }) => {
    this.submitCount -= 1;
    this.attrValues.push(valuesItem);

    if (this.submitCount === 0) {
      let newInstanceAttr;
      let newInstanceItemAttr;
      this.attrValues.forEach(({ type, values }) => {
        if (type === 'instance') {
          newInstanceAttr = values;
        } else if (type === 'instance-item') {
          newInstanceItemAttr = values;
        }
      });
      this.attrValues = [];
      this.finish(newInstanceAttr, newInstanceItemAttr);
    }
  };

  render() {
    if (!store.config.movingModalVisible || !store.instance.isSingleSelected) {
      return null;
    }
    const selectedInstanceItem = store.instance.selectedInstanceItems[0];
    return (
      <Modal
        className="instance-move"
        visible
        title={i18n.translate('MOVE_TITLE')}
        onClose={() => store.config.setMovingModalVisible(false)}
      >
        <div style={{ marginBottom: 16 }}>
          {i18n.translate('MOVE_TO_LABEL', { values: { object: selectedInstanceItem.label } })}
        </div>
        <div className="instance-move-label">{i18n.translate('MOVE_CLASS_LABEL')}</div>
        <Row gutter={16}>
          <Col span={12}>
            <Form.Item {...this.categoryNameError && { validateStatus: 'error', help: this.categoryNameError }}>
              <Select
                value={this.categoryName}
                onChange={this.setCategory}
              >
                {store.ontology.categories.map((c) => (
                  <Select.Option key={c.className} value={c.className}>
                    {c.displayName}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item {...this.itemNameError && { validateStatus: 'error', help: this.itemNameError }}>
              <Select
                value={this.itemName}
                onChange={this.setItemName}
              >
                {this.supportedCategoryItems.map((c) => (
                  <Select.Option key={c.name} value={c.name}>
                    {c.name}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
        </Row>
        {this.itemName && (
          <>
            <div className="instance-move-label">{i18n.translate('MOVE_OBJECT_LABEL')}</div>
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item {...this.instanceIdError && { validateStatus: 'error', help: this.instanceIdError }}>
                  <Select
                    value={this.instanceId}
                    onChange={this.setInstanceId}
                  >
                    <Select.Option value="new">
                      <div style={{ textAlign: 'center' }}>{i18n.translate('MOVE_CREATE_ID')}</div>
                    </Select.Option>
                    {this.supportedInstances.map((instance) => (
                      <Select.Option key={instance.id} value={instance.id}>
                        {instance.number}
                      </Select.Option>
                    ))}
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item {...this.itemIdError && { validateStatus: 'error', help: this.itemIdError }}>
                  <Select
                    value={this.itemId}
                    onChange={this.setItemId}
                  >
                    {this.supportedInstanceItems.map((item) => (item === 'new' ? (
                      <Select.Option key="new" value="new">
                        <div style={{ textAlign: 'center' }}>{i18n.translate('MOVE_CREATE_ID_ITEM')}</div>
                      </Select.Option>
                    ) : (
                      <Select.Option key={(item as InstanceItem).id} value={(item as InstanceItem).id}>
                        {(item as InstanceItem).number}
                      </Select.Option>
                    )))}
                  </Select>
                </Form.Item>
              </Col>
            </Row>
          </>
        )}
        {this.itemId && (
          <div style={{ marginTop: 16 }}>
            <Radio.Group
              value={this.applyType}
              onChange={(e) => this.setApplyType(e.target.value)}
            >
              <Radio className="radio-option" value="current">
                {i18n.translate('MOVE_APPLY_CURRENT_FRAME')}
                {formatFrames(this.getAffectedFrames('current'))}
                {this.allOverlappingFrames.includes(store.frame.currentFrame) && i18n.translate('MOVE_OVERLAP_WARNING', { values: { overlaps: formatFrames([store.frame.currentFrame]) } })}
              </Radio>
              <Radio className="radio-option" value="following">
                {i18n.translate('MOVE_APPLY_FOLLOWING_FRAMES')}
                {formatFrames(this.getAffectedFrames('following'))}
                {this.followingOverlappingFrames.length > 0 && i18n.translate('MOVE_OVERLAP_WARNING', { values: { overlaps: formatFrames(this.followingOverlappingFrames) } })}
              </Radio>
              <Radio className="radio-option" value="all">
                {i18n.translate('MOVE_APPLY_ALL_FRAMES')}
                {formatFrames(this.getAffectedFrames('all'))}
                {this.allOverlappingFrames.length > 0 && i18n.translate('MOVE_OVERLAP_WARNING', { values: { overlaps: formatFrames(this.allOverlappingFrames) } })}
              </Radio>
            </Radio.Group>
          </div>
        )}
        {this.itemId && (this.categoryName !== selectedInstanceItem.instance.category || this.itemName !== selectedInstanceItem.name) && (
          <TargetAttributes
            ref={this.attrRef}
            category={this.categoryName}
            categoryItem={this.itemName}
            instanceId={this.instanceId}
            checkFinished={this.checkFinished}
          />
        )}
        <div style={{ marginTop: 45, textAlign: 'right' }}>
          <Button type="primary" style={{ width: 80 }} onClick={this.handleSave}>
            {i18n.translate('COMMON_SAVE')}
          </Button>
        </div>
      </Modal>
    );
  }
}

export default observer(MovingModal);
