import React from 'react';
import { observer } from 'mobx-react';
import { Tooltip } from 'antd';
import cx from 'classnames';
import { HideAll } from '../../../common/icons';
import store from '../../store/RootStore';
import i18n from '../../locales';
import { ViewMode } from '../../types';

const HideAllShapes = observer(() => {
  const { addMode, viewMode, hideAll } = store.config;
  const disabled = !store.initialized || viewMode === ViewMode.GRID || addMode;
  return (
    <Tooltip placement="bottom" title={i18n.translate('TOOLBAR_TIP_HIDE_ALL')}>
      <div
        className={cx('icon-button', {
          'icon-button--disabled': disabled,
          'icon-button--active': hideAll,
        })}
        onClick={() => {
          if (!disabled) {
            store.config.setHideAll(!hideAll);
          }
        }}
      >
        <HideAll />
      </div>
    </Tooltip>
  );
});

export default HideAllShapes;
