import { ShapeType, ShapeData } from '../common/shapes/types';

export enum Tool {
  RECTANGLE = 'rectangle',
  FOUR_DOTS_RECTANGLE = 'four-dots-rectangle',
  CENTERLINE_RECTANGLE = 'centerline-rectangle',
  ELLIPSE = 'ellipse',
  POLYGON = 'polygon',
  LINE = 'line',
  DOT = 'dot',
  CUBOID = 'cuboid',
  LSHAPE = 'l-shape',
  OCR = 'ocr',
  OCR_POLYGON = 'ocr-polygon',
  GRID = 'grid',
}

export interface Ontology {
  class_name: string;
  display_name?: string;
  display_color: string;
}

export enum LabelItem {
  CATEGORY = 'category',
  NUMBER = 'number',
  TOOL_NAME = 'tool-name',
  ATTRIBUTE_KEYS = 'attribute-keys',
  ATTRIBUTE_VALUES = 'attribute-values',
}

export enum LabelStyle {
  DEFAULT = 'default',
  TRANSPARENT = 'transparent'
}

export enum LabelFormat {
  DEFAULT = 'default',
  COMPRESSED = 'compressed',
}

export interface Instance {
  id: string;
  category: string;
  number: number;
  frames: {
    [frameIndex: number]: FrameData;
  };
}

export interface FrameData {
  frameIndex: number;
  isKeyFrame: boolean;
  shapeType: ShapeType;
  shape: ShapeData;
  attributes?: any;
  isOCR?: boolean;
  OCRText?: string;
}

export interface CategoryInstancesMap {
  [category: string]: {
    [instanceId: string]: number; // instance id -> instance num
  };
}

export interface Relationship {
  from: string;
  to: string;
  relation: string;
}

export enum ReviewResult {
  APPROVE = 'approve',
  REJECT = 'reject',
  SUSPEND = 'suspend',
}

export interface Review {
  instanceId: string;
  frameIndex: number;
  result: ReviewResult;
  type?: string[];
  comment?: string;
}

export enum ValidationType {
  ATTR_EMPTY = 'attr_empty',
  CUSTOM = 'custom',
}

export interface ValidationResult {
  type: ValidationType;
  id: string;
  message: string;
  frames: number[];
  info?: {
    [key: string]: any;
  };
  blockSubmit?: boolean;
}

export interface HotkeyItem {
  key: string;
  affected: string;
  type: string;
  attributes: {
    [attr: string]: any;
  };
}

export enum ViewMode {
  DEFAULT = 'default',
  GRID = 'grid',
}

export enum AttributesMode {
  HIDE = 'hide',
  HOVER = 'hover',
  ALWAYS = 'always',
}

export interface TagProps {
  name: string;
  prefix?: string;
  suffix?: string;
  shortcut?: string;
  text?: string;
}

export interface TagGroupProps {
  name: string;
  type: string;
  tags: TagProps[];
}

export interface FrameAttribute {
  frameIndex: number;
  valid: boolean;
}
