/* eslint-disable no-undef */
import React from 'react';
import { Base64 } from 'js-base64';
// import Spreadsheet from '@appen/spreadsheet';
import { saveAs } from 'file-saver';
import ExcelJS from 'exceljs';
import { notification, Spin } from 'antd';
import pako from 'pako';
import PaPa from 'papaparse';
import STATUS from './type';
import { isAnnotationReadonly, isPreview } from '../../utils/tool-mode';
import { stringToUint8Array, uint8ArrayToString, uint8ToString } from './utils';
import workbookToJson, { checkStyleValid } from './workbook2json';
import jsonToWorkbook from './json2Workbook';
import localMessage, { localizeSheet } from './locale';
// import '@appen/spreadsheet/dist/index.css';
import './index.scss';

notification.config({ top: 60 });

export default class ExcelAnnotationApp extends React.Component {
  sheet = null;

  constructor(props) {
    super(props);
    this.state = {
      status: STATUS.NONE,
    };
  }

  get excelLinkFile() {
    return `${this.props.jobProxy.resultId}.excel.xlsx`;
  }

  get jsonLinkFile() {
    return `${this.props.jobProxy.resultId}.excel.json`;
  }

  componentDidMount() {
    // 根据浏览器语言设置显示语言
    // localizeSheet(Spreadsheet);
    // this.sheet = new Spreadsheet('#x-spreadsheet', {
    //   view: {
    //     height: () => document.documentElement.clientHeight,
    //     width: () => document.documentElement.clientWidth,
    //   },
    //   toolbar: {
    //     allowSave: !(isAnnotationReadonly(this.props.jobProxy.toolMode) || isPreview(this.props.jobProxy.toolMode)),
    //     allowExport: false,
    //     allowPrint: false,
    //   },
    //   lockEnable: !this.props.initial_value_editable,
    // });
    this.sheet.validate();
    this.sheet
      .on('table-save', this.onSubmit.bind(this, true))
      .on('export', this.onExport.bind(this));
    this.onLoad();
  }

  async onLoad() {
    this.setState({ status: STATUS.LOAD });
    try {
      await this.props.jobProxy.loadResult().then(async (res) => {
        if (res) {
          const payload = JSON.parse(JSON.stringify(res));
          await fetch(payload.json)
            .then((result) => result.json())
            .then(async (compressedData) => { // 若能找到之前保存的资源则载入保存的资源, data格式为json
              const depress = pako.inflate(stringToUint8Array(compressedData.saveData));
              const str = uint8ArrayToString(depress);
              const data = JSON.parse(str);
              await this.sheet.loadData(data);
              notification.success({ message: localMessage('loadSuccess') });
            }).catch((e) => {
              notification.error({ message: localMessage('loadFail'), description: (!(e.message instanceof Object) && e.message) || localMessage('retry') });
            });
        } else { // 若找不到则在payload的url中寻找
          await this.loadPayload();
        }
      });
    } catch (e) {
      notification.error({ message: localMessage('ANNOTATION_DATA_LOAD_ERROR'), duration: null });
      return;
    }
    this.setState({ status: STATUS.NONE });
  }

  onSubmit(normalSave) {
    let result;
    if (!isAnnotationReadonly(this.props.jobProxy.toolMode)) {
      result = this.onSave(!normalSave).catch((e) => {
        if (!normalSave) {
          throw new Error(localMessage('saveFail'), e);
        }
      });
    } else {
      notification.warning({ message: localMessage('saveNotAllow') });
    }
    return result;
  };

  async onSave(isSubmit) {
    const uploadPromise = new Promise((resolve, reject) => {
      this.setState({ status: STATUS.SAVE }, () => {
        setTimeout(async () => {
          let excelLink;
          let jsonLink;
          let dataLink;
          try {
            const saveData = this.sheet.getData();
            const workbook = jsonToWorkbook(saveData);
            await workbook.xlsx.writeBuffer()
              .then(async (data) => {
                try {
                  excelLink = await this.props.saveContent(
                    window.btoa(uint8ToString(data)),
                    'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=utf-8',
                    `${isSubmit ? `R.${new Date().getTime()}` : ''}${this.excelLinkFile}`,
                    this.props.job_id,
                  );
                } catch (err) {
                  reject(err);
                }
              });
            const compress = pako.gzip(JSON.stringify(saveData), { to: 'String' });
            jsonLink = await this.props.saveContent(
              Base64.encode(JSON.stringify({ saveData: uint8ToString(compress) })),
              'text/plain',
              `${isSubmit ? `R.${new Date().getTime()}` : ''}${this.jsonLinkFile}`,
              this.props.job_id,
            );

            dataLink = this.props.jobProxy.saveResult({
              excel: excelLink,
              json: jsonLink,
            }, isSubmit);
            this.setState({ status: STATUS.NONE });
            resolve(dataLink);
          } catch (e) {
            this.setState({ status: STATUS.NONE });
            reject(e);
          }
        }, 0);
      });
    });
    let result;
    await uploadPromise.then((res) => {
      result = res;
      notification.success({ message: localMessage('saveSuccess') });
    }).catch((e) => {
      notification.error({ message: localMessage('saveFail'), description: (!(e.message instanceof Object) && e.message) || localMessage('retry') });
      throw new Error(e);
    });
    return result;
  }

  async onExport() {
    this.setState({ status: STATUS.EXPORT });
    try {
      const workbook = jsonToWorkbook(this.sheet.getData());
      await workbook.xlsx.writeBuffer()
        .then((buff) => {
          const blob = new Blob([buff], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=utf-8' });
          saveAs(blob, 'Excel.xlsx'); // 浏览器下载文件
          notification.success({ message: localMessage('exportSuccess') });
        });
    } catch (e) {
      notification.error({ message: localMessage('exportFail'), description: (!(e.message instanceof Object) && e.message) || localMessage('retry') });
    } finally {
      this.setState({ status: STATUS.NONE });
    }
  }

  async loadPayload() {
    let loadPromise;
    let res = await fetch(this.props.excel);
    res = res.blob();
    await res.then((data) => {
      const reader = new FileReader();
      loadPromise = new Promise((resolve, reject) => {
        switch (data.type) {
          case 'text/csv':
            reader.onload = () => {
              try {
                PaPa.parse(reader.result, {
                  complete: (csv) => {
                    this.resolveCsv(csv.data);
                    resolve(true);
                  }
                });
              } catch (err) {
                reject(err);
              }
            };
            reader.readAsText(data);
            break;
          case 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet':
            reader.onload = async () => {
              try {
                const result = new Uint8Array(reader.result);
                const workbook = new ExcelJS.Workbook();
                await workbook.xlsx.load(result);
                if (!checkStyleValid(workbook)) notification.warning({ message: localMessage('loadStyleWarning') });
                const json = workbookToJson(workbook);
                this.sheet.loadData(json);
                this.sheet.reRender();
                resolve(true);
              } catch (err) {
                reject(err);
              }
            };
            reader.readAsArrayBuffer(data);
            break;
          default:
            throw new Error();
        }
      });
    });
    await loadPromise.then(() => {
      notification.success({ message: localMessage('loadSuccess') });
    }).catch((err) => {
      notification.error({ message: localMessage('loadFail') });
      throw new Error(err);
    });
  }

  resolveCsv(data) {
    for (let i = 0; i < data.length; i += 1) {
      const cols = data[i];
      for (let j = 0; j < cols.length; j += 1) {
        this.sheet.cellText(i, j, cols[j]);
        this.sheet.cell(i, j).locked = true;
      }
    }
    this.sheet.lockSheet(0);
    this.sheet.reRender();
  }

  spinTip() {
    switch (this.state.status) {
      case STATUS.LOAD: return `${localMessage('loading')}...`;
      case STATUS.EXPORT: return `${localMessage('exporting')}...`;
      case STATUS.SAVE: return `${localMessage('saving')}...`;
      default: return '';
    };
  }

  render() {
    return (
      <div className="excel-container">
        <Spin
          spinning={this.state.status !== STATUS.NONE}
          delay={0}
          tip={this.spinTip()}
          size="large"
        >
          <div id="x-spreadsheet" />
        </Spin>
      </div>
    );
  }
}
