import React from 'react';
import WaveSurfer from 'wavesurfer.js';
import TimelinePlugin from 'wavesurfer.js/dist/plugin/wavesurfer.timeline.min.js';
import RegionsPlugin from 'wavesurfer.js/dist/plugin/wavesurfer.regions.min.js';
import hexToRgba from 'hex-to-rgba';
import { IoMdPlay, IoMdRewind, IoMdFastforward, IoMdPause, IoMdInformationCircleOutline } from 'react-icons/io';
import { InputNumber, Select, Divider, Input } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import { store } from '../../utils';
import './audio.css';
const { Option } = Select;

const toolName = 'video-segment3';
const defaultColor = '#5ceee4';
const defaultAlpha = 0.45;

export default class AudioAnnotationApp extends React.Component {
  state = {
    isPlaying: false,
    isEditing: false,
    summary: '',
    category: 'Utterance',
    start: '',
    end: '',
    text: '',
    unitId: '',
    textEditing: false,
    showHotkey: false,
    translate: '',
    intent: '3',
    userError: '3',
    transcriptionError: '6',
    actionCategory: '1',
    noActionType: '1',
    wrongActionType: '1',
    rootCause: '1',
    visualResponse: '1',
    participantWant: '',
    assistantAction: '',
    comments: '',
    turn: 'default',
    newTurn: '',
    turns: {},
  };

  newRegion = undefined;

  wavesurfer = null;
  workingRegion = null;
  ontologyColors = new Map([
    ...this.props.ontology && Array.isArray(this.props.ontology) &&
    this.props.ontology.map(i => ([i.class_name, i.display_color])),
  ]);

  componentDidMount() {
    this.wavesurfer = WaveSurfer.create({
      container: '#wave',
      pixelRatio: 1,
      scrollParent: true,
      normalize: true,
      minimap: true,
      backend: 'MediaElement',
      backgroundColor: '#504D66',
      waveColor: '#27253c',
      progressColor: '#F7963D',
      cursorColor: '#F7963D',
      plugins: [
        RegionsPlugin.create(),
        TimelinePlugin.create({
          container: '#wave-timeline',
          primaryColor: '#EDEDEF',
          secondaryColor: '#DCDBE0',
          primaryFontColor: '#EDEDEF',
          secondaryFontColor: '#DCDBE0',
        })
      ]
    });
    const mediaElt = document.querySelector('video');
    this.wavesurfer.load(mediaElt);
    // this.wavesurfer.load(this.props.audio);

    this.wavesurfer.on('ready', () => {
      this.wavesurfer.enableDragSelection({
        color: hexToRgba(defaultColor, defaultAlpha)
      });

      const { mode, unit_id } = this.props;
      const unitId = unit_id;
      this.setState({ unitId: unitId });
      const { jobId, taskId } = this.resolveUnitId(unitId);

      const isCacheValid = this.isCacheValid(mode, jobId, taskId);
      if (!isCacheValid) {
        store.clear(toolName);
      }
      store.setStorage(toolName, 'baseInfo', { mode, jobId, taskId });

      const { regions: storedRegions, turns } = store.getStorage(toolName, unitId) || {};
      if (storedRegions) {
        this.loadRegions(storedRegions || []);
        this.setState({turns: turns || {}});
      } else {
        const annotation = this.props.review_from;
        if (annotation && annotation.regions && annotation.regions.length > 0) {
          this.loadRegions(annotation.regions);
          this.setState({turns: annotation.turns || {}});
          // this.setGrade(annotation.grade);
          this.saveRegions();
        }
      }

      const initTurns = this.state.turns;
      if (Object.keys(initTurns).length === 0) {
        initTurns['default'] = this.defaultGrade();
        this.setState({turns: initTurns});
      }
    });

    // bind actions
    this.wavesurfer.on('play', () => {
      this.setState({ isPlaying: true });
    });
    this.wavesurfer.on('pause', () => {
      this.setState({ isPlaying: false });
    });

    this.wavesurfer.on('audioprocess', () => {
      if (this.newRegion) {
        this.newRegion.update({end: this.roundTime(this.wavesurfer.getCurrentTime())});
      }
    });
    this.wavesurfer.on('region-in', this.showForm);
    this.wavesurfer.on('region-out', this.hideForm);
    this.wavesurfer.on('region-click', this.onRegionClick);
    this.wavesurfer.on('region-update-end', this.onRegionEdit);
    this.wavesurfer.on('region-updated', this.onRegionUpdate);

    document.addEventListener('keydown', (e) => {
      const { keyCode, ctrlKey, shiftKey } = e;
      switch (keyCode) {
        case 32:
          if (!this.state.textEditing) {
            e.preventDefault();
            this.wavesurfer.playPause();
          }
          break;
        case 37: // left arrow
          this.skipBackward(e);
          break;
        case 39: // right arrow
          this.skipForward(e);
          break;
        case 67: // c
          if (ctrlKey && shiftKey) {
            e.preventDefault();
            this.wavesurfer.regions.clear();
            store.clear(toolName);
          }
          break;
        case 68: // d
          if (ctrlKey && shiftKey) {
            e.preventDefault();
            this.handleRegionDelete(e);
          }
          break;
        case 69: // e
          if (!this.state.textEditing) {
            e.preventDefault();
            this.newRegion = undefined;
          }
          break;
        case 83: // s
          if (ctrlKey && shiftKey) {
            e.preventDefault();
            this.handleRegionSave(e);
          } else if (!this.state.textEditing) {
            e.preventDefault();
            // remove if it's already created
            if (this.newRegion && this.newRegion.id) {
              this.newRegion.remove();
            }
            this.newRegion = {start: this.roundTime(this.wavesurfer.getCurrentTime()), color: hexToRgba(defaultColor, defaultAlpha)};
            this.newRegion = this.wavesurfer.addRegion(this.newRegion);
          }
          break;
        default:
      }
    });
  }

  resolveUnitId(unitId) {
    const units = unitId.split("_");
    let jobId, taskId, recordId;
    if (units && units.length >= 3) {
      jobId = units[0];
      taskId = units[1];
      recordId = units[2];
    }
    return {
      jobId,
      taskId,
      recordId,
    }
  }

  getGrade() {
    return {
      intent: this.state.intent,
      userError: this.state.userError,
      transcriptionError: this.state.transcriptionError,
      actionCategory: this.state.actionCategory,
      noActionType: this.state.noActionType,
      wrongActionType: this.state.wrongActionType,
      rootCause: this.state.rootCause,
      visualResponse: this.state.visualResponse,
      participantWant: this.state.participantWant,
      assistantAction: this.state.assistantAction,
      comments: this.state.comments,
    }
  }

  setGrade(grade) {
    if (!grade) return;
    this.setState({
      intent: grade.intent || '3',
      userError: grade.userError || '3',
      transcriptionError: grade.transcriptionError || '6',
      actionCategory: grade.actionCategory || '1',
      noActionType: grade.noActionType || '1',
      wrongActionType: grade.wrongActionType || '1',
      rootCause: grade.rootCause  || '1',
      visualResponse: grade.visualResponse || '1',
      participantWant: grade.participantWant || '',
      assistantAction: grade.assistantAction || '',
      comments: grade.comments || '',
    });
  }

  defaultGrade() {
    return {
      intent: '3',
      userError: '3',
      transcriptionError: '6',
      actionCategory: '1',
      noActionType: '1',
      wrongActionType: '1',
      rootCause: '1',
      visualResponse: '1',
      participantWant: '',
      assistantAction: '',
      comments: '',
    }
  }

  exportResult() {
    const grade = this.getGrade();
    return Object.keys(this.wavesurfer.regions.list).map((id) => {
      const { start, end, data } = this.wavesurfer.regions.list[id];
      return { start: this.roundTime(start), end: this.roundTime(end), data, grade };
    });
  }

  isCacheValid(mode, jobId, taskId) {
    try {
      const baseInfo = store.getStorage(toolName, 'baseInfo');
      if (baseInfo && jobId === baseInfo.jobId && taskId === baseInfo.taskId && mode === baseInfo.mode) {
        return true;
      }
    } catch (e) {
      // failed to parse baseInfo
    }
    return false;
  }

  loadRegions(regions) {
    regions.forEach((region) => {
      const regionColor = this.ontologyColors.get(region.data.category) || defaultColor;
      region.color = hexToRgba(regionColor, defaultAlpha);
      this.wavesurfer.addRegion(region);
    });
  }

  saveRegions() {
    // const turns = this.state.turns;
    // turns[this.state.turn] = this.getGrade();
    // this.setState({turns: turns});
    const regions = Object.keys(this.wavesurfer.regions.list).map((id) => {
      const { start, end, data } = this.wavesurfer.regions.list[id];
      return { start: this.roundTime(start), end: this.roundTime(end), data };
    });
    store.setStorage(toolName, this.state.unitId, { regions, turns: this.state.turns});
  }

  roundTime(time) {
    return Math.round(time * 1000) / 1000;
  }

  playPause = (e) => {
    e.preventDefault();
    this.setState({ isPlaying: !this.state.isPlaying });
    this.wavesurfer.playPause();
  }

  skipBackward = (e) => {
    e.preventDefault();
    this.wavesurfer.skipBackward();
  }

  skipForward = (e) => {
    e.preventDefault();
    this.wavesurfer.skipForward();
  }

  showSummary = (region) => {
    this.setState({ summary: `[${this.roundTime(region.start)} - ${this.roundTime(region.end)}] [${region.data.category||''}]: ${region.data.text||''}` });
  }

  hideSummary = () => {
    this.setState({ summary: '' });
  }

  onRegionClick = (region, e) => {
    e.stopPropagation();
    // Play on click, loop on shift click
    e.shiftKey ? region.playLoop() : region.play();

    this.onRegionEdit(region);
  }

  hideForm = () => {
    // this.workingRegion = null;
    // this.setState({
    //   isEditing: false
    // });
  }

  showForm = (region) => {
    this.workingRegion = region;
    const turnName = (region.data || {}).turn || 'default';
    this.setState({
      isEditing: true,
      category: (region.data || {}).category || 'speech',
      start: this.roundTime(region.start), //Math.round(region.start * 10) / 10,
      end: this.roundTime(region.end), // Math.round(region.end * 10) / 10,
      text: (region.data || {}).text || '',
      turn: turnName,
      // translate: (region.data || {}).translate || '',
      // intent: (region.data || {}).intent || '3',
      // userError: (region.data || {}).userError || '3',
      // transcriptionError: (region.data || {}).transcriptionError || '6',
      // actionCategory: (region.data || {}).actionCategory || '1',
      // noActionType: (region.data || {}).noActionType || '1',
      // wrongActionType: (region.data || {}).wrongActionType || '1',
      // rootCause: (region.data || {}).wrongActionType || '1',
      // visualResponse: (region.data || {}).visualResponse || '1',
      // participantWant: (region.data || {}).participantWant || '',
      // assistantAction: (region.data || {}).assistantAction || '',
      // comments: (region.data || {}).comments || '',
    });
    this.setGrade(this.state.turns[turnName] || {});
  }

  onRegionEdit = (region) => {
    this.workingRegion = region;
    const turnName = (region.data || {}).turn || 'default';
    this.setState({
      isEditing: true,
      category: (region.data || {}).category || 'speech',
      start: this.roundTime(region.start), //Math.round(region.start * 10) / 10,
      end: this.roundTime(region.end), // Math.round(region.end * 10) / 10,
      text: (region.data || {}).text || '',
      turn: turnName,
      // translate: (region.data || {}).translate || '',
      // intent: (region.data || {}).intent || '3',
      // userError: (region.data || {}).userError || '3',
      // transcriptionError: (region.data || {}).transcriptionError || '6',
      // actionCategory: (region.data || {}).actionCategory || '1',
      // noActionType: (region.data || {}).noActionType || '1',
      // wrongActionType: (region.data || {}).wrongActionType || '1',
      // rootCause: (region.data || {}).wrongActionType || '1',
      // visualResponse: (region.data || {}).visualResponse || '1',
      // participantWant: (region.data || {}).participantWant || '',
      // assistantAction: (region.data || {}).assistantAction || '',
      // comments: (region.data || {}).comments || '',
    });

    this.setGrade(this.state.turns[turnName] || {});

    // save to region
    this.workingRegion.update({
      data: {
        text: this.state.text,
        category: this.state.category,
        turn: this.state.turn,
        // translate: this.state.translate,
        // intent: this.state.intent,
        // userError: this.state.userError,
        // transcriptionError: this.state.transcriptionError,
        // actionCategory: this.state.actionCategory,
        // noActionType: this.state.noActionType,
        // wrongActionType: this.state.wrongActionType,
        // rootCause: this.state.rootCause,
        // visualResponse: this.state.visualResponse,
        // participantWant: this.state.participantWant,
        // assistantAction: this.state.assistantAction,
        // comments: this.state.comments,
      }
    });
    this.saveRegions();
  }

  onRegionUpdate = (region) => {
    this.saveRegions();

    if (this.workingRegion) {
      this.setState({
        start: this.roundTime(region.start), //Math.round(region.start * 10) / 10,
        end: this.roundTime(region.end), // Math.round(region.end * 10) / 10,
      });
    }
  }

  handleCategorySelect = (category, e) => {
    e.preventDefault();
    this.setState({ category: category.class_name });

    // save to region
    this.workingRegion.update({
      color: hexToRgba(category.display_color, defaultAlpha),
      data: {
        text: this.state.text,
        category: category.class_name,
        turn: this.state.turn,
        // translate: this.state.translate,
        // intent: this.state.intent,
        // userError: this.state.userError,
        // transcriptionError: this.state.transcriptionError,
        // actionCategory: this.state.actionCategory,
        // noActionType: this.state.noActionType,
        // wrongActionType: this.state.wrongActionType,
        // rootCause: this.state.rootCause,
        // visualResponse: this.state.visualResponse,
        // participantWant: this.state.participantWant,
        // assistantAction: this.state.assistantAction,
        // comments: this.state.comments,
      }
    });
    this.saveRegions();
  }

  handleRegionDelete = (e) => {
    e.preventDefault();
    if (!this.workingRegion) {
      return;
    }
    this.workingRegion.remove();
    this.workingRegion = null;
    this.saveRegions();
    this.setState({ isEditing: false });
  }

  handleRegionSave = (e) => {
    e.preventDefault();
    if (!this.workingRegion) {
      return;
    }
    const { category, start, end, text, turn } = this.state;
    this.workingRegion.update({
      start,
      end,
      data: { category, text, turn},
    });
    this.workingRegion = null;

    const turns = this.state.turns;
    turns[this.state.turn] = this.getGrade();
    this.setState({turns: turns});

    this.saveRegions();
    this.setState({ isEditing: false });
  }

  setSpeed = (n) => {
    const rate = this.speedFormula(n);
    this.wavesurfer.setPlaybackRate(rate);
  }

  setZoom = (n) => {
    let num = parseInt(n, 10);
    if (isNaN(num)) num = 1;
    else if (num > 100) num = 100;
    else if (num < 1) num = 1;
    const duration = this.wavesurfer.getDuration();
    const width = parseInt((getComputedStyle(this.wave).width).replace(/px$/, ''), 10);
    const pxPerSec = num * width / duration;
    this.wavesurfer.zoom(pxPerSec);
  }

  speedFormula(n) {
    const num = parseInt(n);
    if (isNaN(num)) return 1.0;
    let rate = (1 + 0.1 * num).toFixed(1);
    if (rate > 2) return 2.0;
    if (rate < 0.1) return 0.1;
    return rate;
  }

  handleNewTurnName = event => {
    this.setState({
      newTurn: event.target.value,
    });
  };

  createTurn = () => {
    const { turns, newTurn } = this.state;
    if (!newTurn) return;
    const turnList = Object.keys(turns);
    if (turnList.includes(newTurn)) {
      this.setState({newTurn: ''});
      return;
    }

    turns[newTurn] = this.defaultGrade();
    console.log('create turn', turns);

    this.setState({
      turns: turns,
      newTurn: '',
    });
  };

  handleTurnChange = (value) => {
    console.log('turn select', value, this.state.turns);
    if (this.state.turns[value]) {
      this.setGrade(this.state.turns[value] || {});
    }
    this.setState({turn: value});
  };

  render() {
    const { isPlaying, isEditing, category, start, end, text, intent, userError, transcriptionError, actionCategory, noActionType, wrongActionType, rootCause, visualResponse, participantWant, assistantAction, comments, turn, newTurn, turns } = this.state;

    return (
        <div className={`${toolName}-container`}>
          <div id="wave" ref={r => this.wave = r}/>
          <div id="wave-timeline" />

          <div className={`${toolName}-btn-group`}>
            <button className={`${toolName}-audio-btn`} onClick={this.skipBackward}>
              <IoMdRewind />
            </button>
            <button className={`${toolName}-audio-btn`} onClick={this.playPause}>
              {isPlaying ? <IoMdPause /> : <IoMdPlay />}
            </button>
            <button className={`${toolName}-audio-btn`} onClick={this.skipForward}>
              <IoMdFastforward />
            </button>
            <span className={`${toolName}-hotkey-btn`}
                  onMouseOver={e => this.setState({showHotkey: true})}
                  onMouseLeave={e => this.setState({showHotkey: false})}
            >
            Hot Key Explanation <IoMdInformationCircleOutline />
          </span>
            <span className={`${toolName}-speed-control`}>
            <span>Playback Rate: </span>
            <InputNumber
                id={`${toolName}-speed-btn`}
                defaultValue={0}
                min={-9}
                max={10}
                step={1}
                type={"number"}
                size={"small"}
                formatter={value => this.speedFormula(value)}
                onChange={this.setSpeed}
            />
            <span>&nbsp;&nbsp;&nbsp;Zoom: </span>
             <InputNumber
                 id={`${toolName}-speed-btn`}
                 defaultValue={1}
                 min={1}
                 max={100}
                 step={1}
                 type={"number"}
                 size={"small"}
                 onChange={this.setZoom}
             />
          </span>
          </div>

          <div className={`${toolName}-video-container`}>
            <div className={`${toolName}-form-title`}>VIDEO</div>
            <video className={`${toolName}-video`} src={this.props.audio} type="video/mpeg">
            </video>
          </div>


          { this.state.showHotkey && (<div className={`${toolName}-hotkey`}>
                <table>
                  <tbody>
                  <tr>
                    <th>Hot Key</th>
                    <th>Features</th>
                  </tr>
                  <tr>
                    <td>TAB</td>
                    <td>Play or pause</td>
                  </tr>
                  <tr>
                    <td>Left and right arrow keys</td>
                    <td>Play backward or forward</td>
                  </tr>
                  <tr>
                    <td>S</td>
                    <td>Start a segment while playing</td>
                  </tr>
                  <tr>
                    <td>E</td>
                    <td>End a segment while playing</td>
                  </tr>
                  {/*<tr>
                    <td>ENTER</td>
                    <td>Save segment</td>
                  </tr>*/}
                  <tr>
                    <td>Crl+Shift+D</td>
                    <td>Delete selected segment</td>
                  </tr>
                  <tr>
                    <td>Crl+Shift+C</td>
                    <td>Delete all segment</td>
                  </tr>
                  </tbody>
                </table>
              </div>
          )}

          <div className={`${toolName}-region-form-container`}>
            <div className={`${toolName}-form-title`}>SEGMENT</div>
            {
              isEditing &&
              <div className={`${toolName}-form-group`}>
                <label>Turn ID</label>
                <Select
                    style={{ width: 350 }}
                    placeholder="select or create a turn"
                    onChange={this.handleTurnChange}
                    value={this.state.turn}
                    dropdownRender={menu => (
                        <div>
                          {menu}
                          <Divider style={{ margin: '4px 0' }} />
                          <div style={{ display: 'flex', flexWrap: 'nowrap', padding: 8 }}>
                            <Input style={{ flex: 'auto' }} value={newTurn}
                                   onChange={this.handleNewTurnName}
                                   onFocus={e => this.setState({textEditing: true})}
                                   onBlur={e => this.setState({textEditing: false})}
                            />
                            <a
                                style={{ flex: 'none', padding: '8px', display: 'block', cursor: 'pointer' }}
                                onClick={this.createTurn}
                            >
                              <PlusOutlined /> New Turn
                            </a>
                          </div>
                        </div>
                    )}
                >
                  {Object.keys(turns).map(item => (
                      <Option key={item}>{item}</Option>
                  ))}
                </Select>
              </div>
            }
            {isEditing &&
              <form className={`${toolName}-form`} onSubmit={this.handleRegionSave}>
                {
                  this.props.ontology && this.props.ontology.length > 0 &&
                  <div className={`${toolName}-form-group`}>
                    <label>Category</label>
                    {this.props.ontology.map(i => (
                        <div
                            key={i.key}
                            className={`${toolName}-ontology-btn ${i.class_name === category ? 'label-selected' : 'label-unselected'}`}
                            style={{ background: i.display_color }}
                            onClick={(e) => this.handleCategorySelect(i, e)}
                        >
                          {i.class_name}
                        </div>
                    ))}
                  </div>
                }

                <div className={`${toolName}-form-group`}>
                  <label>Start/End</label>
                  <input type={"text"} value={start} onChange={e => this.setState({ start: e.target.value })} />
                  <span> - </span>
                  <input type={"text"} value={end} onChange={e => this.setState({ end: e.target.value })} />
                  <span> Duration: {this.roundTime(end - start)}</span>
                </div>

                <div className={`${toolName}-form-group`}>
                  <label>Transcription</label>
                  <textarea rows="3" value={text}
                            onChange={e => this.setState({ text: e.target.value})}
                            onFocus={e => this.setState({textEditing: true})}
                            onBlur={e => {
                              this.setState({textEditing: false});
                              // this.handleRegionSave(e);
                            }}/>
                </div>
                <button className={`${toolName}-action-btn`} type="submit">
                  Save Segment
                </button>
                <button className={`${toolName}-action-btn danger`} onClick={this.handleRegionDelete}>
                  Delete Segment
                </button>
              </form>
            }
            {
              !isEditing &&
              <div className={`${toolName}-form-text`}>Please create or select a segment</div>
            }

          </div>

          {isEditing &&
          <div className={`${toolName}-grade-container`}>
            <div className={`${toolName}-form-title`}>TURN GRADE</div>
            <form className={`${toolName}-form`}>
              <div className={`${toolName}-form-group`}>
                <label>1. GRADE: Was the assistant successful?</label>
                <ul>
                  <li>
                    <label>
                      <input type="radio" value="3" checked={intent === "3"}
                             onChange={e => this.setState({intent: e.target.value})}/>
                      <span>Success</span>
                    </label>
                    <label>
                      <input type="radio" value="2" checked={intent === "2"}
                             onChange={e => this.setState({intent: e.target.value})}/>
                      <span>Close</span>
                    </label>
                    <label>
                      <input type="radio" value="1" checked={intent === "1"}
                             onChange={e => this.setState({intent: e.target.value})}/>
                      <span>Failure</span>
                    </label>
                  </li>
                </ul>
              </div>

              <div className={`${toolName}-form-group`}>
                <label>2. USER ERROR: Is there any error in the participant's utterance?</label>
                <ul>
                  <li>
                    <label>
                      <input type="radio" value="3" checked={userError === "3"}
                             onChange={e => this.setState({userError: e.target.value})}/>
                      <span>No errors</span>
                    </label>
                    <label>
                      <input type="radio" value="2" checked={userError === "2"}
                             onChange={e => this.setState({userError: e.target.value})}/>
                      <span>Speech error</span>
                    </label>
                    <label>
                      <input type="radio" value="1" checked={userError === "1"}
                             onChange={e => this.setState({userError: e.target.value})}/>
                      <span>Impossible action requested</span>
                    </label>
                  </li>
                </ul>
              </div>


              <div className={`${toolName}-form-group`}>
                <label>3. TRANSCRIPTION ERROR: Does the assistant make errors in transcribing the participant's
                  utterance? If so, what type of error? If there are multiple types of transcription errors, please
                  choose the error type
                </label>
                <ul>
                  <li>
                    <label>
                      <input type="radio" value="6" checked={transcriptionError === "6"}
                             onChange={e => this.setState({transcriptionError: e.target.value})}/>
                      <span>No errors</span>
                    </label>
                    <label>
                      <input type="radio" value="1" checked={transcriptionError === "1"}
                             onChange={e => this.setState({transcriptionError: e.target.value})}/>
                      <span>Named entity error</span>
                    </label>
                    <label>
                      <input type="radio" value="2" checked={transcriptionError === "2"}
                             onChange={e => this.setState({transcriptionError: e.target.value})}/>
                      <span>Substitution</span>
                    </label>
                    <label>
                      <input type="radio" value="3" checked={transcriptionError === "3"}
                             onChange={e => this.setState({transcriptionError: e.target.value})}/>
                      <span>Truncation</span>
                    </label>
                    <label>
                      <input type="radio" value="4" checked={transcriptionError === "4"}
                             onChange={e => this.setState({transcriptionError: e.target.value})}/>
                      <span>Deletion</span>
                    </label>
                    <label>
                      <input type="radio" value="5" checked={transcriptionError === "5"}
                             onChange={e => this.setState({transcriptionError: e.target.value})}/>
                      <span>Other</span>
                    </label>
                  </li>
                </ul>
              </div>

              {
                intent === '1' &&
                <div>
                  <div className={`${toolName}-form-group`}>
                    <label>4. RESPONSE: How does the assistant respond to the participant's utterance? What sort of
                      action was taken?</label>
                    <ul>
                      <li>
                        <label>
                          <input type="radio" value="1" checked={actionCategory === "1"}
                                 onChange={e => this.setState({actionCategory: e.target.value})}/>
                          <span>No action</span>
                        </label>
                        <label>
                          <input type="radio" value="2" checked={actionCategory === "2"}
                                 onChange={e => this.setState({actionCategory: e.target.value})}/>
                          <span>Wrong action</span>
                        </label>
                      </li>
                    </ul>
                  </div>
                  {
                    actionCategory === '1' &&
                    <div>
                      <div className={`${toolName}-form-group`}>
                        <label>What type of non-action?</label>
                        <ul>
                          <li>
                            <label>
                              <input type="radio" value="1" checked={noActionType === "1"}
                                     onChange={e => this.setState({noActionType: e.target.value})}/>
                              <span>Generic response</span>
                            </label>
                            <label>
                              <input type="radio" value="2" checked={noActionType === "2"}
                                     onChange={e => this.setState({noActionType: e.target.value})}/>
                              <span>Missing functionality</span>
                            </label>
                            <label>
                              <input type="radio" value="3" checked={noActionType === "3"}
                                     onChange={e => this.setState({noActionType: e.target.value})}/>
                              <span>App or hardware accessory issue</span>
                            </label>
                            <label>
                              <input type="radio" value="4" checked={noActionType === "4"}
                                     onChange={e => this.setState({noActionType: e.target.value})}/>
                              <span>No data</span>
                            </label>
                            <label>
                              <input type="radio" value="5" checked={noActionType === "5"}
                                     onChange={e => this.setState({noActionType: e.target.value})}/>
                              <span>Test device issue</span>
                            </label>
                            <label>
                              <input type="radio" value="6" checked={noActionType === "6"}
                                     onChange={e => this.setState({noActionType: e.target.value})}/>
                              <span>Other</span>
                            </label>
                          </li>
                        </ul>
                      </div>
                    </div>
                  }
                  {
                    actionCategory === '2' &&
                    <div>
                      <div className={`${toolName}-form-group`}>
                        <label>What type of wrong action?</label>
                        <ul>
                          <li>
                            <label>
                              <input type="radio" value="1" checked={wrongActionType === "1"}
                                     onChange={e => this.setState({wrongActionType: e.target.value})}/>
                              <span>Wrong use case</span>
                            </label>
                            <label>
                              <input type="radio" value="2" checked={wrongActionType === "2"}
                                     onChange={e => this.setState({wrongActionType: e.target.value})}/>
                              <span>Wrong content</span>
                            </label>
                            <label>
                              <input type="radio" value="3" checked={wrongActionType === "3"}
                                     onChange={e => this.setState({wrongActionType: e.target.value})}/>
                              <span>Web search</span>
                            </label>
                            <label>
                              <input type="radio" value="4" checked={wrongActionType === "4"}
                                     onChange={e => this.setState({wrongActionType: e.target.value})}/>
                              <span>Bad data</span>
                            </label>
                            <label>
                              <input type="radio" value="5" checked={wrongActionType === "5"}
                                     onChange={e => this.setState({wrongActionType: e.target.value})}/>
                              <span>Other</span>
                            </label>
                          </li>
                        </ul>
                      </div>
                    </div>
                  }

                  <div className={`${toolName}-form-group`}>
                    <label>5. ROOT CAUSE: What is the root cause of the failure?</label>
                    <ul>
                      <li>
                        <label>
                          <input type="radio" value="1" checked={rootCause === "1"}
                                 onChange={e => this.setState({rootCause: e.target.value})}/>
                          <span>Input (USER ERROR)</span>
                        </label>
                        <label>
                          <input type="radio" value="2" checked={rootCause === "2"}
                                 onChange={e => this.setState({rootCause: e.target.value})}/>
                          <span>Transcription (TRANSCRIPTION ERROR)</span>
                        </label>
                        <label>
                          <input type="radio" value="3" checked={rootCause === "3"}
                                 onChange={e => this.setState({rootCause: e.target.value})}/>
                          <span>Response</span>
                        </label>
                      </li>
                    </ul>
                  </div>
                </div>
              }

              <div className={`${toolName}-form-group`}>
                <label>What does the device show on the screen when it responds to the user?</label>
                <ul>
                  <li>
                    <label>
                      <input type="radio" value="1" checked={visualResponse === "1"}
                             onChange={e => this.setState({visualResponse: e.target.value})}/>
                      <span>Go to app</span>
                    </label>
                    <label>
                      <input type="radio" value="2" checked={visualResponse === "2"}
                             onChange={e => this.setState({visualResponse: e.target.value})}/>
                      <span>Customised response</span>
                    </label>
                    <label>
                      <input type="radio" value="3" checked={visualResponse === "3"}
                             onChange={e => this.setState({visualResponse: e.target.value})}/>
                      <span>Text display</span>
                    </label>
                    <label>
                      <input type="radio" value="4" checked={visualResponse === "4"}
                             onChange={e => this.setState({visualResponse: e.target.value})}/>
                      <span>Web search</span>
                    </label>
                    <label>
                      <input type="radio" value="5" checked={visualResponse === "5"}
                             onChange={e => this.setState({visualResponse: e.target.value})}/>
                      <span>Other</span>
                    </label>
                  </li>
                </ul>
              </div>

              <div className={`${toolName}-form-group`}>
                <label>Please summarize each side of the interaction. The summary you give needs to be informative
                  enough that somebody reading it can understand exactly what happened in the video without watching it,
                  so be sure to include all necessary information.</label>
                <label>What did the user want?</label>
                <textarea rows="2" value={participantWant}
                          placeholder={"What did the user want?"}
                          onChange={e => this.setState({participantWant: e.target.value})}
                          onFocus={e => this.setState({textEditing: true})}
                          onBlur={e => {
                            this.setState({textEditing: false});
                          }}/>
                <label>What did the assistant do?</label>
                <textarea rows="2" value={assistantAction}
                          placeholder={"What did the assistant do?"}
                          onChange={e => this.setState({assistantAction: e.target.value})}
                          onFocus={e => this.setState({textEditing: true})}
                          onBlur={e => {
                            this.setState({textEditing: false});
                          }}/>
              </div>

              <div className={`${toolName}-form-group`}>
                <label>Please include a comment if any unusual events happened during an interaction. For example, if
                  the timer goes off and interrupts while talking, or if a notification blocks the device
                  response.</label>
                <textarea rows="2" value={comments}
                          placeholder={"Comments:"}
                          onChange={e => this.setState({comments: e.target.value})}
                          onFocus={e => this.setState({textEditing: true})}
                          onBlur={e => {
                            this.setState({textEditing: false});
                          }}/>
              </div>
            </form>
          </div>
          }
        </div>
    );
  }
}
